import React, { useState, useEffect, useRef } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  TextField,
  Button,
  LinearProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Chip,
  IconButton,
  Paper,
} from '@mui/material';
import {
  Close as CloseIcon,
  CheckCircle as CheckCircleIcon,
  NavigateNext as NavigateNextIcon,
} from '@mui/icons-material';

interface VocabularyWord {
  unit: string;
  english: string;
  meaning: string;
}

interface EvaluationWord extends VocabularyWord {
  isCorrect?: boolean;
  userInput?: string;
}

interface VocabularyEvaluationProps {
  vocabularyData: VocabularyWord[];
  evaluationLevel: number;
  previousEvaluation?: any;
  onComplete: (results: any) => void;
  onClose: () => void;
}

const VocabularyEvaluation: React.FC<VocabularyEvaluationProps> = ({
  vocabularyData,
  evaluationLevel,
  previousEvaluation,
  onComplete,
  onClose,
}) => {
  const [evaluationWords, setEvaluationWords] = useState<EvaluationWord[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [userInput, setUserInput] = useState('');
  const [showResult, setShowResult] = useState(false);
  const [isCorrect, setIsCorrect] = useState(false);
  const [autoCompletedText, setAutoCompletedText] = useState('');
  const inputRef = useRef<HTMLInputElement>(null);

  // ÌèâÍ∞Ä ÎåÄÏÉÅ Îã®Ïñ¥ Ï∂îÏ∂ú
  useEffect(() => {
    const words = getEvaluationWords();
    setEvaluationWords(words);
  }, []);

  const getEvaluationWords = (): EvaluationWord[] => {
    // 1Ï∞® ÌèâÍ∞Ä: Ï†ÑÏ≤¥ Îã®Ïñ¥
    if (evaluationLevel === 1) {
      if (!vocabularyData) return [];
      return vocabularyData.map((word: VocabularyWord) => ({
        ...word,
        isCorrect: undefined,
        userInput: undefined,
      }));
    }

    // 2Ï∞® Ïù¥ÏÉÅ: Ïù¥Ï†Ñ ÌèâÍ∞ÄÏóêÏÑú ÌãÄÎ¶∞ Îã®Ïñ¥Îßå
    if (previousEvaluation?.incorrectWords) {
      return previousEvaluation.incorrectWords.map((word: VocabularyWord) => ({
        ...word,
        isCorrect: undefined,
        userInput: undefined,
      }));
    }

    return [];
  };

  // ÏÇ¨Ïö©Ïûê ÏûÖÎ†•Ïóê Îî∞Î•∏ ÏûêÎèôÏôÑÏÑ±
  useEffect(() => {
    if (!userInput || currentIndex >= evaluationWords.length) return;

    const currentWord = evaluationWords[currentIndex];
    const meanings = currentWord.meaning.split(',').map(m => m.trim());
    
    // Í∞Å ÎúªÏóê ÎåÄÌï¥ Ï≤´ Í∏ÄÏûê Îß§Ïπ≠ ÌôïÏù∏
    for (const meaning of meanings) {
      // Í¥ÑÌò∏ ÎÇ¥Ïö© Ï†úÍ±∞
      const cleanedMeaning = meaning.replace(/\([^)]*\)\s*/g, '').trim();
      
      // Ï≤´ Í∏ÄÏûêÍ∞Ä ÏùºÏπòÌïòÎäîÏßÄ ÌôïÏù∏
      if (cleanedMeaning.startsWith(userInput)) {
        setAutoCompletedText(meaning);
        setIsCorrect(true);
        return;
      }
    }

    // ÏùºÏπòÌïòÎäî Ï≤´ Í∏ÄÏûêÍ∞Ä ÏóÜÏúºÎ©¥
    setAutoCompletedText('');
    setIsCorrect(false);
  }, [userInput, currentIndex, evaluationWords]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    if (value.length <= 1) {
      setUserInput(value);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' && autoCompletedText && isCorrect) {
      handleNext();
    }
  };

  const handleNext = (skipMode = false) => {
    if (currentIndex >= evaluationWords.length) return;

    if (!skipMode && !userInput) {
      alert('ÎãµÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    // ÌòÑÏû¨ Îã®Ïñ¥ Í≤∞Í≥º Ï†ÄÏû•
    const updatedWords = [...evaluationWords];
    updatedWords[currentIndex] = {
      ...updatedWords[currentIndex],
      isCorrect: skipMode ? false : isCorrect,
      userInput: userInput || '(Í±¥ÎÑàÎõ∞Í∏∞)',
    };
    setEvaluationWords(updatedWords);

    // Îã§Ïùå Îã®Ïñ¥Î°ú
    if (currentIndex < evaluationWords.length - 1) {
      setCurrentIndex(currentIndex + 1);
      setUserInput('');
      setAutoCompletedText('');
      setIsCorrect(false);
      inputRef.current?.focus();
    } else {
      // ÌèâÍ∞Ä ÏôÑÎ£å
      handleEvaluationComplete();
    }
  };

  const handleSkip = () => {
    handleNext(true);
  };

  const handleEvaluationComplete = () => {
    const correctWords = evaluationWords.filter(w => w.isCorrect === true);
    const incorrectWords = evaluationWords.filter(w => w.isCorrect !== true);
    
    const results = {
      evaluationLevel: evaluationLevel,
      totalWords: evaluationWords.length,
      correctWords: correctWords.length,
      incorrectWords: incorrectWords,
      correctRate: Math.round((correctWords.length / evaluationWords.length) * 100),
      timestamp: new Date().toISOString(),
    };

    setShowResult(true);
    setTimeout(() => {
      onComplete(results);
    }, 2000);
  };

  if (evaluationWords.length === 0) {
    return (
      <Dialog open={true} onClose={onClose} maxWidth="sm" fullWidth>
        <DialogTitle>ÌèâÍ∞Ä Ï§ÄÎπÑ</DialogTitle>
        <DialogContent>
          <Box sx={{ py: 2, textAlign: 'center' }}>
            <Typography variant="body1" sx={{ mb: 2 }}>
              ÌèâÍ∞ÄÌï† Îã®Ïñ¥Í∞Ä ÏóÜÏäµÎãàÎã§.
            </Typography>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={onClose}>Îã´Í∏∞</Button>
        </DialogActions>
      </Dialog>
    );
  }

  const currentWord = evaluationWords[currentIndex];
  const progress = ((currentIndex + 1) / evaluationWords.length) * 100;

  return (
    <Dialog
      open={true}
      onClose={onClose}
      maxWidth="md"
      fullWidth
      PaperProps={{
        sx: { height: '80vh', display: 'flex', flexDirection: 'column' }
      }}
    >
      <DialogTitle sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <Typography variant="h6">{evaluationLevel}Ï∞® ÌèâÍ∞Ä</Typography>
