      </DialogTitle>

      <DialogContent sx={{ flex: 1, display: 'flex', flexDirection: 'column', gap: 3 }}>
        {/* ÏßÑÌñâÎ•† ÌëúÏãú */}
        <Box>
          <LinearProgress
            variant="determinate"
            value={progress}
            sx={{ height: 8, borderRadius: 4 }}
          />
        </Box>

        {/* Îã®Ïñ¥ Ïπ¥Îìú */}
        <Card sx={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <CardContent sx={{ textAlign: 'center', width: '100%', maxWidth: 600 }}>
            {/* ÏòÅÏñ¥ Îã®Ïñ¥ */}
            <Typography variant="h3" sx={{ mb: 4, fontWeight: 'bold' }}>
              {currentWord?.english}
            </Typography>

            {/* ÏûÖÎ†• ÌïÑÎìú */}
            <Box sx={{ mb: 4 }}>
              <TextField
                ref={inputRef}
                value={userInput}
                onChange={handleInputChange}
                onKeyPress={handleKeyPress}
                placeholder="ÎúªÏùò Ï≤´ Í∏ÄÏûêÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                variant="outlined"
                size="medium"
                autoFocus
                sx={{
                  width: '100%',
                  maxWidth: 400,
                  '& .MuiOutlinedInput-root': {
                    fontSize: '1.5rem',
                  }
                }}
              />
            </Box>

            {/* ÏûêÎèôÏôÑÏÑ± Í≤∞Í≥º */}
            {autoCompletedText && (
              <Paper
                elevation={3}
                onClick={isCorrect ? () => handleNext() : undefined}
                sx={{
                  p: 3,
                  bgcolor: isCorrect ? 'success.50' : 'error.50',
                  border: 2,
                  borderColor: isCorrect ? 'success.main' : 'error.main',
                  borderRadius: 2,
                  cursor: isCorrect ? 'pointer' : 'default',
                  maxWidth: 400,
                  mx: 'auto',
                  transition: 'all 0.3s ease',
                  '&:hover': isCorrect ? {
                    bgcolor: 'success.100',
                    transform: 'scale(1.02)',
                  } : {},
                }}
              >
                <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 1 }}>
                  {isCorrect && <CheckCircleIcon color="success" />}
                  <Typography variant="h6" color={isCorrect ? 'success.main' : 'error.main'}>
                    {autoCompletedText}
                  </Typography>
                </Box>
                {isCorrect && (
                  <Typography variant="caption" color="text.secondary" sx={{ mt: 1, display: 'block' }}>
                    ÌÅ¥Î¶≠ÌïòÍ±∞ÎÇò EnterÎ•º ÎàåÎü¨ Îã§ÏùåÏúºÎ°ú
                  </Typography>
                )}
              </Paper>
            )}
          </CardContent>
        </Card>

        {/* Ïï°ÏÖò Î≤ÑÌäº */}
        <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center' }}>
          <Button
            variant="outlined"
            size="large"
            onClick={handleSkip}
            disabled={showResult}
          >
            Í±¥ÎÑàÎõ∞Í∏∞ (Ïò§Îãµ)
          </Button>
          <Button
            variant="contained"
            size="large"
            onClick={() => handleNext()}
            disabled={!userInput && !showResult}
            endIcon={<NavigateNextIcon />}
          >
            {currentIndex === evaluationWords.length - 1 ? 'ÌèâÍ∞Ä ÏôÑÎ£å' : 'Îã§Ïùå Îã®Ïñ¥'}
          </Button>
        </Box>
      </DialogContent>

      {/* Í≤∞Í≥º Îã§Ïù¥ÏñºÎ°úÍ∑∏ */}
      <Dialog open={showResult} maxWidth="sm" fullWidth>
        <DialogTitle>{evaluationLevel}Ï∞® ÌèâÍ∞Ä Í≤∞Í≥º</DialogTitle>
        <DialogContent>
          <Box sx={{ textAlign: 'center', py: 3 }}>
            <Typography variant="h4" sx={{ mb: 2 }}>
              {evaluationWords.filter(w => w.isCorrect === true).length} / {evaluationWords.length}
            </Typography>
            <Typography variant="h6" color="primary">
              Ï†ïÎãµÎ•†: {Math.round((evaluationWords.filter(w => w.isCorrect === true).length / evaluationWords.length) * 100)}%
            </Typography>
            
            {evaluationWords.filter(w => w.isCorrect !== true).length === 0 ? (
              <Box sx={{ mt: 2 }}>
                <Typography variant="h6" color="success.main" sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 1 }}>
