import React, { useState, useEffect, useRef } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  TextField,
  Button,
  LinearProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Chip,
  IconButton,
  Paper,
} from '@mui/material';
import {
  Close as CloseIcon,
  CheckCircle as CheckCircleIcon,
  Cancel as CancelIcon,
  NavigateNext as NavigateNextIcon,
} from '@mui/icons-material';

interface VocabularyWord {
  unit: string;
  english: string;
  meaning: string;
}

interface EvaluationWord extends VocabularyWord {
  isCorrect?: boolean;
  userInput?: string;
}

interface VocabularyEvaluationProps {
  assignment: any;
  learningHistory: any[];
  evaluationLevel?: number;
  firstEvaluationResults?: any;
  onComplete: (results: any) => void;
  onClose: () => void;
}

const VocabularyEvaluation: React.FC<VocabularyEvaluationProps> = ({
  assignment,
  learningHistory,
  evaluationLevel = 1,
  firstEvaluationResults,
  onComplete,
  onClose,
}) => {
  const [evaluationWords, setEvaluationWords] = useState<EvaluationWord[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [userInput, setUserInput] = useState('');
  const [showResult, setShowResult] = useState(false);
  const [isCorrect, setIsCorrect] = useState(false);
  const [autoCompletedText, setAutoCompletedText] = useState('');
  const inputRef = useRef<HTMLInputElement>(null);

  // í‰ê°€ ëŒ€ìƒ ë‹¨ì–´ ì¶”ì¶œ
  useEffect(() => {
    console.log('VocabularyEvaluation - evaluationLevel:', evaluationLevel);
    console.log('VocabularyEvaluation - learningHistory:', learningHistory);
    console.log('VocabularyEvaluation - firstEvaluationResults:', firstEvaluationResults);
    
    if (evaluationLevel > 1 && firstEvaluationResults?.incorrectWords) {
      // 2ì°¨ ì´ìƒ í‰ê°€: ì´ì „ í‰ê°€ì—ì„œ í‹€ë¦° ë‹¨ì–´ë§Œ
      console.log(`Setting up ${evaluationLevel}ì°¨ evaluation with incorrect words from ${evaluationLevel - 1}ì°¨ evaluation`);
      setEvaluationWords(firstEvaluationResults.incorrectWords);
      return;
    }
    
    // 1ì°¨ í‰ê°€: ìµœì´ˆ í•™ìŠµì˜ 1ì°¨ ë¼ìš´ë“œì—ì„œ ì–´ë ¤ì› ë˜ ë‹¨ì–´ë“¤
    if (!learningHistory || learningHistory.length === 0) {
      console.error('No learning history found');
      return;
    }

    // ìµœì´ˆ í•™ìŠµ (1íšŒì°¨) ì°¾ê¸°
    const firstLearning = learningHistory.find(history => 
      history.isFirstLearning === true || history.learningSession === 1
    );
    
    console.log('First learning found:', firstLearning);
    
    if (!firstLearning) {
      console.log('No first learning session found, using oldest');
      // í•˜ìœ„ í˜¸í™˜ì„±ì„ ìœ„í•´ ê°€ì¥ ì˜¤ë˜ëœ í•™ìŠµ ê¸°ë¡ ì‚¬ìš©
      const oldestLearning = learningHistory[0];
      console.log('Oldest learning:', oldestLearning);
      
      const firstRound = oldestLearning.rounds?.[0];
      console.log('First round:', firstRound);
      
      if (!firstRound) {
        console.error('No first round data found');
        return;
      }
      
      // wordStatesê°€ ì—†ìœ¼ë©´ words ì‚¬ìš© (í•˜ìœ„ í˜¸í™˜ì„±)
      if (!firstRound.wordStates && firstRound.words) {
        console.log('Using legacy words format');
        const difficultWords = firstRound.words
          .filter((word: any) => 
            word.status === 'repeat' || 
            word.status === 'unknown' || 
            word.status === 'forgot'
          )
          .map((word: any) => ({
            unit: word.unit,
            english: word.english,
            meaning: word.meaning,
          }));
        
        console.log(`Found ${difficultWords.length} difficult words (legacy)`);
        setEvaluationWords(difficultWords);
        return;
      }
      
      extractDifficultWords(firstRound);
      return;
    }

    // ìµœì´ˆ í•™ìŠµì˜ ì²« ë²ˆì§¸ ë¼ìš´ë“œ ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
    const firstRound = firstLearning.rounds?.[0];
    console.log('First round from first learning:', firstRound);
    
    if (!firstRound) {
      console.error('No first round data found in first learning session');
      return;
    }
    
    // wordStatesê°€ ì—†ìœ¼ë©´ words ì‚¬ìš© (í•˜ìœ„ í˜¸í™˜ì„±)
    if (!firstRound.wordStates && firstRound.words) {
      console.log('Using legacy words format in first learning');
      const difficultWords = firstRound.words
        .filter((word: any) => 
          word.status === 'repeat' || 
          word.status === 'unknown' || 
          word.status === 'forgot'
        )
        .map((word: any) => ({
          unit: word.unit,
          english: word.english,
          meaning: word.meaning || word.korean,
        }));
      
      console.log(`Found ${difficultWords.length} difficult words (legacy)`);
      setEvaluationWords(difficultWords);
      return;
    }

    extractDifficultWords(firstRound);
  }, [learningHistory]);

  // ì–´ë ¤ì› ë˜ ë‹¨ì–´ ì¶”ì¶œ í•¨ìˆ˜
  const extractDifficultWords = (firstRound: any) => {
    console.log('extractDifficultWords - firstRound:', firstRound);
    console.log('firstRound keys:', Object.keys(firstRound));
    
    // wordStatesê°€ ìˆëŠ” ê²½ìš° (ìƒˆ í˜•ì‹)
    if (firstRound.wordStates) {
      console.log('Found wordStates:', firstRound.wordStates);
      const difficultWords = firstRound.wordStates
        .filter((wordState: any) => {
          console.log(`Word: ${wordState.word.english}, Status: ${wordState.status}`);
          return wordState.status === 'repeat' || 
                 wordState.status === 'unknown' || 
                 wordState.status === 'forgot';
        })
        .map((wordState: any) => ({
          unit: wordState.word.unit,
          english: wordState.word.english,
          meaning: wordState.word.meaning || wordState.word.korean,
        }));

      console.log(`Found ${difficultWords.length} difficult words from wordStates`);
      setEvaluationWords(difficultWords);
    } 
    // wordsê°€ ìˆëŠ” ê²½ìš° (ë ˆê±°ì‹œ í˜•ì‹)
    else if (firstRound.words) {
      console.log('Found words (legacy):', firstRound.words);
      const difficultWords = firstRound.words
        .filter((word: any) => {
          console.log(`Word: ${word.english}, Status: ${word.status}`);
          return word.status === 'repeat' || 
                 word.status === 'unknown' || 
                 word.status === 'forgot';
        })
        .map((word: any) => ({
          unit: word.unit,
          english: word.english,
          meaning: word.meaning || word.korean,
        }));

      console.log(`Found ${difficultWords.length} difficult words from words`);
      setEvaluationWords(difficultWords);
    }
    // ë‘˜ ë‹¤ ì—†ëŠ” ê²½ìš°
    else {
      console.error('No word data found in first round');
      console.log('Available data in firstRound:', firstRound);
      setEvaluationWords([]);
    }
  };

  // ì²« ê¸€ì ì…ë ¥ ì‹œ ìë™ì™„ì„± ì²˜ë¦¬
  useEffect(() => {
    if (!userInput || currentIndex >= evaluationWords.length) return;

    const currentWord = evaluationWords[currentIndex];
    const meanings = currentWord.meaning.split(',').map(m => m.trim());
    
    // ê° ëœ»ì— ëŒ€í•´ ì²« ê¸€ì ë§¤ì¹­ í™•ì¸
    for (const meaning of meanings) {
      // ë¨¼ì € ê´„í˜¸ ì œê±° ì²˜ë¦¬
      let cleanedMeaning = meaning;
      
      // ê´„í˜¸ì™€ ê·¸ ë‚´ìš©ì„ ì œê±° (ì˜ˆ: "(ì˜ˆì˜ ë°”ë¥´ê²Œ) í–‰ë™í•˜ë‹¤" â†’ "í–‰ë™í•˜ë‹¤")
      cleanedMeaning = cleanedMeaning.replace(/\([^)]*\)\s*/g, '').trim();
      
      // íŠ¹ìˆ˜ íŒ¨í„´ ì²˜ë¦¬ (Aì—ê²Œ Bë¥¼ ì œê³µí•˜ë‹¤, ~ë¼ê³  ìƒê°í•œë‹¤ ë“±)
      const patterns = [
        /Aì—ê²Œ Bë¥¼ (.+)$/,
        /~ë¼ê³  (.+)$/,
        /~ì„ (.+)$/,
        /~ë¥¼ (.+)$/,
        /~ì— (.+)$/,
        /~ì—ê²Œ (.+)$/,
      ];

      let targetWord = cleanedMeaning;
      let prefix = '';
      let suffix = '';

      // íŒ¨í„´ ë§¤ì¹­
      for (const pattern of patterns) {
        const match = cleanedMeaning.match(pattern);
        if (match) {
          targetWord = match[1];
          prefix = cleanedMeaning.substring(0, match.index! + cleanedMeaning.indexOf(match[1]));
          suffix = '';
          break;
        }
      }

      // ì¼ë°˜ì ì¸ ê²½ìš° (íŒ¨í„´ì´ ì—†ëŠ” ê²½ìš°)
      if (prefix === '' && targetWord === cleanedMeaning) {
        // ì²« ê¸€ìê°€ ì¼ì¹˜í•˜ëŠ”ì§€ í™•ì¸
        if (targetWord.startsWith(userInput)) {
          setAutoCompletedText(meaning); // ì›ë³¸ meaning í‘œì‹œ
          setIsCorrect(true);
          return;
        }
      } else {
        // íŒ¨í„´ì´ ìˆëŠ” ê²½ìš°
        if (targetWord.startsWith(userInput)) {
          setAutoCompletedText(meaning);
          setIsCorrect(true);
          return;
        }
      }
    }

    // ì¼ì¹˜í•˜ëŠ” ì²« ê¸€ìê°€ ì—†ìœ¼ë©´
    setAutoCompletedText('');
    setIsCorrect(false);
  }, [userInput, currentIndex, evaluationWords]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    if (value.length <= 1) { // ì²« ê¸€ìë§Œ ì…ë ¥ ê°€ëŠ¥
      setUserInput(value);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' && autoCompletedText && isCorrect) {
      handleNext();
    }
  };

  const handleNext = (skipMode = false) => {
    if (currentIndex >= evaluationWords.length) return;

    // ê±´ë„ˆë›°ê¸° ëª¨ë“œê°€ ì•„ë‹ˆê³  ì‚¬ìš©ìê°€ ì…ë ¥í•˜ì§€ ì•Šì•˜ìœ¼ë©´ ë¦¬í„´
    if (!skipMode && !userInput) {
      alert('ë‹µì„ ì…ë ¥í•´ì£¼ì„¸ìš”.');
      return;
    }

    // í˜„ì¬ ë‹¨ì–´ ê²°ê³¼ ì €ì¥
    const updatedWords = [...evaluationWords];
    updatedWords[currentIndex] = {
      ...updatedWords[currentIndex],
      isCorrect: skipMode ? false : isCorrect,
      userInput: userInput || '(ê±´ë„ˆë›°ê¸°)',
    };
    setEvaluationWords(updatedWords);

    // ë‹¤ìŒ ë‹¨ì–´ë¡œ
    if (currentIndex < evaluationWords.length - 1) {
      setCurrentIndex(currentIndex + 1);
      setUserInput('');
      setAutoCompletedText('');
      setIsCorrect(false);
      setShowResult(false);
      inputRef.current?.focus();
    } else {
      // í‰ê°€ ì™„ë£Œ
      handleEvaluationComplete();
    }
  };

  const handleSkip = () => {
    // ê±´ë„ˆë›°ê¸°ëŠ” ì˜¤ë‹µ ì²˜ë¦¬
    handleNext(true);
  };

  const handleEvaluationComplete = () => {
    const correctCount = evaluationWords.filter(w => w.isCorrect === true).length;
    const incorrectWords = evaluationWords.filter(w => w.isCorrect !== true);
    
    console.log('í‰ê°€ ì™„ë£Œ - ì „ì²´ ë‹¨ì–´:', evaluationWords);
    console.log('í‰ê°€ ì™„ë£Œ - ì •ë‹µ ìˆ˜:', correctCount);
    console.log('í‰ê°€ ì™„ë£Œ - ì˜¤ë‹µ ë‹¨ì–´:', incorrectWords);
    console.log('í‰ê°€ ì™„ë£Œ - ê° ë‹¨ì–´ì˜ isCorrect ê°’:', evaluationWords.map(w => ({
      english: w.english,
      isCorrect: w.isCorrect,
      userInput: w.userInput
    })));
    
    const results = {
      totalWords: evaluationWords.length,
      correctWords: correctCount,
      incorrectWords: incorrectWords,
      evaluationLevel: evaluationLevel,
      timestamp: new Date().toISOString(),
    };

    console.log('í‰ê°€ ê²°ê³¼ ìµœì¢…:', JSON.stringify(results, null, 2));

    setShowResult(true);
    setTimeout(() => {
      onComplete(results);
    }, 2000);
  };

  if (evaluationWords.length === 0) {
    return (
      <Dialog open={true} onClose={onClose} maxWidth="sm" fullWidth>
        <DialogTitle>í‰ê°€ ì¤€ë¹„</DialogTitle>
        <DialogContent>
          <Box sx={{ py: 2, textAlign: 'center' }}>
            <Typography variant="body1" sx={{ mb: 2 }}>
              í‰ê°€í•  ë‹¨ì–´ê°€ ì—†ìŠµë‹ˆë‹¤.
            </Typography>
            <Typography variant="body2" color="text.secondary">
              {evaluationLevel === 1 
                ? '1ì°¨ í•™ìŠµì—ì„œ "1ë²ˆ ë” ë³¼ë˜ìš”", "ëª¨ë¥´ê² ì–´ìš”", "ì•„ ëª°ëì–´ìš”"ë¡œ í‘œì‹œí•œ ë‹¨ì–´ê°€ ìˆì–´ì•¼ í‰ê°€ë¥¼ ì§„í–‰í•  ìˆ˜ ìˆìŠµë‹ˆë‹¤.'
                : '1ì°¨ í‰ê°€ì—ì„œ í‹€ë¦° ë‹¨ì–´ê°€ ìˆì–´ì•¼ 2ì°¨ í‰ê°€ë¥¼ ì§„í–‰í•  ìˆ˜ ìˆìŠµë‹ˆë‹¤.'}
            </Typography>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={onClose}>ë‹«ê¸°</Button>
        </DialogActions>
      </Dialog>
    );
  }

  const currentWord = evaluationWords[currentIndex];
  const progress = ((currentIndex + 1) / evaluationWords.length) * 100;

  return (
    <Dialog
      open={true}
      onClose={onClose}
      maxWidth="md"
      fullWidth
      PaperProps={{
        sx: { height: '80vh', display: 'flex', flexDirection: 'column' }
      }}
    >
      <DialogTitle sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <Typography variant="h6">{evaluationLevel}ì°¨ í‰ê°€</Typography>
          <Chip
            label={`${currentIndex + 1} / ${evaluationWords.length}`}
            color="primary"
            size="small"
          />
        </Box>
        <IconButton onClick={onClose}>
          <CloseIcon />
        </IconButton>
      </DialogTitle>

      <DialogContent sx={{ flex: 1, display: 'flex', flexDirection: 'column', gap: 3 }}>
        {/* ì§„í–‰ë¥  í‘œì‹œ */}
        <Box>
          <LinearProgress
            variant="determinate"
            value={progress}
            sx={{ height: 8, borderRadius: 4 }}
          />
        </Box>

        {/* ë‹¨ì–´ ì¹´ë“œ */}
        <Card sx={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <CardContent sx={{ textAlign: 'center', width: '100%', maxWidth: 600 }}>
            {/* ì˜ì–´ ë‹¨ì–´ */}
            <Typography variant="h3" sx={{ mb: 4, fontWeight: 'bold' }}>
              {currentWord?.english}
            </Typography>

            {/* ì…ë ¥ í•„ë“œ */}
            <Box sx={{ mb: 4 }}>
              <TextField
                ref={inputRef}
                value={userInput}
                onChange={handleInputChange}
                onKeyPress={handleKeyPress}
                placeholder="ëœ»ì˜ ì²« ê¸€ìë¥¼ ì…ë ¥í•˜ì„¸ìš”"
                variant="outlined"
                size="medium"
                autoFocus
                sx={{
                  width: '100%',
                  maxWidth: 400,
                  '& .MuiOutlinedInput-root': {
                    fontSize: '1.5rem',
                  }
                }}
              />
            </Box>

            {/* ìë™ì™„ì„± ê²°ê³¼ */}
            {autoCompletedText && (
              <Paper
                elevation={3}
                onClick={isCorrect ? () => handleNext() : undefined}
                sx={{
                  p: 3,
                  bgcolor: isCorrect ? 'success.50' : 'error.50',
                  border: 2,
                  borderColor: isCorrect ? 'success.main' : 'error.main',
                  borderRadius: 2,
                  cursor: isCorrect ? 'pointer' : 'default',
                  transition: 'transform 0.2s',
                  '&:hover': isCorrect ? {
                    transform: 'scale(1.02)',
                  } : {},
                }}
              >
                <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 2 }}>
                  {isCorrect ? (
                    <CheckCircleIcon color="success" sx={{ fontSize: 40 }} />
                  ) : (
                    <CancelIcon color="error" sx={{ fontSize: 40 }} />
                  )}
                  <Typography variant="h5">
                    {autoCompletedText}
                  </Typography>
                </Box>
              </Paper>
            )}

            {/* ì›ë˜ ëœ» (ë””ë²„ê¹…ìš© - ë‚˜ì¤‘ì— ì œê±°) */}
            <Typography variant="caption" color="text.secondary" sx={{ mt: 2, display: 'block' }}>
              ì •ë‹µ: {currentWord?.meaning}
            </Typography>
          </CardContent>
        </Card>

        {/* ì•¡ì…˜ ë²„íŠ¼ */}
        <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center' }}>
          <Button
            variant="outlined"
            size="large"
            onClick={handleSkip}
            disabled={showResult}
          >
            ê±´ë„ˆë›°ê¸° (ì˜¤ë‹µ)
          </Button>
          <Button
            variant="contained"
            size="large"
            onClick={() => handleNext()}
            disabled={!userInput && !showResult}
            endIcon={<NavigateNextIcon />}
          >
            {currentIndex === evaluationWords.length - 1 ? 'í‰ê°€ ì™„ë£Œ' : 'ë‹¤ìŒ ë‹¨ì–´'}
          </Button>
        </Box>
      </DialogContent>

      {/* ê²°ê³¼ ë‹¤ì´ì–¼ë¡œê·¸ */}
      <Dialog open={showResult} maxWidth="sm" fullWidth>
        <DialogTitle>{evaluationLevel}ì°¨ í‰ê°€ ê²°ê³¼</DialogTitle>
        <DialogContent>
          <Box sx={{ textAlign: 'center', py: 3 }}>
            <Typography variant="h4" sx={{ mb: 2 }}>
              {evaluationWords.filter(w => w.isCorrect).length} / {evaluationWords.length}
            </Typography>
            <Typography variant="h6" color="primary">
              ì •ë‹µë¥ : {Math.round((evaluationWords.filter(w => w.isCorrect).length / evaluationWords.length) * 100)}%
            </Typography>
            {evaluationWords.filter(w => !w.isCorrect).length > 0 ? (
              <Typography variant="body1" color="text.secondary" sx={{ mt: 1 }}>
                ì˜¤ë‹µ: {evaluationWords.filter(w => !w.isCorrect).length}ê°œ
              </Typography>
            ) : (
              <Box sx={{ mt: 2 }}>
                <Typography variant="h6" color="success.main" sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 1 }}>
                  <CheckCircleIcon /> ëª¨ë‘ ì •ë‹µ!
                </Typography>
              </Box>
            )}
