import React, { useState, useEffect } from 'react';
import {
  Card,
  CardContent,
  Button,
  TextField,
  LinearProgress,
  Alert,
  Box,
  Typography,
  Chip,
} from '@mui/material';
import {
  CheckCircle as CheckCircleIcon,
  Cancel as CancelIcon,
} from '@mui/icons-material';

interface VocabularyItem {
  id: string;
  word: string;
  meaning: string;
  example?: string;
  translation?: string;
  round?: number;
  isKnown?: boolean;
}

interface EvaluationResult {
  wordId: string;
  isCorrect: boolean;
  userAnswer: string;
}

interface VocabularyEvaluationProps {
  vocabularyData: VocabularyItem[];
  onComplete: (results: EvaluationResult[], attemptNumber: number) => void;
  attemptNumber: number;
  studentId: string;
}

const VocabularyEvaluation: React.FC<VocabularyEvaluationProps> = ({
  vocabularyData,
  onComplete,
  attemptNumber,
  studentId
}) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [userInput, setUserInput] = useState('');
  const [results, setResults] = useState<EvaluationResult[]>([]);
  const [showFeedback, setShowFeedback] = useState(false);
  const [isCorrect, setIsCorrect] = useState<boolean | null>(null);
  const [suggestions, setSuggestions] = useState<string[]>([]);
  const [correctAnswer, setCorrectAnswer] = useState<string>('');  // 정답 저장
  const [evaluationWords, setEvaluationWords] = useState<VocabularyItem[]>([]);

  // 평가 대상 단어 필터링 (1라운드에서 "알겠어요"를 선택한 단어 제외)
  useEffect(() => {
    console.log('VocabularyEvaluation: vocabularyData received:', vocabularyData);
    const wordsToEvaluate = vocabularyData.filter(
      item => !(item.round === 1 && item.isKnown === true)
    );
    console.log('VocabularyEvaluation: wordsToEvaluate:', wordsToEvaluate);
    setEvaluationWords(wordsToEvaluate);
  }, [vocabularyData]);

  const currentWord = evaluationWords[currentIndex];
  const progress = evaluationWords.length > 0 
    ? ((currentIndex + 1) / evaluationWords.length) * 100 
    : 0;

  // 뜻에서 주요 부분의 첫 글자 추출하는 함수
  const extractKeyLetter = (meaning: string): string => {
    // meaning이 undefined인 경우 처리
    if (!meaning) {
      console.error('extractKeyLetter: meaning is undefined');
      return '';
    }
    
    // 괄호 제거
    let cleanMeaning = meaning.replace(/\([^)]*\)/g, '').trim();
    
    // 조사 제거 패턴
    const particlePatterns = [
      /~라고\s+/g,
      /~하고\s+/g,
      /~에게\s+/g,
      /~를\s+/g,
      /~을\s+/g,
      /~가\s+/g,
      /~이\s+/g,
      /~의\s+/g,
      /~에\s+/g,
      /~로\s+/g,
      /~으로\s+/g,
      /A에게\s+B를\s+/g,
    ];

    particlePatterns.forEach(pattern => {
      cleanMeaning = cleanMeaning.replace(pattern, '');
    });

    // 공백으로 분리
    const words = cleanMeaning.split(/\s+/);
    
    // 단어가 하나면 그 단어 반환
    if (words.length === 1) {
      return words[0];
    }

    // 동사나 명사 찾기 (보통 마지막 단어가 핵심)
    // 하지만 "제공하다", "주다" 같은 동사가 앞에 있을 수도 있음
    for (const word of words) {
      // 동사 어미로 끝나는 단어 찾기
      if (word.endsWith('하다') || word.endsWith('되다') || word.endsWith('이다')) {
        return word.charAt(0);
      }
    }

    // 명사일 가능성이 높은 첫 번째 의미있는 단어의 첫 글자
    return words[0].charAt(0);
  };

  // 자동완성 제안 생성
  const generateSuggestions = (input: string) => {
    if (!currentWord || !input) {
      setSuggestions([]);
      setCorrectAnswer('');
      return;
    }
    
    if (!currentWord.meaning) {
      console.error('generateSuggestions: currentWord.meaning is undefined', currentWord);
      setSuggestions([]);
      setCorrectAnswer('');
      return;
    }

    const keyLetter = extractKeyLetter(currentWord.meaning);
    
    // 첫 글자가 맞으면 전체 뜻을 입력창에 자동완성
    if (keyLetter.length === 1) {
      if (input === keyLetter) {
        setUserInput(currentWord.meaning);  // 전체 뜻을 입력창에 설정
        setCorrectAnswer(currentWord.meaning);
      }
    } else {
      // 전체 단어가 정답인 경우
      if (keyLetter.toLowerCase().startsWith(input.toLowerCase())) {
        setUserInput(keyLetter);  // 전체 단어를 입력창에 설정
        setCorrectAnswer(keyLetter);
      }
    }
  };

  // 입력 변경 처리
  const handleInputChange = (value: string) => {
    setUserInput(value);
    
    // 입력값이 1글자일 때만 자동완성 시도
    if (value.length === 1) {
      generateSuggestions(value);
    }
  };

  // 답변 처리
  const handleAnswer = (correct: boolean, answer: string) => {
    setIsCorrect(correct);
    setShowFeedback(true);
    
    const result: EvaluationResult = {
      wordId: currentWord.id,
      isCorrect: correct,
      userAnswer: answer
    };
    
    setResults([...results, result]);
    
    // 1초 후 다음 문제로
    setTimeout(() => {
      if (currentIndex < evaluationWords.length - 1) {
        setCurrentIndex(currentIndex + 1);
        setUserInput('');
        setShowFeedback(false);
        setIsCorrect(null);
        setSuggestions([]);
        setCorrectAnswer('');
      } else {
        // 평가 완료
        onComplete([...results, result], attemptNumber);
      }
    }, 1000);
  };

  // 입력 버튼 처리
  const handleSubmit = () => {
    if (!currentWord || !currentWord.meaning) {
      console.error('handleSubmit: currentWord or meaning is undefined');
      return;
    }
    
    const keyLetter = extractKeyLetter(currentWord.meaning);
    let isCorrect = false;
    
    // 정답 판정
    if (keyLetter.length === 1) {
      // 한 글자인 경우: 전체 뜻과 비교
      isCorrect = userInput.trim() === currentWord.meaning;
    } else {
      // 여러 글자인 경우: 추출된 단어와 비교
      isCorrect = userInput.trim() === keyLetter;
    }
    
    handleAnswer(isCorrect, userInput);
  };

  const handleKeyPress = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      handleSubmit();
    }
  };

  if (evaluationWords.length === 0) {
    return (
      <Alert severity="info" sx={{ maxWidth: 600, mx: 'auto', mt: 3 }}>
        평가할 단어가 없습니다. 먼저 학습을 완료해주세요.
      </Alert>
    );
  }

  if (!currentWord) {
    return null;
  }

  return (
    <Card sx={{ maxWidth: 800, mx: 'auto', mt: 3 }}>
      <CardContent>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Typography variant="h5">{attemptNumber}차 평가</Typography>
          <Typography variant="body2" color="text.secondary">
            {currentIndex + 1} / {evaluationWords.length}
          </Typography>
        </Box>
        <LinearProgress variant="determinate" value={progress} sx={{ mb: 3 }} />
        {/* 현재 단어 표시 */}
        <Box sx={{ textAlign: 'center', mb: 4 }}>
          <Typography variant="h3" sx={{ fontWeight: 'bold', mb: 2 }}>
            {currentWord.word}
          </Typography>
          {currentWord.example && (
            <Typography variant="body1" sx={{ fontStyle: 'italic', color: 'text.secondary' }}>
              "{currentWord.example}"
            </Typography>
          )}
        </Box>

        {/* 입력 필드 */}
        <Box sx={{ mb: 3 }}>
          <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
            뜻의 첫 글자를 입력하세요
          </Typography>
          <Box sx={{ position: 'relative' }}>
            <TextField
              fullWidth
              value={userInput}
              onChange={(e) => handleInputChange(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="첫 글자 입력..."
              disabled={showFeedback}
              autoFocus
              variant="outlined"
            />
          </Box>
          <Button
            variant="contained"
            onClick={handleSubmit}
            disabled={showFeedback}
            sx={{ mt: 2 }}
            fullWidth
          >
            입력
          </Button>
        </Box>

        {/* 피드백 표시 */}
        {showFeedback && (
          <Alert
            severity={isCorrect ? 'success' : 'error'}
            icon={isCorrect ? <CheckCircleIcon /> : <CancelIcon />}
            sx={{ mb: 3 }}
          >
            {isCorrect ? '정답입니다!' : `오답입니다. 정답: ${currentWord.meaning}`}
          </Alert>
        )}

        {/* 진행 상태 */}
        <Box sx={{ display: 'flex', justifyContent: 'center', gap: 0.5 }}>
          {evaluationWords.map((_, index) => (
            <Box
              key={index}
              sx={{
                width: 8,
                height: 8,
                borderRadius: '50%',
                bgcolor: index < currentIndex
                  ? results[index]?.isCorrect
                    ? 'success.main'
                    : 'error.main'
                  : index === currentIndex
                  ? 'primary.main'
                  : 'grey.300'
              }}
            />
          ))}
        </Box>
      </CardContent>
    </Card>
  );
};

export default VocabularyEvaluation;
