import React, { useState } from 'react';
import {
  Box,
  Button,
  Card,
  CardContent,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
  Chip,
} from '@mui/material';
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  Work as WorkIcon,
} from '@mui/icons-material';
import { fetchGoogleSheetData, validateGoogleSheetUrl } from '../utils/googleSheets';

interface Task {
  id: string;
  title: string;
  description: string;
  area: 'vocabulary' | 'phrase' | 'grammar' | 'logic';
  status: 'active' | 'completed' | 'pending';
  assignedStudents: string[];
  googleSheetLink?: string;
}

const TaskManagement: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([
    {
      id: '1',
      title: '기본 문법 규칙 학습',
      description: '시제, 조동사, 수동태 등 기본 문법 규칙 학습 및 연습 문제 풀이',
      area: 'grammar',
      status: 'active',
      assignedStudents: ['김철수', '이영희', '박민수'],
    },
    {
      id: '2',
      title: '일상 대화 표현 학습',
      description: '일상 대화에서 사용하는 기본 표현과 역할극 연습',
      area: 'phrase',
      status: 'pending',
      assignedStudents: ['김철수', '이영희'],
    },
    {
      id: '3',
      title: 'TOEIC 핵심 단어 학습',
      description: 'TOEIC 시험에 자주 출제되는 핵심 단어 100개 학습',
      area: 'vocabulary',
      status: 'active',
      assignedStudents: ['박민수'],
    },
    {
      id: '4',
      title: '영어 논리적 사고 훈련',
      description: '영어로 논리적 사고를 표현하는 방법과 구조 학습',
      area: 'logic',
      status: 'pending',
      assignedStudents: [],
    },
  ]);

  const [openDialog, setOpenDialog] = useState(false);
  const [openWorkDialog, setOpenWorkDialog] = useState(false);
  const [editingTask, setEditingTask] = useState<Task | null>(null);
  const [workingTask, setWorkingTask] = useState<Task | null>(null);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    area: 'vocabulary' as 'vocabulary' | 'phrase' | 'grammar' | 'logic',
  });
  const [workData, setWorkData] = useState({
    googleSheetLink: '',
    previewData: [] as Array<{ unit: string; english: string; meaning: string }>,
    isLoading: false,
    error: '',
  });

  const handleOpenDialog = (task?: Task, area?: 'vocabulary' | 'phrase' | 'grammar' | 'logic') => {
    if (task) {
      setEditingTask(task);
      setFormData({
        title: task.title,
        description: task.description,
        area: task.area,
      });
    } else {
      setEditingTask(null);
      setFormData({
        title: '',
        description: '',
        area: area || 'vocabulary',
      });
    }
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setEditingTask(null);
    setFormData({
      title: '',
      description: '',
      area: 'vocabulary',
    });
  };

  const handleSubmit = () => {
    if (editingTask) {
      // 편집 모드
      setTasks(tasks.map(task =>
        task.id === editingTask.id
          ? { ...task, ...formData }
          : task
      ));
    } else {
      // 새 과제 추가
      const newTask: Task = {
        id: Date.now().toString(),
        ...formData,
        status: 'pending',
        assignedStudents: [],
      };
      setTasks([...tasks, newTask]);
    }
    handleCloseDialog();
  };

  const handleDeleteTask = (taskId: string) => {
    setTasks(tasks.filter(task => task.id !== taskId));
  };

  const handleWorkTask = (task: Task) => {
    setWorkingTask(task);
    setWorkData({
      googleSheetLink: task.googleSheetLink || '',
      previewData: [],
    });
    setOpenWorkDialog(true);
  };

  const handleCloseWorkDialog = () => {
    setOpenWorkDialog(false);
    setWorkingTask(null);
    setWorkData({
      googleSheetLink: '',
      previewData: [],
    });
  };

  const handlePreviewSheet = () => {
    // 실제로는 구글 시트 API를 호출하여 데이터를 가져와야 함
    // 여기서는 샘플 데이터로 미리보기
    const sampleData = [
      { unit: 'Unit 1', english: 'apple', meaning: '사과' },
      { unit: 'Unit 1', english: 'banana', meaning: '바나나' },
      { unit: 'Unit 2', english: 'computer', meaning: '컴퓨터' },
      { unit: 'Unit 2', english: 'keyboard', meaning: '키보드' },
    ];
    setWorkData({ ...workData, previewData: sampleData });
  };

  const handleSaveSheetLink = () => {
    if (workingTask) {
      setTasks(tasks.map(task =>
        task.id === workingTask.id
          ? { ...task, googleSheetLink: workData.googleSheetLink }
          : task
      ));
      handleCloseWorkDialog();
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active':
        return 'primary';
      case 'completed':
        return 'success';
      case 'pending':
        return 'warning';
      default:
        return 'default';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'active':
        return '진행중';
      case 'completed':
        return '완료';
      case 'pending':
        return '대기중';
      default:
        return status;
    }
  };

  const getAreaColor = (area: string) => {
    switch (area) {
      case 'vocabulary':
        return 'primary';
      case 'phrase':
        return 'secondary';
      case 'grammar':
        return 'success';
      case 'logic':
        return 'warning';
      default:
        return 'default';
    }
  };

  const getAreaText = (area: string) => {
    switch (area) {
      case 'vocabulary':
        return '단어';
      case 'phrase':
        return '구문';
      case 'grammar':
        return '어법';
      case 'logic':
        return '논리';
      default:
        return area;
    }
  };

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" component="h1">
          과제 관리
        </Typography>
        <Box sx={{ display: 'flex', gap: 2 }}>
          <Button
            variant="contained"
            color="primary"
            startIcon={<AddIcon />}
            onClick={() => handleOpenDialog(undefined, 'vocabulary')}
          >
            단어 과제 등록
          </Button>
          <Button
            variant="outlined"
            color="secondary"
            startIcon={<AddIcon />}
            onClick={() => handleOpenDialog(undefined, 'phrase')}
          >
            구문 과제 등록
          </Button>
          <Button
            variant="outlined"
            color="success"
            startIcon={<AddIcon />}
            onClick={() => handleOpenDialog(undefined, 'grammar')}
          >
            어법 과제 등록
          </Button>
          <Button
            variant="outlined"
            color="warning"
            startIcon={<AddIcon />}
            onClick={() => handleOpenDialog(undefined, 'logic')}
          >
            논리 과제 등록
          </Button>
        </Box>
      </Box>

      <Card>
        <CardContent>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>과제명</TableCell>
                  <TableCell>설명</TableCell>
                  <TableCell>영역</TableCell>
                  <TableCell>상태</TableCell>
                  <TableCell>배정된 학생</TableCell>
                  <TableCell>작업</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {tasks.map((task) => (
                  <TableRow key={task.id}>
                    <TableCell>{task.title}</TableCell>
                    <TableCell>{task.description}</TableCell>
                    <TableCell>
                      <Chip
                        label={getAreaText(task.area)}
                        color={getAreaColor(task.area) as any}
                        size="small"
                      />
                    </TableCell>
                    <TableCell>
                      <Chip
                        label={getStatusText(task.status)}
                        color={getStatusColor(task.status) as any}
                        size="small"
                      />
                    </TableCell>
                    <TableCell>
                      {task.assignedStudents.length > 0 ? (
                        <Box>
                          {task.assignedStudents.slice(0, 2).join(', ')}
                          {task.assignedStudents.length > 2 && ` 외 ${task.assignedStudents.length - 2}명`}
                        </Box>
                      ) : (
                        '배정된 학생 없음'
                      )}
                    </TableCell>
                    <TableCell>
                      <IconButton
                        size="small"
                        onClick={() => handleOpenDialog(task, task.area)}
                      >
                        <EditIcon />
                      </IconButton>
                      <IconButton
                        size="small"
                        onClick={() => handleWorkTask(task)}
                        color="primary"
                      >
                        <WorkIcon />
                      </IconButton>
                      <IconButton
                        size="small"
                        onClick={() => handleDeleteTask(task.id)}
                      >
                        <DeleteIcon />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </CardContent>
      </Card>

      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
        <DialogTitle>
          {editingTask ? '과제 편집' : '새 과제 추가'}
        </DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 2 }}>
            <TextField
              fullWidth
              label="과제명"
              value={formData.title}
              onChange={(e) => setFormData({ ...formData, title: e.target.value })}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              label="설명"
              multiline
              rows={3}
              value={formData.description}
              onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              select
              label="영역"
              value={formData.area}
              onChange={(e) => setFormData({ ...formData, area: e.target.value as any })}
              SelectProps={{
                native: true,
              }}
            >
              <option value="vocabulary">단어</option>
              <option value="phrase">구문</option>
              <option value="grammar">어법</option>
              <option value="logic">논리</option>
            </TextField>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>취소</Button>
          <Button onClick={handleSubmit} variant="contained">
            {editingTask ? '수정' : '추가'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* 구글 시트 작업 다이얼로그 */}
      <Dialog open={openWorkDialog} onClose={handleCloseWorkDialog} maxWidth="md" fullWidth>
        <DialogTitle>
          {workingTask?.title} - 구글 시트 연결
        </DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 2 }}>
            <Typography variant="h6" sx={{ mb: 2 }}>
              구글 시트 링크 입력
            </Typography>
            <TextField
              fullWidth
              label="구글 시트 공개 링크"
              value={workData.googleSheetLink}
              onChange={(e) => setWorkData({ ...workData, googleSheetLink: e.target.value })}
              placeholder="https://docs.google.com/spreadsheets/d/..."
              sx={{ mb: 2 }}
            />
            <Box sx={{ display: 'flex', gap: 2, mb: 3 }}>
              <Button
                variant="outlined"
                onClick={handlePreviewSheet}
                disabled={!workData.googleSheetLink}
              >
                미리보기
              </Button>
              <Button
                variant="contained"
                onClick={handleSaveSheetLink}
                disabled={!workData.googleSheetLink}
              >
                링크 저장
              </Button>
            </Box>

            {workData.previewData.length > 0 && (
              <Box>
                <Typography variant="h6" sx={{ mb: 2 }}>
                  단어장 미리보기
                </Typography>
                <TableContainer component={Paper}>
                  <Table size="small">
                    <TableHead>
                      <TableRow>
                        <TableCell>단원명</TableCell>
                        <TableCell>영어</TableCell>
                        <TableCell>뜻</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {workData.previewData.map((row, index) => (
                        <TableRow key={index}>
                          <TableCell>{row.unit}</TableCell>
                          <TableCell>{row.english}</TableCell>
                          <TableCell>{row.meaning}</TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>
              </Box>
            )}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseWorkDialog}>닫기</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default TaskManagement;
