import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  Card,
  CardContent,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
  Chip,
  Avatar,
  Grid,
  Tabs,
  Tab,
  Checkbox,
  FormControlLabel,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  Grade as GradeIcon,
  Assignment as AssignmentIcon,
} from '@mui/icons-material';
import { saveStudents, loadStudents } from '../utils/storage';
import { saveTasks, loadTasks } from '../utils/storage';

interface Task {
  id: string;
  title: string;
  description: string;
  area: 'vocabulary' | 'phrase' | 'grammar' | 'logic';
  status: 'active' | 'completed' | 'pending';
  assignedStudents: string[];
  googleSheetLink?: string;
  vocabularyData?: Array<{ unit: string; english: string; meaning: string }>;
}

interface TaskAssignment {
  taskId: string;
  taskTitle: string;
  area: 'vocabulary' | 'phrase' | 'grammar' | 'logic';
  weeklySchedule: {
    [day: string]: {
      isActive: boolean;
      dailyAmount: string;
    };
  };
  startDate: string;
  endDate?: string;
  status: 'active' | 'completed' | 'paused';
  progress: {
    completed: number;
    total: number;
  };
}

interface Student {
  id: string;
  name: string;
  address: string;
  phone: string;
  grade: '초6' | '중1' | '중2' | '중3' | '고1' | '고2' | '고3';
  joinDate: string;
  status: 'active' | 'inactive';
  assignedTasks: string[];
  taskAssignments: TaskAssignment[];
  grades: {
    [year: string]: {
      mockExam3?: string;
      mockExam6?: string;
      mockExam9?: string;
      mockExam11?: string;
      midterm1?: string;
      final1?: string;
      midterm2?: string;
      final2?: string;
    };
  };
}

const StudentManagement: React.FC = () => {
  const [students, setStudents] = useState<Student[]>(() => {
    // 로컬 스토리지에서 저장된 데이터 불러오기
    const savedStudents = loadStudents();
    if (savedStudents.length > 0) {
      return savedStudents;
    }
    
    // 저장된 데이터가 없으면 기본 데이터 사용
    return [
      {
        id: '1',
        name: '김철수',
        address: '서울시 강남구 역삼동 123-45',
        phone: '010-1234-5678',
        grade: '중2',
        joinDate: '2023-09-01',
        status: 'active',
        assignedTasks: ['영어 문법 과제', '영어 회화 연습'],
        taskAssignments: [],
        grades: {
          '2024': {
            mockExam3: '85점',
            mockExam6: '92점',
            midterm1: '88점',
            final1: '90점',
          },
        },
      },
      {
        id: '2',
        name: '이영희',
        address: '서울시 서초구 서초동 456-78',
        phone: '010-2345-6789',
        grade: '고1',
        joinDate: '2023-10-15',
        status: 'active',
        assignedTasks: ['영어 문법 과제'],
        taskAssignments: [],
        grades: {
          '2024': {
            mockExam3: '78점',
            mockExam6: '85점',
            midterm1: '82점',
            final1: '87점',
          },
        },
      },
      {
        id: '3',
        name: '박민수',
        address: '서울시 마포구 합정동 789-12',
        phone: '010-3456-7890',
        grade: '고3',
        joinDate: '2023-08-20',
        status: 'active',
        assignedTasks: ['영어 문법 과제'],
        taskAssignments: [],
        grades: {
          '2024': {
            mockExam3: '95점',
            mockExam6: '98점',
            midterm1: '96점',
            final1: '97점',
          },
        },
      },
    ];
  });

  // students가 변경될 때마다 로컬 스토리지에 저장
  useEffect(() => {
    saveStudents(students);
  }, [students]);

  const [openDialog, setOpenDialog] = useState(false);
  const [openGradeDialog, setOpenGradeDialog] = useState(false);
  const [openAssignmentDialog, setOpenAssignmentDialog] = useState(false);
  const [editingStudent, setEditingStudent] = useState<Student | null>(null);
  const [gradingStudent, setGradingStudent] = useState<Student | null>(null);
  const [assigningStudent, setAssigningStudent] = useState<Student | null>(null);
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear().toString());
  const [selectedArea, setSelectedArea] = useState<'vocabulary' | 'phrase' | 'grammar' | 'logic'>('vocabulary');
  const [weeklySchedule, setWeeklySchedule] = useState<{
    [day: string]: { isActive: boolean };
  }>({});
  const [dailyAmount, setDailyAmount] = useState<string>('');
  const [selectedStartUnit, setSelectedStartUnit] = useState<string>('');
  const [availableUnits, setAvailableUnits] = useState<string[]>([]);
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    phone: '',
    grade: '초6' as '초6' | '중1' | '중2' | '중3' | '고1' | '고2' | '고3',
  });

  const handleOpenDialog = (student?: Student) => {
    if (student) {
      setEditingStudent(student);
      setFormData({
        name: student.name,
        address: student.address,
        phone: student.phone,
        grade: student.grade,
      });
    } else {
      setEditingStudent(null);
      setFormData({
        name: '',
        address: '',
        phone: '',
        grade: '초6',
      });
    }
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setEditingStudent(null);
    setFormData({
      name: '',
      address: '',
      phone: '',
      grade: '초6',
    });
  };

  const handleSubmit = () => {
    if (editingStudent) {
      // 편집 모드
      setStudents(students.map(student =>
        student.id === editingStudent.id
          ? { ...student, ...formData }
          : student
      ));
    } else {
      // 새 학생 추가
      const newStudent: Student = {
        id: Date.now().toString(),
        ...formData,
        joinDate: new Date().toISOString().split('T')[0],
        status: 'active',
        assignedTasks: [],
        taskAssignments: [],
        grades: {},
      };
      setStudents([...students, newStudent]);
    }
    handleCloseDialog();
  };

  const handleDeleteStudent = (studentId: string) => {
    setStudents(students.filter(student => student.id !== studentId));
  };

  const handleOpenGradeDialog = (student: Student) => {
    setGradingStudent(student);
    setOpenGradeDialog(true);
  };

  const handleCloseGradeDialog = () => {
    setOpenGradeDialog(false);
    setGradingStudent(null);
  };

  const handleSaveGrade = (studentId: string, gradeType: string, score: string) => {
    setStudents(students.map(student =>
      student.id === studentId
        ? {
            ...student,
            grades: {
              ...student.grades,
              [selectedYear]: {
                ...student.grades[selectedYear],
                [gradeType]: score,
              },
            },
          }
        : student
    ));
  };

  const handleOpenAssignmentDialog = (student: Student) => {
    setAssigningStudent(student);
    setSelectedArea('vocabulary');
    setWeeklySchedule({});
    setDailyAmount('');
    setSelectedVocabularyTask(null);
    setVocabularyAnalysis(null);
    setOpenAssignmentDialog(true);
  };

  const handleCloseAssignmentDialog = () => {
    setOpenAssignmentDialog(false);
    setAssigningStudent(null);
    setSelectedArea('vocabulary');
    setWeeklySchedule({});
    setDailyAmount('');
    setSelectedVocabularyTask(null);
    setVocabularyAnalysis(null);
  };

  const handleAssignTask = (taskId: string, taskTitle: string, area: 'vocabulary' | 'phrase' | 'grammar' | 'logic', weeklySchedule: any) => {
    if (!assigningStudent) return;

    const newAssignment: TaskAssignment = {
      taskId,
      taskTitle,
      area,
      weeklySchedule: {
        ...weeklySchedule,
        dailyAmount,
      },
      startDate: new Date().toISOString().split('T')[0],
      status: 'active',
      progress: {
        completed: 0,
        total: 0, // 단어장의 경우 단원 수로 계산
      },
    };

    setStudents(students.map(student =>
      student.id === assigningStudent.id
        ? {
            ...student,
            taskAssignments: [...student.taskAssignments, newAssignment],
          }
        : student
    ));

    handleCloseAssignmentDialog();
  };

  const handleScheduleChange = (day: string, field: string, value: any) => {
    if (field === 'isActive') {
      setWeeklySchedule(prev => ({
        ...prev,
        [day]: {
          ...prev[day],
          isActive: value,
        },
      }));
    } else if (field === 'value') {
      setDailyAmount(value);
    }
  };

  // 선택된 요일 수 계산 함수
  const getActiveDaysCount = () => {
    return Object.values(weeklySchedule).filter((day: any) => 
      day && typeof day === 'object' && day.isActive
    ).length;
  };

  // 오늘부터 시작하는 날짜 계산 함수
  const calculateStartDate = (weeklySchedule: { [day: string]: { isActive: boolean } }) => {
    const today = new Date();
    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
    const todayDayName = dayNames[today.getDay()];
    
    // 오늘이 학습 요일에 포함되어 있는지 확인
    if (weeklySchedule[todayDayName]?.isActive) {
      return today.toISOString().split('T')[0];
    }
    
    // 오늘이 학습 요일에 없으면 다음 학습 요일 찾기
    let nextDay = new Date(today);
    let daysChecked = 0;
    
    while (daysChecked < 7) {
      nextDay.setDate(nextDay.getDate() + 1);
      const nextDayName = dayNames[nextDay.getDay()];
      
      if (weeklySchedule[nextDayName]?.isActive) {
        return nextDay.toISOString().split('T')[0];
      }
      
      daysChecked++;
    }
    
    // 기본값으로 오늘 날짜 반환
    return today.toISOString().split('T')[0];
  };

  // 단어장 단원 분석 함수
  const analyzeVocabularyUnits = (task: Task) => {
    if (task.area !== 'vocabulary' || !task.vocabularyData) {
      return null;
    }

    // 단원별로 그룹화
    const units = task.vocabularyData.reduce((acc, item) => {
      if (!acc[item.unit]) {
        acc[item.unit] = [];
      }
      acc[item.unit].push(item);
      return acc;
    }, {} as { [unit: string]: Array<{ unit: string; english: string; meaning: string }> });

    const unitNames = Object.keys(units);
    const totalUnits = unitNames.length;
    const totalWords = task.vocabularyData.length;

    return {
      totalUnits,
      totalWords,
      unitNames,
      units,
    };
  };

  // 선택된 단어장 정보
  const [selectedVocabularyTask, setSelectedVocabularyTask] = useState<Task | null>(null);
  const [vocabularyAnalysis, setVocabularyAnalysis] = useState<any>(null);

  // 단어장 선택 함수
  const handleSelectVocabularyTask = (task: Task) => {
    setSelectedVocabularyTask(task);
    const analysis = analyzeVocabularyUnits(task);
    setVocabularyAnalysis(analysis);
    if (analysis) {
      setAvailableUnits(analysis.unitNames);
      setSelectedStartUnit(analysis.unitNames[0] || ''); // 첫 번째 단원을 기본값으로 설정
    }
  };

  // 단어장 선택 해제 함수
  const handleDeselectVocabularyTask = () => {
    setSelectedVocabularyTask(null);
    setVocabularyAnalysis(null);
  };

  const getGradeColor = (grade: string) => {
    switch (grade) {
      case '초6':
        return 'warning';
      case '중1':
      case '중2':
      case '중3':
        return 'primary';
      case '고1':
      case '고2':
      case '고3':
        return 'success';
      default:
        return 'default';
    }
  };

  const getGradeText = (grade: string) => {
    return grade;
  };

  const getStatusColor = (status: string) => {
    return status === 'active' ? 'success' : 'error';
  };

  const getStatusText = (status: string) => {
    return status === 'active' ? '활성' : '비활성';
  };

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" component="h1">
          학생 관리
        </Typography>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={() => handleOpenDialog()}
        >
          새 학생 추가
        </Button>
      </Box>

      <Card>
        <CardContent>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>학생</TableCell>
                  <TableCell>주소</TableCell>
                  <TableCell>연락처</TableCell>
                  <TableCell>학년</TableCell>
                  <TableCell>가입일</TableCell>
                  <TableCell>상태</TableCell>
                  <TableCell>배정된 과제</TableCell>
                  <TableCell>과제 배정</TableCell>
                  <TableCell>성적</TableCell>
                  <TableCell>작업</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {students.map((student) => (
                  <TableRow key={student.id}>
                    <TableCell>
                      <Box sx={{ display: 'flex', alignItems: 'center' }}>
                        <Avatar sx={{ mr: 2 }}>
                          {student.name.charAt(0)}
                        </Avatar>
                        {student.name}
                      </Box>
                    </TableCell>
                    <TableCell>{student.address}</TableCell>
                    <TableCell>{student.phone}</TableCell>
                    <TableCell>
                      <Chip
                        label={getGradeText(student.grade)}
                        color={getGradeColor(student.grade) as any}
                        size="small"
                      />
                    </TableCell>
                    <TableCell>{student.joinDate}</TableCell>
                    <TableCell>
                      <Chip
                        label={getStatusText(student.status)}
                        color={getStatusColor(student.status) as any}
                        size="small"
                      />
                    </TableCell>
                    <TableCell>
                      {student.assignedTasks.length > 0 ? (
                        <Box>
                          {student.assignedTasks.slice(0, 2).join(', ')}
                          {student.assignedTasks.length > 2 && ` 외 ${student.assignedTasks.length - 2}개`}
                        </Box>
                      ) : (
                        '배정된 과제 없음'
                      )}
                    </TableCell>
                    <TableCell>
                      {student.taskAssignments && student.taskAssignments.length > 0 ? (
                        <Chip
                          label={`${student.taskAssignments.length}개 과제`}
                          color="success"
                          size="small"
                        />
                      ) : (
                        <Chip
                          label="배정 없음"
                          color="default"
                          size="small"
                        />
                      )}
                    </TableCell>
                    <TableCell>
                      {student.grades && Object.keys(student.grades).length > 0 ? (
                        <Chip
                          label={`${Object.keys(student.grades).length}년도 성적`}
                          color="info"
                          size="small"
                        />
                      ) : (
                        <Chip
                          label="성적 없음"
                          color="default"
                          size="small"
                        />
                      )}
                    </TableCell>
                    <TableCell>
                      <IconButton
                        size="small"
                        onClick={() => handleOpenDialog(student)}
                      >
                        <EditIcon />
                      </IconButton>
                      <IconButton
                        size="small"
                        onClick={() => handleOpenGradeDialog(student)}
                        color="info"
                      >
                        <GradeIcon />
                      </IconButton>
                      <IconButton
                        size="small"
                        onClick={() => handleOpenAssignmentDialog(student)}
                        color="success"
                      >
                        <AssignmentIcon />
                      </IconButton>
                      <IconButton
                        size="small"
                        onClick={() => handleDeleteStudent(student.id)}
                      >
                        <DeleteIcon />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </CardContent>
      </Card>

      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
        <DialogTitle>
          {editingStudent ? '학생 정보 편집' : '새 학생 추가'}
        </DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 2 }}>
            <TextField
              fullWidth
              label="이름"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              label="주소"
              value={formData.address}
              onChange={(e) => setFormData({ ...formData, address: e.target.value })}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              label="연락처"
              value={formData.phone}
              onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              select
              label="학년"
              value={formData.grade}
              onChange={(e) => setFormData({ ...formData, grade: e.target.value as any })}
              SelectProps={{
                native: true,
              }}
            >
              <option value="초6">초등학교 6학년</option>
              <option value="중1">중학교 1학년</option>
              <option value="중2">중학교 2학년</option>
              <option value="중3">중학교 3학년</option>
              <option value="고1">고등학교 1학년</option>
              <option value="고2">고등학교 2학년</option>
              <option value="고3">고등학교 3학년</option>
            </TextField>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>취소</Button>
          <Button onClick={handleSubmit} variant="contained">
            {editingStudent ? '수정' : '추가'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* 성적 관리 다이얼로그 */}
      <Dialog open={openGradeDialog} onClose={handleCloseGradeDialog} maxWidth="lg" fullWidth>
        <DialogTitle>
          {gradingStudent?.name} - 성적 관리
        </DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 2 }}>
            {/* 연도 선택 */}
            <Box sx={{ mb: 3 }}>
              <Typography variant="h6" sx={{ mb: 2 }}>
                연도 선택
              </Typography>
              <TextField
                select
                label="연도"
                value={selectedYear}
                onChange={(e) => setSelectedYear(e.target.value)}
                sx={{ minWidth: 120 }}
              >
                {Array.from({ length: 5 }, (_, i) => new Date().getFullYear() - i).map(year => (
                  <option key={year} value={year.toString()}>
                    {year}년
                  </option>
                ))}
              </TextField>
            </Box>

            <Typography variant="h6" sx={{ mb: 2 }}>
              {selectedYear}년 모의고사 성적
            </Typography>
            <Grid container spacing={2} sx={{ mb: 3 }}>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="3월 모의고사"
                  value={gradingStudent?.grades?.[selectedYear]?.mockExam3 || ''}
                  onChange={(e) => {
                    if (gradingStudent) {
                      handleSaveGrade(gradingStudent.id, 'mockExam3', e.target.value);
                    }
                  }}
                  placeholder="예: 85점, A등급"
                />
              </Grid>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="6월 모의고사"
                  value={gradingStudent?.grades?.[selectedYear]?.mockExam6 || ''}
                  onChange={(e) => {
                    if (gradingStudent) {
                      handleSaveGrade(gradingStudent.id, 'mockExam6', e.target.value);
                    }
                  }}
                  placeholder="예: 92점, A등급"
                />
              </Grid>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="9월 모의고사"
                  value={gradingStudent?.grades?.[selectedYear]?.mockExam9 || ''}
                  onChange={(e) => {
                    if (gradingStudent) {
                      handleSaveGrade(gradingStudent.id, 'mockExam9', e.target.value);
                    }
                  }}
                  placeholder="예: 88점, A등급"
                />
              </Grid>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="11월 모의고사"
                  value={gradingStudent?.grades?.[selectedYear]?.mockExam11 || ''}
                  onChange={(e) => {
                    if (gradingStudent) {
                      handleSaveGrade(gradingStudent.id, 'mockExam11', e.target.value);
                    }
                  }}
                  placeholder="예: 95점, A등급"
                />
              </Grid>
            </Grid>

            <Typography variant="h6" sx={{ mb: 2 }}>
              {selectedYear}년 학기별 성적
            </Typography>
            <Grid container spacing={2}>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="1학기 중간고사"
                  value={gradingStudent?.grades?.[selectedYear]?.midterm1 || ''}
                  onChange={(e) => {
                    if (gradingStudent) {
                      handleSaveGrade(gradingStudent.id, 'midterm1', e.target.value);
                    }
                  }}
                  placeholder="예: 88점, A등급"
                />
              </Grid>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="1학기 기말고사"
                  value={gradingStudent?.grades?.[selectedYear]?.final1 || ''}
                  onChange={(e) => {
                    if (gradingStudent) {
                      handleSaveGrade(gradingStudent.id, 'final1', e.target.value);
                    }
                  }}
                  placeholder="예: 90점, A등급"
                />
              </Grid>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="2학기 중간고사"
                  value={gradingStudent?.grades?.[selectedYear]?.midterm2 || ''}
                  onChange={(e) => {
                    if (gradingStudent) {
                      handleSaveGrade(gradingStudent.id, 'midterm2', e.target.value);
                    }
                  }}
                  placeholder="예: 87점, A등급"
                />
              </Grid>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="2학기 기말고사"
                  value={gradingStudent?.grades?.[selectedYear]?.final2 || ''}
                  onChange={(e) => {
                    if (gradingStudent) {
                      handleSaveGrade(gradingStudent.id, 'final2', e.target.value);
                    }
                  }}
                  placeholder="예: 93점, A등급"
                />
              </Grid>
            </Grid>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseGradeDialog}>닫기</Button>
        </DialogActions>
      </Dialog>

      {/* 과제 배정 다이얼로그 */}
      <Dialog open={openAssignmentDialog} onClose={handleCloseAssignmentDialog} maxWidth="lg" fullWidth>
        <DialogTitle>
          {assigningStudent?.name} - 과제 배정
        </DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 2 }}>
            <Typography variant="h6" sx={{ mb: 2 }}>
              배정 가능한 과제 목록
            </Typography>
            
            {/* 영역별 탭 */}
            <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
              <Tabs value={selectedArea} onChange={(e, newValue) => setSelectedArea(newValue)}>
                <Tab label="단어" value="vocabulary" />
                <Tab label="구문" value="phrase" />
                <Tab label="어법" value="grammar" />
                <Tab label="논리" value="logic" />
              </Tabs>
            </Box>

            {/* 단어 영역일 때 단어장 선택 및 분석 */}
            {selectedArea === 'vocabulary' && (
              <Box sx={{ mb: 3 }}>
                <Typography variant="h6" sx={{ mb: 2 }}>
                  단어장 선택
                </Typography>
                
                {/* 단어장 목록 */}
                <Grid container spacing={2} sx={{ mb: 3 }}>
                  {loadTasks()
                    .filter((task: Task) => task.area === 'vocabulary' && task.vocabularyData)
                    .map((task: Task) => {
                      const analysis = analyzeVocabularyUnits(task);
                      return (
                        <Grid item xs={12} md={4} key={task.id}>
                          <Card 
                            sx={{ 
                              cursor: 'pointer',
                              border: selectedVocabularyTask?.id === task.id ? 2 : 1,
                              borderColor: selectedVocabularyTask?.id === task.id ? 'primary.main' : 'divider'
                            }}
                            onClick={() => handleSelectVocabularyTask(task)}
                          >
                            <CardContent>
                              <Typography variant="h6" sx={{ mb: 1 }}>
                                {task.title}
                              </Typography>
                              <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                                {task.description}
                              </Typography>
                              {analysis && (
                                <Box>
                                  <Typography variant="body2" color="primary">
                                    총 {analysis.totalUnits}단원, {analysis.totalWords}개 단어
                                  </Typography>
                                  <Typography variant="caption" color="text.secondary">
                                    단원: {analysis.unitNames.slice(0, 3).join(', ')}
                                    {analysis.unitNames.length > 3 && '...'}
                                  </Typography>
                                  <Chip
                                    label={task.status === 'active' ? '활성' : task.status === 'pending' ? '대기' : '완료'}
                                    color={task.status === 'active' ? 'success' : task.status === 'pending' ? 'warning' : 'default'}
                                    size="small"
                                    sx={{ mt: 1 }}
                                  />
                                </Box>
                              )}
                            </CardContent>
                          </Card>
                        </Grid>
                      );
                    })}
                </Grid>

                {/* 선택된 단어장 분석 정보 */}
                {selectedVocabularyTask && vocabularyAnalysis && (
                  <Box sx={{ mb: 3, p: 2, bgcolor: 'grey.50', borderRadius: 1 }}>
                    <Typography variant="h6" sx={{ mb: 2 }}>
                      {selectedVocabularyTask.title} - 단원 분석
                    </Typography>
                    
                    <Grid container spacing={2}>
                      <Grid item xs={12} md={6}>
                        <Typography variant="subtitle1" sx={{ mb: 1 }}>
                          전체 정보
                        </Typography>
                        <Typography variant="body2">
                          • 총 단원 수: {vocabularyAnalysis.totalUnits}단원
                        </Typography>
                        <Typography variant="body2">
                          • 총 단어 수: {vocabularyAnalysis.totalWords}개
                        </Typography>
                        <Typography variant="body2">
                          • 평균 단원당: {Math.round(vocabularyAnalysis.totalWords / vocabularyAnalysis.totalUnits)}개 단어
                        </Typography>
                      </Grid>
                      
                      <Grid item xs={12} md={6}>
                        <Typography variant="subtitle1" sx={{ mb: 1 }}>
                          시작 단원 선택
                        </Typography>
                        <FormControl fullWidth sx={{ mb: 2 }}>
                          <InputLabel>시작 단원</InputLabel>
                          <Select
                            value={selectedStartUnit}
                            onChange={(e) => setSelectedStartUnit(e.target.value)}
                            label="시작 단원"
                          >
                            {vocabularyAnalysis.unitNames.map((unitName: string) => (
                              <MenuItem key={unitName} value={unitName}>
                                {unitName} ({vocabularyAnalysis.units[unitName].length}개 단어)
                              </MenuItem>
                            ))}
                          </Select>
                        </FormControl>
                        
                        <Typography variant="subtitle1" sx={{ mb: 1 }}>
                          단원별 단어 수
                        </Typography>
                        <Box sx={{ maxHeight: 200, overflow: 'auto' }}>
                          {vocabularyAnalysis.unitNames.map((unitName: string) => (
                            <Typography key={unitName} variant="body2">
                              • {unitName}: {vocabularyAnalysis.units[unitName].length}개 단어
                            </Typography>
                          ))}
                        </Box>
                      </Grid>
                    </Grid>

                    <Button
                      variant="outlined"
                      onClick={handleDeselectVocabularyTask}
                      sx={{ mt: 2 }}
                    >
                      단어장 선택 해제
                    </Button>
                  </Box>
                )}
              </Box>
            )}

            {/* 과제 목록 - 단어 영역이 아닐 때만 표시 */}
            {selectedArea !== 'vocabulary' && (
              <Grid container spacing={2}>
                {loadTasks()
                  .filter((task: Task) => task.area === selectedArea && task.status === 'active')
                  .map((task: Task) => (
                    <Grid item xs={12} md={6} key={task.id}>
                      <Card>
                        <CardContent>
                          <Typography variant="h6" sx={{ mb: 1 }}>
                            {task.title}
                          </Typography>
                          <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                            {task.description}
                          </Typography>

                          {/* 주간 스케줄 설정 */}
                          <Typography variant="subtitle2" sx={{ mb: 1 }}>
                            주간 학습 스케줄
                          </Typography>
                          <Grid container spacing={1} sx={{ mb: 2 }}>
                            {['월', '화', '수', '목', '금', '토', '일'].map((day) => (
                              <Grid item xs={3} sm={2} key={day}>
                                <FormControlLabel
                                  control={
                                    <Checkbox
                                      checked={weeklySchedule[day]?.isActive || false}
                                      onChange={(e) => handleScheduleChange(day, 'isActive', e.target.checked)}
                                    />
                                  }
                                  label={day}
                                />
                              </Grid>
                            ))}
                          </Grid>

                          {/* 1일 학습량 설정 */}
                                                  <TextField
                          fullWidth
                          label="1일 학습량"
                          value={dailyAmount}
                          onChange={(e) => handleScheduleChange('dailyAmount', 'value', e.target.value)}
                          placeholder={
                            task.area === 'phrase' 
                              ? "예: 3문제" 
                              : "예: 5문제"
                          }
                          sx={{ mb: 2 }}
                        />

                          <Button
                            variant="contained"
                            fullWidth
                            onClick={() => handleAssignTask(task.id, task.title, task.area, weeklySchedule)}
                          >
                            과제 배정
                          </Button>
                        </CardContent>
                      </Card>
                    </Grid>
                  ))}
              </Grid>
            )}

            {/* 단어 영역일 때 선택된 단어장 배정 */}
            {selectedArea === 'vocabulary' && selectedVocabularyTask && (
              <Box sx={{ mt: 3 }}>
                <Typography variant="h6" sx={{ mb: 2 }}>
                  {selectedVocabularyTask.title} - 배정 설정
                </Typography>
                
                {/* 주간 스케줄 설정 */}
                <Typography variant="subtitle2" sx={{ mb: 1 }}>
                  주간 학습 스케줄
                </Typography>
                <Grid container spacing={1} sx={{ mb: 2 }}>
                  {['월', '화', '수', '목', '금', '토', '일'].map((day) => (
                    <Grid item xs={3} sm={2} key={day}>
                      <FormControlLabel
                        control={
                          <Checkbox
                            checked={weeklySchedule[day]?.isActive || false}
                            onChange={(e) => handleScheduleChange(day, 'isActive', e.target.checked)}
                          />
                        }
                        label={day}
                      />
                    </Grid>
                  ))}
                </Grid>

                {/* 1일 단원 수 설정 */}
                <TextField
                  fullWidth
                  label="1일 단원 수"
                  value={dailyAmount}
                  onChange={(e) => handleScheduleChange('dailyAmount', 'value', e.target.value)}
                  placeholder="예: 2단원"
                  sx={{ mb: 2 }}
                />

                {/* 예상 완료 기간 계산 */}
                {dailyAmount && vocabularyAnalysis && (
                  <Box sx={{ mb: 2, p: 2, bgcolor: 'info.50', borderRadius: 1 }}>
                    <Typography variant="subtitle2" color="info.main">
                      예상 완료 기간
                    </Typography>
                    <Typography variant="body2">
                      • 총 {vocabularyAnalysis.totalUnits}단원 ÷ {dailyAmount}단원/일 = 
                      {Math.ceil(vocabularyAnalysis.totalUnits / parseInt(dailyAmount))}일
                    </Typography>
                    <Typography variant="body2">
                      • 선택된 요일 수: {getActiveDaysCount()}일
                    </Typography>
                  </Box>
                )}

                <Button
                  variant="contained"
                  fullWidth
                  onClick={() => handleAssignTask(selectedVocabularyTask.id, selectedVocabularyTask.title, selectedVocabularyTask.area, weeklySchedule)}
                >
                  단어장 배정
                </Button>
              </Box>
            )}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseAssignmentDialog}>닫기</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default StudentManagement;
