import React, { useState } from 'react';
import {
  Box,
  Button,
  Card,
  CardContent,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
  Chip,
} from '@mui/material';
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
} from '@mui/icons-material';

interface Task {
  id: string;
  title: string;
  description: string;
  area: 'vocabulary' | 'phrase' | 'grammar' | 'logic';
  status: 'active' | 'completed' | 'pending';
  assignedStudents: string[];
}

const TaskManagement: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([
    {
      id: '1',
      title: '기본 문법 규칙 학습',
      description: '시제, 조동사, 수동태 등 기본 문법 규칙 학습 및 연습 문제 풀이',
      area: 'grammar',
      status: 'active',
      assignedStudents: ['김철수', '이영희', '박민수'],
    },
    {
      id: '2',
      title: '일상 대화 표현 학습',
      description: '일상 대화에서 사용하는 기본 표현과 역할극 연습',
      area: 'phrase',
      status: 'pending',
      assignedStudents: ['김철수', '이영희'],
    },
    {
      id: '3',
      title: 'TOEIC 핵심 단어 학습',
      description: 'TOEIC 시험에 자주 출제되는 핵심 단어 100개 학습',
      area: 'vocabulary',
      status: 'active',
      assignedStudents: ['박민수'],
    },
    {
      id: '4',
      title: '영어 논리적 사고 훈련',
      description: '영어로 논리적 사고를 표현하는 방법과 구조 학습',
      area: 'logic',
      status: 'pending',
      assignedStudents: [],
    },
  ]);

  const [openDialog, setOpenDialog] = useState(false);
  const [editingTask, setEditingTask] = useState<Task | null>(null);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    area: 'vocabulary' as 'vocabulary' | 'phrase' | 'grammar' | 'logic',
  });

  const handleOpenDialog = (task?: Task) => {
    if (task) {
      setEditingTask(task);
      setFormData({
        title: task.title,
        description: task.description,
        area: task.area,
      });
    } else {
      setEditingTask(null);
      setFormData({
        title: '',
        description: '',
        area: 'vocabulary',
      });
    }
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setEditingTask(null);
    setFormData({
      title: '',
      description: '',
      area: 'vocabulary',
    });
  };

  const handleSubmit = () => {
    if (editingTask) {
      // 편집 모드
      setTasks(tasks.map(task =>
        task.id === editingTask.id
          ? { ...task, ...formData }
          : task
      ));
    } else {
      // 새 과제 추가
      const newTask: Task = {
        id: Date.now().toString(),
        ...formData,
        status: 'pending',
        assignedStudents: [],
      };
      setTasks([...tasks, newTask]);
    }
    handleCloseDialog();
  };

  const handleDeleteTask = (taskId: string) => {
    setTasks(tasks.filter(task => task.id !== taskId));
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active':
        return 'primary';
      case 'completed':
        return 'success';
      case 'pending':
        return 'warning';
      default:
        return 'default';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'active':
        return '진행중';
      case 'completed':
        return '완료';
      case 'pending':
        return '대기중';
      default:
        return status;
    }
  };

  const getAreaColor = (area: string) => {
    switch (area) {
      case 'vocabulary':
        return 'primary';
      case 'phrase':
        return 'secondary';
      case 'grammar':
        return 'success';
      case 'logic':
        return 'warning';
      default:
        return 'default';
    }
  };

  const getAreaText = (area: string) => {
    switch (area) {
      case 'vocabulary':
        return '단어';
      case 'phrase':
        return '구문';
      case 'grammar':
        return '어법';
      case 'logic':
        return '논리';
      default:
        return area;
    }
  };

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" component="h1">
          과제 관리
        </Typography>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={() => handleOpenDialog()}
        >
          새 과제 추가
        </Button>
      </Box>

      <Card>
        <CardContent>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>과제명</TableCell>
                  <TableCell>설명</TableCell>
                  <TableCell>영역</TableCell>
                  <TableCell>상태</TableCell>
                  <TableCell>배정된 학생</TableCell>
                  <TableCell>작업</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {tasks.map((task) => (
                  <TableRow key={task.id}>
                    <TableCell>{task.title}</TableCell>
                    <TableCell>{task.description}</TableCell>
                    <TableCell>{task.dueDate}</TableCell>
                    <TableCell>
                      <Chip
                        label={getStatusText(task.status)}
                        color={getStatusColor(task.status) as any}
                        size="small"
                      />
                    </TableCell>
                    <TableCell>
                      {task.assignedStudents.length > 0 ? (
                        <Box>
                          {task.assignedStudents.slice(0, 2).join(', ')}
                          {task.assignedStudents.length > 2 && ` 외 ${task.assignedStudents.length - 2}명`}
                        </Box>
                      ) : (
                        '배정된 학생 없음'
                      )}
                    </TableCell>
                    <TableCell>
                      <IconButton
                        size="small"
                        onClick={() => handleOpenDialog(task)}
                      >
                        <EditIcon />
                      </IconButton>
                      <IconButton
                        size="small"
                        onClick={() => handleDeleteTask(task.id)}
                      >
                        <DeleteIcon />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </CardContent>
      </Card>

      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
        <DialogTitle>
          {editingTask ? '과제 편집' : '새 과제 추가'}
        </DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 2 }}>
            <TextField
              fullWidth
              label="과제명"
              value={formData.title}
              onChange={(e) => setFormData({ ...formData, title: e.target.value })}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              label="설명"
              multiline
              rows={3}
              value={formData.description}
              onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              label="마감일"
              type="date"
              value={formData.dueDate}
              onChange={(e) => setFormData({ ...formData, dueDate: e.target.value })}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>취소</Button>
          <Button onClick={handleSubmit} variant="contained">
            {editingTask ? '수정' : '추가'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default TaskManagement;
