import React, { useState, useEffect, useRef } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  TextField,
  Button,
  LinearProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Chip,
  IconButton,
  Paper,
} from '@mui/material';
import {
  Close as CloseIcon,
  CheckCircle as CheckCircleIcon,
  Cancel as CancelIcon,
  NavigateNext as NavigateNextIcon,
} from '@mui/icons-material';

interface VocabularyWord {
  unit: string;
  english: string;
  meaning: string;
}

interface EvaluationWord extends VocabularyWord {
  isCorrect?: boolean;
  userInput?: string;
}

interface VocabularyEvaluationProps {
  assignment: any;
  learningHistory: any[];
  onComplete: (results: any) => void;
  onClose: () => void;
}

const VocabularyEvaluation: React.FC<VocabularyEvaluationProps> = ({
  assignment,
  learningHistory,
  onComplete,
  onClose,
}) => {
  const [evaluationWords, setEvaluationWords] = useState<EvaluationWord[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [userInput, setUserInput] = useState('');
  const [showResult, setShowResult] = useState(false);
  const [isCorrect, setIsCorrect] = useState(false);
  const [autoCompletedText, setAutoCompletedText] = useState('');
  const inputRef = useRef<HTMLInputElement>(null);

  // 평가 대상 단어 추출 (최초 학습의 1차 라운드에서 어려웠던 단어들)
  useEffect(() => {
    console.log('VocabularyEvaluation - learningHistory:', learningHistory);
    
    if (!learningHistory || learningHistory.length === 0) {
      console.error('No learning history found');
      return;
    }

    // 최초 학습 (1회차) 찾기
    const firstLearning = learningHistory.find(history => 
      history.isFirstLearning === true || history.learningSession === 1
    );
    
    console.log('First learning found:', firstLearning);
    
    if (!firstLearning) {
      console.log('No first learning session found, using oldest');
      // 하위 호환성을 위해 가장 오래된 학습 기록 사용
      const oldestLearning = learningHistory[0];
      console.log('Oldest learning:', oldestLearning);
      
      const firstRound = oldestLearning.rounds?.[0];
      console.log('First round:', firstRound);
      
      if (!firstRound) {
        console.error('No first round data found');
        return;
      }
      
      // wordStates가 없으면 words 사용 (하위 호환성)
      if (!firstRound.wordStates && firstRound.words) {
        console.log('Using legacy words format');
        const difficultWords = firstRound.words
          .filter((word: any) => 
            word.status === 'repeat' || 
            word.status === 'unknown' || 
            word.status === 'forgot'
          )
          .map((word: any) => ({
            unit: word.unit,
            english: word.english,
            meaning: word.meaning,
          }));
        
        console.log(`Found ${difficultWords.length} difficult words (legacy)`);
        setEvaluationWords(difficultWords);
        return;
      }
      
      extractDifficultWords(firstRound);
      return;
    }

    // 최초 학습의 첫 번째 라운드 데이터 가져오기
    const firstRound = firstLearning.rounds?.[0];
    console.log('First round from first learning:', firstRound);
    
    if (!firstRound) {
      console.error('No first round data found in first learning session');
      return;
    }
    
    // wordStates가 없으면 words 사용 (하위 호환성)
    if (!firstRound.wordStates && firstRound.words) {
      console.log('Using legacy words format in first learning');
      const difficultWords = firstRound.words
        .filter((word: any) => 
          word.status === 'repeat' || 
          word.status === 'unknown' || 
          word.status === 'forgot'
        )
        .map((word: any) => ({
          unit: word.unit,
          english: word.english,
          meaning: word.meaning,
        }));
      
      console.log(`Found ${difficultWords.length} difficult words (legacy)`);
      setEvaluationWords(difficultWords);
      return;
    }

    extractDifficultWords(firstRound);
  }, [learningHistory]);

  // 어려웠던 단어 추출 함수
  const extractDifficultWords = (firstRound: any) => {
    console.log('extractDifficultWords - firstRound:', firstRound);
    
    // wordStates가 있는 경우 (새 형식)
    if (firstRound.wordStates) {
      const difficultWords = firstRound.wordStates
        .filter((wordState: any) => 
          wordState.status === 'repeat' || 
          wordState.status === 'unknown' || 
          wordState.status === 'forgot'
        )
        .map((wordState: any) => ({
          unit: wordState.word.unit,
          english: wordState.word.english,
          meaning: wordState.word.meaning,
        }));

      console.log(`Found ${difficultWords.length} difficult words from wordStates`);
      setEvaluationWords(difficultWords);
    } 
    // words가 있는 경우 (레거시 형식)
    else if (firstRound.words) {
      const difficultWords = firstRound.words
        .filter((word: any) => 
          word.status === 'repeat' || 
          word.status === 'unknown' || 
          word.status === 'forgot'
        )
        .map((word: any) => ({
          unit: word.unit,
          english: word.english,
          meaning: word.meaning,
        }));

      console.log(`Found ${difficultWords.length} difficult words from words`);
      setEvaluationWords(difficultWords);
    }
    // 둘 다 없는 경우
    else {
      console.error('No word data found in first round');
      setEvaluationWords([]);
    }
  };

  // 첫 글자 입력 시 자동완성 처리
  useEffect(() => {
    if (!userInput || currentIndex >= evaluationWords.length) return;

    const currentWord = evaluationWords[currentIndex];
    const meanings = currentWord.meaning.split(',').map(m => m.trim());
    
    // 각 뜻에 대해 첫 글자 매칭 확인
    for (const meaning of meanings) {
      // 특수 패턴 처리 (A에게 B를 제공하다, ~라고 생각한다 등)
      const patterns = [
        /A에게 B를 (.+)$/,
        /~라고 (.+)$/,
        /~을 (.+)$/,
        /~를 (.+)$/,
        /~에 (.+)$/,
        /~에게 (.+)$/,
      ];

      let targetWord = meaning;
      let prefix = '';
      let suffix = '';

      // 패턴 매칭
      for (const pattern of patterns) {
        const match = meaning.match(pattern);
        if (match) {
          targetWord = match[1];
          prefix = meaning.substring(0, match.index! + meaning.indexOf(match[1]));
          suffix = '';
          break;
        }
      }

      // 일반적인 경우 (패턴이 없는 경우)
      if (prefix === '' && targetWord === meaning) {
        // 첫 글자가 일치하는지 확인
        if (targetWord.startsWith(userInput)) {
          setAutoCompletedText(targetWord);
          setIsCorrect(true);
          return;
        }
      } else {
        // 패턴이 있는 경우
        if (targetWord.startsWith(userInput)) {
          setAutoCompletedText(meaning);
          setIsCorrect(true);
          return;
        }
      }
    }

    // 일치하는 첫 글자가 없으면
    setAutoCompletedText('');
    setIsCorrect(false);
  }, [userInput, currentIndex, evaluationWords]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    if (value.length <= 1) { // 첫 글자만 입력 가능
      setUserInput(value);
    }
  };

  const handleNext = () => {
    if (currentIndex >= evaluationWords.length) return;

    // 현재 단어 결과 저장
    const updatedWords = [...evaluationWords];
    updatedWords[currentIndex] = {
      ...updatedWords[currentIndex],
      isCorrect: isCorrect,
      userInput: userInput,
    };
    setEvaluationWords(updatedWords);

    // 다음 단어로
    if (currentIndex < evaluationWords.length - 1) {
      setCurrentIndex(currentIndex + 1);
      setUserInput('');
      setAutoCompletedText('');
      setIsCorrect(false);
      setShowResult(false);
      inputRef.current?.focus();
    } else {
      // 평가 완료
      handleEvaluationComplete();
    }
  };

  const handleSkip = () => {
    // 건너뛰기는 오답 처리
    setIsCorrect(false);
    handleNext();
  };

  const handleEvaluationComplete = () => {
    const results = {
      totalWords: evaluationWords.length,
      correctWords: evaluationWords.filter(w => w.isCorrect).length,
      incorrectWords: evaluationWords.filter(w => !w.isCorrect),
      evaluationLevel: 1, // 1차 평가
      timestamp: new Date().toISOString(),
    };

    setShowResult(true);
    setTimeout(() => {
      onComplete(results);
    }, 2000);
  };

  if (evaluationWords.length === 0) {
    return (
      <Dialog open={true} onClose={onClose} maxWidth="sm" fullWidth>
        <DialogTitle>평가 준비</DialogTitle>
        <DialogContent>
          <Box sx={{ py: 2, textAlign: 'center' }}>
            <Typography variant="body1" sx={{ mb: 2 }}>
              평가할 단어가 없습니다.
            </Typography>
            <Typography variant="body2" color="text.secondary">
              1차 학습에서 "1번 더 볼래요", "모르겠어요", "아 몰랐어요"로 
              표시한 단어가 있어야 평가를 진행할 수 있습니다.
            </Typography>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={onClose}>닫기</Button>
        </DialogActions>
      </Dialog>
    );
  }

  const currentWord = evaluationWords[currentIndex];
  const progress = ((currentIndex + 1) / evaluationWords.length) * 100;

  return (
    <Dialog
      open={true}
      onClose={onClose}
      maxWidth="md"
      fullWidth
      PaperProps={{
        sx: { height: '80vh', display: 'flex', flexDirection: 'column' }
      }}
    >
      <DialogTitle sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <Typography variant="h6">1차 평가</Typography>
          <Chip
            label={`${currentIndex + 1} / ${evaluationWords.length}`}
            color="primary"
            size="small"
          />
        </Box>
        <IconButton onClick={onClose}>
          <CloseIcon />
        </IconButton>
      </DialogTitle>

      <DialogContent sx={{ flex: 1, display: 'flex', flexDirection: 'column', gap: 3 }}>
        {/* 진행률 표시 */}
        <Box>
          <LinearProgress
            variant="determinate"
            value={progress}
            sx={{ height: 8, borderRadius: 4 }}
          />
        </Box>

        {/* 단어 카드 */}
        <Card sx={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <CardContent sx={{ textAlign: 'center', width: '100%', maxWidth: 600 }}>
            {/* 영어 단어 */}
            <Typography variant="h3" sx={{ mb: 4, fontWeight: 'bold' }}>
              {currentWord?.english}
            </Typography>

            {/* 입력 필드 */}
            <Box sx={{ mb: 4 }}>
              <TextField
                ref={inputRef}
                value={userInput}
                onChange={handleInputChange}
                placeholder="뜻의 첫 글자를 입력하세요"
                variant="outlined"
                size="medium"
                autoFocus
                sx={{
                  width: '100%',
                  maxWidth: 400,
                  '& .MuiOutlinedInput-root': {
                    fontSize: '1.5rem',
                  }
                }}
              />
            </Box>

            {/* 자동완성 결과 */}
            {autoCompletedText && (
              <Paper
                elevation={3}
                sx={{
                  p: 3,
                  bgcolor: isCorrect ? 'success.50' : 'error.50',
                  border: 2,
                  borderColor: isCorrect ? 'success.main' : 'error.main',
                  borderRadius: 2,
                }}
              >
                <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 2 }}>
                  {isCorrect ? (
                    <CheckCircleIcon color="success" sx={{ fontSize: 40 }} />
                  ) : (
                    <CancelIcon color="error" sx={{ fontSize: 40 }} />
                  )}
                  <Typography variant="h5">
                    {autoCompletedText}
                  </Typography>
                </Box>
              </Paper>
            )}

            {/* 원래 뜻 (디버깅용 - 나중에 제거) */}
            <Typography variant="caption" color="text.secondary" sx={{ mt: 2, display: 'block' }}>
              정답: {currentWord?.meaning}
            </Typography>
          </CardContent>
        </Card>

        {/* 액션 버튼 */}
        <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center' }}>
          <Button
            variant="outlined"
            size="large"
            onClick={handleSkip}
            disabled={showResult}
          >
            건너뛰기 (오답)
          </Button>
          <Button
            variant="contained"
            size="large"
            onClick={handleNext}
            disabled={!userInput && !showResult}
            endIcon={<NavigateNextIcon />}
          >
            {currentIndex === evaluationWords.length - 1 ? '평가 완료' : '다음 단어'}
          </Button>
        </Box>
      </DialogContent>

      {/* 결과 다이얼로그 */}
      <Dialog open={showResult} maxWidth="sm" fullWidth>
        <DialogTitle>1차 평가 결과</DialogTitle>
        <DialogContent>
          <Box sx={{ textAlign: 'center', py: 3 }}>
            <Typography variant="h4" sx={{ mb: 2 }}>
              {evaluationWords.filter(w => w.isCorrect).length} / {evaluationWords.length}
            </Typography>
            <Typography variant="h6" color="primary">
              정답률: {Math.round((evaluationWords.filter(w => w.isCorrect).length / evaluationWords.length) * 100)}%
            </Typography>
            {evaluationWords.filter(w => !w.isCorrect).length > 0 && (
              <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
                오답 {evaluationWords.filter(w => !w.isCorrect).length}개는 2차 평가에서 다시 확인합니다.
              </Typography>
            )}
          </Box>
        </DialogContent>
      </Dialog>
    </Dialog>
  );
};

export default VocabularyEvaluation;
