import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Chip,
  LinearProgress,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  Avatar,
  useTheme,
  useMediaQuery,
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  AppBar,
  Toolbar,
  Container,
  Tabs,
  Tab,
  Slider,
  ToggleButton,
  ToggleButtonGroup,
} from '@mui/material';
import {
  Assignment as AssignmentIcon,
  CheckCircle as CheckCircleIcon,
  ArrowBack as ArrowBackIcon,
  Info as InfoIcon,
  CalendarToday as CalendarIcon,
  TrendingUp as TrendingUpIcon,
  Close as CloseIcon,
  Refresh as RefreshIcon,
  Today as TodayIcon,
  ViewModule as ViewModuleIcon,
  PlayArrow as PlayArrowIcon,
  Settings as SettingsIcon,
  Timer as TimerIcon,
  DateRange as DateRangeIcon,
  CalendarMonth as CalendarMonthIcon,
  NavigateBefore as NavigateBeforeIcon,
  NavigateNext as NavigateNextIcon,
} from '@mui/icons-material';
import { useParams, useNavigate } from 'react-router-dom';
import { loadStudents, saveStudents, loadTasks } from '../utils/storage';
import VocabularyLearning from '../components/VocabularyLearning';
import VocabularyEvaluation from '../components/VocabularyEvaluation';

interface TaskAssignment {
  taskId: string;
  taskTitle: string;
  area: 'vocabulary' | 'phrase' | 'grammar' | 'logic';
  weeklySchedule: {
    [day: string]: {
      isActive: boolean;
      dailyAmount: string;
    };
  };
  startDate: string;
  endDate?: string;
  status: 'active' | 'completed' | 'paused';
  progress: {
    completed: number;
    total: number;
  };
  startUnit?: string;
  vocabularyData?: Array<{ unit: string; english: string; meaning: string }>;
  targetDate?: string; // 학습이나 평가를 수행하는 대상 날짜
  targetUnit?: string; // 특정 단원을 지정하여 학습할 때 사용
}

interface Student {
  id: string;
  name: string;
  address: string;
  phone: string;
  grade: '초6' | '중1' | '중2' | '중3' | '고1' | '고2' | '고3';
  joinDate: string;
  status: 'active' | 'inactive';
  assignedTasks: string[];
  taskAssignments: TaskAssignment[];
  grades: {
    [year: string]: {
      mockExam3?: string;
      mockExam6?: string;
      mockExam9?: string;
      mockExam11?: string;
      midterm1?: string;
      final1?: string;
      midterm2?: string;
      final2?: string;
    };
  };
  learningHistory?: Array<{
    date: string;
    taskId: string;
    taskTitle: string;
    learningSession: number; // 학습 회차 (1, 2, 3...)
    isFirstLearning: boolean; // 최초 학습 여부
    rounds: any[];
    summary: {
      totalRounds: number;
      totalWords: number;
      finalCompletedWords: number;
      completionRate: number;
    };
  }>;
  evaluationHistory?: Array<{
    date: string;
    taskId: string;
    taskTitle: string;
    evaluationLevel: number;
    results: any;
  }>;
}

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ pt: 2 }}>{children}</Box>}
    </div>
  );
}

const StudentPage: React.FC = () => {
  const { studentId } = useParams<{ studentId: string }>();
  const navigate = useNavigate();
  const [student, setStudent] = useState<Student | null>(null);
  const [selectedAssignment, setSelectedAssignment] = useState<TaskAssignment | null>(null);
  const [openDetailDialog, setOpenDetailDialog] = useState(false);
  const [openMobileDetail, setOpenMobileDetail] = useState(false);
  const [tabValue, setTabValue] = useState(0);
  const [showVocabularyLearning, setShowVocabularyLearning] = useState(false);
  const [showVocabularyEvaluation, setShowVocabularyEvaluation] = useState(false);
  const [showEvaluationSelectDialog, setShowEvaluationSelectDialog] = useState(false);
  const [evaluationLevel, setEvaluationLevel] = useState<1 | 2>(1);
  const [selectedArea, setSelectedArea] = useState<string>('all');
  const [timerDuration, setTimerDuration] = useState(() => {
    // 로컬 스토리지에서 저장된 타이머 시간 불러오기
    const saved = localStorage.getItem(`timer_duration_${studentId}`);
    return saved ? parseFloat(saved) : 1.5;
  });
  const [viewMode, setViewMode] = useState<'today' | 'week' | 'month'>('today');
  const [weekOffset, setWeekOffset] = useState(0); // 0 = 이번 주, -1 = 지난 주, 1 = 다음 주
  const [monthOffset, setMonthOffset] = useState(0); // 0 = 이번 달, -1 = 지난 달, 1 = 다음 달
  
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  useEffect(() => {
    if (studentId) {
      const students = loadStudents();
      const foundStudent = students.find(s => s.id === studentId);
      if (foundStudent) {
        setStudent(foundStudent);
      } else {
        // 학생을 찾을 수 없는 경우 관리자 페이지로 리다이렉트
        navigate('/students');
      }
    }
  }, [studentId, navigate]);

  // viewMode가 변경될 때 selectedArea를 리셋
  useEffect(() => {
    setSelectedArea('all');
  }, [viewMode]);

  // 학생 정보 변경 모니터링
  useEffect(() => {
    if (student) {
      console.log('Student data updated:', {
        name: student.name,
        taskAssignments: student.taskAssignments.map(a => ({ 
          taskId: a.taskId, 
          title: a.taskTitle 
        })),
        learningHistoryCount: student.learningHistory?.length || 0
      });
    }
  }, [student]);

  const getAreaText = (area: string) => {
    switch (area) {
      case 'vocabulary': return '단어';
      case 'phrase': return '구문';
      case 'grammar': return '어법';
      case 'logic': return '논리';
      default: return area;
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'active': return '진행중';
      case 'completed': return '완료';
      case 'paused': return '일시정지';
      default: return status;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'success';
      case 'completed': return 'primary';
      case 'paused': return 'warning';
      default: return 'default';
    }
  };

  const getGradeText = (grade: string) => {
    return grade;
  };

  const getGradeColor = (grade: string) => {
    switch (grade) {
      case '초6':
        return 'warning';
      case '중1':
      case '중2':
      case '중3':
        return 'primary';
      case '고1':
      case '고2':
      case '고3':
        return 'success';
      default:
        return 'default';
    }
  };

  const handleOpenDetailDialog = (assignment: TaskAssignment) => {
    console.log('Opening detail dialog for assignment:', assignment.taskTitle, 'taskId:', assignment.taskId);
    setSelectedAssignment(assignment);
    if (isMobile) {
      setOpenMobileDetail(true);
    } else {
      setOpenDetailDialog(true);
    }
  };

  const handleCloseDetailDialog = () => {
    setOpenDetailDialog(false);
    setOpenMobileDetail(false);
    setSelectedAssignment(null);
  };

  const handleStartVocabularyLearning = (assignment: TaskAssignment & { targetUnit?: string }, targetDate?: Date) => {
    console.log('Starting vocabulary learning for assignment:', assignment);
    console.log('Assignment taskId:', assignment.taskId);
    console.log('Assignment title:', assignment.taskTitle);
    console.log('Target date:', targetDate);
    
    // targetDate가 있으면 assignment에 추가
    const assignmentWithDate = targetDate 
      ? { ...assignment, targetDate: targetDate.toISOString() }
      : assignment;
    
    console.log('Assignment with date:', assignmentWithDate);
    
    setSelectedAssignment(assignmentWithDate);
    // 타이머 설정 다이얼로그를 건너뛰고 바로 학습 시작
    setShowVocabularyLearning(true);
  };

  const handleStartEvaluation = (assignment: TaskAssignment, targetDate?: Date) => {
    // 해당 과제의 학습 기록 확인
    const taskLearningHistory = student?.learningHistory?.filter(
      history => history.taskId === assignment.taskId
    ) || [];
    
    if (taskLearningHistory.length === 0) {
      alert('먼저 학습을 완료해주세요. 평가는 학습 완료 후에 가능합니다.');
      return;
    }
    
    // targetDate가 있으면 assignment에 추가
    const assignmentWithDate = targetDate 
      ? { ...assignment, targetDate: targetDate.toISOString() }
      : assignment;
    
    setSelectedAssignment(assignmentWithDate);
    
    // 1차 평가 기록이 있는지 확인
    const firstEvaluation = student?.evaluationHistory?.find(
      e => e.taskId === assignment.taskId && e.evaluationLevel === 1
    );
    
    if (!firstEvaluation) {
      // 1차 평가가 없으면 무조건 1차 평가 시작
      setEvaluationLevel(1);
      setShowVocabularyEvaluation(true);
    } else if (firstEvaluation.results.incorrectWords?.length > 0) {
      // 1차 평가가 있고 오답이 있으면 평가 선택 다이얼로그 표시
      setShowEvaluationSelectDialog(true);
    } else {
      // 1차 평가에서 모두 정답인 경우
      alert('1차 평가에서 모든 문제를 맞추셨습니다. 추가 평가가 필요하지 않습니다.');
    }
  };

  const handleCompleteVocabularyLearning = (results: any[]) => {
    // 학습 완료 처리 로직
    console.log('Vocabulary learning completed with results:', results);
    console.log('Selected assignment:', selectedAssignment);
    
    if (!student || !selectedAssignment) return;
    
    // targetDate가 있으면 해당 날짜 사용, 없으면 현재 날짜 사용
    const learningDate = selectedAssignment.targetDate 
      ? new Date(selectedAssignment.targetDate) 
      : new Date();
    
    console.log('Learning date:', learningDate);
    console.log('Selected assignment taskId:', selectedAssignment.taskId);
    
    // 학습 결과 요약
    const totalRounds = results.length;
    const finalResult = results[results.length - 1];
    const totalWords = results[0].totalWords;
    
    // actualFinalCompletedWords가 있으면 사용, 없으면 기존 방식으로 계산
    let finalCompletedWords;
    if (finalResult.actualFinalCompletedWords !== undefined) {
      finalCompletedWords = finalResult.actualFinalCompletedWords;
    } else {
      // 1회차에서 완료한 단어 수
      const firstRoundCompleted = results.length > 1 
        ? totalWords - results[1].totalWords 
        : results[0].completedWords;
      
      // 마지막 회차에서 완료한 단어 수
      const lastRoundCompleted = results.length > 1
        ? finalResult.completedWords
        : 0;
      
      finalCompletedWords = firstRoundCompleted + lastRoundCompleted;
    }
    
    // 학생 정보 업데이트
    const updatedStudent = { ...student };
    
    // 학습 기록 배열이 없으면 생성
    if (!updatedStudent.learningHistory) {
      updatedStudent.learningHistory = [];
    }
    
    // 해당 과제의 기존 학습 횟수 계산
    const previousLearningCount = updatedStudent.learningHistory.filter(
      history => history.taskId === selectedAssignment.taskId
    ).length;
    
    console.log('Previous learning count for taskId', selectedAssignment.taskId, ':', previousLearningCount);
    
    // 학습 기록 저장 (학습 회차 추가)
    const learningRecord = {
      date: learningDate.toISOString(), // targetDate 사용
      taskId: selectedAssignment.taskId,
      taskTitle: selectedAssignment.taskTitle,
      learningSession: previousLearningCount + 1, // 학습 회차 (1회차, 2회차...)
      isFirstLearning: previousLearningCount === 0, // 최초 학습 여부
      rounds: results,
      summary: {
        totalRounds,
        totalWords,
        finalCompletedWords,
        completionRate: Math.round((finalCompletedWords / totalWords) * 100),
      }
    };
    
    console.log('New learning record:', learningRecord);
    console.log('Learning record date:', learningRecord.date);
    console.log('Learning record date (local):', new Date(learningRecord.date).toLocaleString());
    
    // 학습 기록 추가
    updatedStudent.learningHistory.push(learningRecord);
    
    console.log('Updated learning history:', updatedStudent.learningHistory);
    console.log('Learning history for all tasks:', updatedStudent.learningHistory.map(h => ({ taskId: h.taskId, date: h.date })));
    
    // 과제 진행률 업데이트
    const assignmentIndex = updatedStudent.taskAssignments.findIndex(
      a => a.taskId === selectedAssignment.taskId
    );
    
    if (assignmentIndex !== -1) {
      // 완료된 단어 수만큼 진행률 증가
      updatedStudent.taskAssignments[assignmentIndex].progress.completed += finalCompletedWords;
      
      // 진행률이 전체를 초과하지 않도록 조정
      const totalProgress = updatedStudent.taskAssignments[assignmentIndex].progress.total;
      if (updatedStudent.taskAssignments[assignmentIndex].progress.completed > totalProgress) {
        updatedStudent.taskAssignments[assignmentIndex].progress.completed = totalProgress;
      }
    }
    
    // 학생 정보 저장
    const students = loadStudents();
    const studentIndex = students.findIndex(s => s.id === student.id);
    if (studentIndex !== -1) {
      students[studentIndex] = updatedStudent;
      saveStudents(students);
      setStudent(updatedStudent);
    }
    
    // 결과 알림
    const sessionText = learningRecord.learningSession === 1 ? '최초 학습' : `${learningRecord.learningSession}회차 학습`;
    alert(`${sessionText}이 완료되었습니다!\n\n총 ${totalRounds}회차 학습\n전체 ${totalWords}개 단어 중 ${finalCompletedWords}개 완료 (${learningRecord.summary.completionRate}%)`);
    
    setShowVocabularyLearning(false);
    setSelectedAssignment(null);
  };

  const handleCloseVocabularyLearning = () => {
    setShowVocabularyLearning(false);
    setSelectedAssignment(null);
  };

  const handleCompleteVocabularyEvaluation = (results: any) => {
    console.log('평가 완료:', results);
    
    if (!student || !selectedAssignment) return;
    
    // targetDate가 있으면 해당 날짜 사용, 없으면 현재 날짜 사용
    const evaluationDate = selectedAssignment.targetDate 
      ? new Date(selectedAssignment.targetDate) 
      : new Date();
    
    // 평가 결과를 학생 정보에 저장
    const evaluationRecord = {
      date: evaluationDate.toISOString(), // targetDate 사용
      taskId: selectedAssignment.taskId,
      taskTitle: selectedAssignment.taskTitle,
      evaluationLevel: results.evaluationLevel,
      results: results,
    };
    
    // 학생 정보 업데이트
    const updatedStudent = { ...student };
    
    // 평가 기록 배열이 없으면 생성
    if (!updatedStudent.evaluationHistory) {
      updatedStudent.evaluationHistory = [];
    }
    
    // 평가 기록 추가
    updatedStudent.evaluationHistory.push(evaluationRecord);
    
    // 학생 정보 저장
    const students = loadStudents();
    const studentIndex = students.findIndex(s => s.id === student.id);
    if (studentIndex !== -1) {
      students[studentIndex] = updatedStudent;
      saveStudents(students);
      setStudent(updatedStudent);
    }
    
    // 결과 알림
    alert(`1차 평가가 완료되었습니다!\n\n정답: ${results.correctWords}개\n오답: ${results.incorrectWords.length}개\n정답률: ${Math.round((results.correctWords / results.totalWords) * 100)}%`);
    
    setShowVocabularyEvaluation(false);
    setSelectedAssignment(null);
  };

  const handleCloseVocabularyEvaluation = () => {
    setShowVocabularyEvaluation(false);
    setSelectedAssignment(null);
  };

  const handleRestartLearning = () => {
    // 기존 과제 배정 정보에서 startUnit 확인
    const existingAssignment = student?.taskAssignments?.find(
      a => a.taskId === selectedAssignment?.taskId
    );
    const startUnit = existingAssignment?.startUnit || '';
    
    console.log('Restarting learning with:', {
      selectedAssignment,
      existingAssignment,
      startUnit
    });
    
    // 현재 과제 정보 가져오기
    const tasks = loadTasks();
    const targetTask = tasks.find((t: any) => 
      t.id === selectedAssignment?.taskId || t.title === selectedAssignment?.taskTitle
    );
    
    if (!targetTask) {
      console.error('Target task not found');
      alert('과제를 찾을 수 없습니다.');
      return;
    }
    
    console.log('Found target task:', targetTask);
    console.log('Task vocabularyData:', targetTask.vocabularyData);
    
    // 찾은 과제로 배정 생성
    const assignment: TaskAssignment = {
      taskId: targetTask.id,
      taskTitle: targetTask.title,
      area: targetTask.area,
      weeklySchedule: {
        '월': { isActive: true, dailyAmount: '5' },
        '화': { isActive: true, dailyAmount: '5' },
        '수': { isActive: true, dailyAmount: '5' },
        '목': { isActive: true, dailyAmount: '5' },
        '금': { isActive: true, dailyAmount: '5' },
        '토': { isActive: false, dailyAmount: '0' },
        '일': { isActive: false, dailyAmount: '0' },
      },
      startDate: new Date().toISOString().split('T')[0],
      status: 'active',
      progress: {
        completed: 0,
        total: targetTask.vocabularyData?.length || 0,
      },
      vocabularyData: targetTask.vocabularyData,
      startUnit: startUnit, // 기존 startUnit 유지
    };
    
    console.log('Created new assignment:', assignment);
    console.log('Assignment has vocabularyData:', !!assignment.vocabularyData);
    
    if (student) {
      const updatedStudent = {
        ...student,
        taskAssignments: [assignment],
      };
      
      // 로컬 스토리지에 저장
      const students = loadStudents();
      const updatedStudents = students.map(s => 
        s.id === student.id ? updatedStudent : s
      );
      saveStudents(updatedStudents);
      
      // 상태 업데이트
      setStudent(updatedStudent);
      console.log('Student updated with new assignment');
    }
  };

  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setTabValue(newValue);
  };

  const getTodayAssignments = () => {
    if (!student) return [];
    
    console.log('Getting today assignments for student:', student.name);
    console.log('All task assignments:', student.taskAssignments);
    
    // 오늘 요일 확인
    const today = new Date();
    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
    const todayDayName = dayNames[today.getDay()];
    
    console.log('Today is:', todayDayName);
    
    // 오늘 요일에 활성화된 과제만 필터링
    const todayTasks = student.taskAssignments.filter(assignment => {
      // 활성 상태인지 확인
      if (assignment.status !== 'active') return false;
      
      // 오늘 요일의 스케줄이 활성화되어 있는지 확인
      const todaySchedule = assignment.weeklySchedule?.[todayDayName];
      if (!todaySchedule?.isActive) return false;
      
      // 시작일이 오늘보다 늦으면 제외
      const assignmentStartDate = new Date(assignment.startDate);
      assignmentStartDate.setHours(0, 0, 0, 0);
      today.setHours(0, 0, 0, 0);
      if (assignmentStartDate > today) return false;
      
      // 종료일이 있고 오늘보다 이전이면 제외
      if (assignment.endDate) {
        const assignmentEndDate = new Date(assignment.endDate);
        assignmentEndDate.setHours(0, 0, 0, 0);
        if (assignmentEndDate < today) return false;
      }
      
      // 전체 학습이 완료된 과제는 제외
      if (isAssignmentCompleted(assignment, today)) return false;
      
      console.log('Task included in today:', assignment.taskTitle, 'taskId:', assignment.taskId);
      return true;
    });
    
    console.log('Today tasks count:', todayTasks.length);
    console.log('Today tasks:', todayTasks.map(t => ({ title: t.taskTitle, taskId: t.taskId })));
    
    // 오늘의 과제 뷰에서는 항상 더미 데이터를 반환하여 UI가 표시되도록 함
    if (todayTasks.length === 0) {
      return [{
        taskId: 'dummy',
        taskTitle: 'dummy',
        area: 'vocabulary' as const,
        weeklySchedule: {},
        startDate: '',
        status: 'active' as const,
        progress: { completed: 0, total: 0 },
      }];
    }
    
    return todayTasks;
  };

  const getWeekAssignments = () => {
    if (!student) return [];
    
    // 이번 주에 학습 예정인 모든 과제
    const today = new Date();
    const dayOfWeek = today.getDay();
    const weekStart = new Date(today);
    weekStart.setDate(today.getDate() - dayOfWeek);
    const weekEnd = new Date(weekStart);
    weekEnd.setDate(weekStart.getDate() + 6);
    
    return student.taskAssignments.filter(assignment => {
      if (assignment.status !== 'active') return false;
      
      // 주간 스케줄에서 이번 주에 활성화된 요일이 있는지 확인
      const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
      return Object.entries(assignment.weeklySchedule || {}).some(([day, schedule]) => 
        schedule.isActive
      );
    });
  };

  const getMonthAssignments = () => {
    if (!student) return [];
    
    // 이번 달에 활성화된 모든 과제
    return student.taskAssignments.filter(assignment => 
      assignment.status === 'active'
    );
  };

  const getActiveAssignments = () => {
    if (!student) return [];
    const activeAssignments = student.taskAssignments.filter(assignment => assignment.status === 'active');
    
    // taskId 중복 체크
    const taskIds = activeAssignments.map(a => a.taskId);
    const uniqueTaskIds = new Set(taskIds);
    if (taskIds.length !== uniqueTaskIds.size) {
      console.error('Duplicate taskIds found!', taskIds);
    }
    console.log('Active assignments taskIds:', taskIds);
    
    return activeAssignments;
  };

  const getAssignmentsByViewMode = () => {
    switch (viewMode) {
      case 'week':
        return getWeekAssignments();
      case 'month':
        return getMonthAssignments();
      default:
        return getTodayAssignments();
    }
  };

  const getViewModeTitle = () => {
    switch (viewMode) {
      case 'week':
        return '이번 주 과제';
      case 'month':
        return '이번 달 과제';
      default:
        return '오늘의 과제';
    }
  };

  // 주간 날짜 범위 계산
  const getWeekDateRange = (offset: number = 0) => {
    const today = new Date();
    const currentDay = today.getDay();
    const diff = currentDay === 0 ? -6 : 1 - currentDay; // 월요일 기준
    
    const monday = new Date(today);
    monday.setDate(today.getDate() + diff + (offset * 7));
    
    const sunday = new Date(monday);
    sunday.setDate(monday.getDate() + 6);
    
    return { monday, sunday };
  };

  // 주간 타이틀 생성
  const getWeekTitle = () => {
    if (viewMode !== 'week') return getViewModeTitle();
    
    const { monday, sunday } = getWeekDateRange(weekOffset);
    const formatDate = (date: Date) => `${date.getMonth() + 1}/${date.getDate()}`;
    
    if (weekOffset === 0) {
      return `이번 주 과제 (${formatDate(monday)} - ${formatDate(sunday)})`;
    } else if (weekOffset < 0) {
      return `${Math.abs(weekOffset)}주 전 과제 (${formatDate(monday)} - ${formatDate(sunday)})`;
    } else {
      return `${weekOffset}주 후 과제 (${formatDate(monday)} - ${formatDate(sunday)})`;
    }
  };

  // 월간 날짜 계산
  const getMonthDateRange = (offset: number = 0) => {
    const today = new Date();
    const year = today.getFullYear();
    const month = today.getMonth() + offset;
    
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    
    return { firstDay, lastDay, year: firstDay.getFullYear(), month: firstDay.getMonth() };
  };

  // 월간 캘린더 데이터 생성
  const getMonthCalendarData = () => {
    const { firstDay, lastDay, year, month } = getMonthDateRange(monthOffset);
    const startDayOfWeek = (firstDay.getDay() + 6) % 7; // 0 = 월요일, 6 = 일요일
    const daysInMonth = lastDay.getDate();
    
    const calendar = [];
    let week = [];
    
    // 첫 주의 빈 날짜 채우기
    for (let i = 0; i < startDayOfWeek; i++) {
      week.push(null);
    }
    
    // 날짜 채우기
    for (let day = 1; day <= daysInMonth; day++) {
      week.push(new Date(year, month, day));
      
      if (week.length === 7) {
        calendar.push(week);
        week = [];
      }
    }
    
    // 마지막 주의 빈 날짜 채우기
    if (week.length > 0) {
      while (week.length < 7) {
        week.push(null);
      }
      calendar.push(week);
    }
    
    return calendar;
  };

  // 월간 타이틀 생성
  const getMonthTitle = () => {
    if (viewMode !== 'month') return getViewModeTitle();
    
    const { year, month } = getMonthDateRange(monthOffset);
    const monthNames = ['1월', '2월', '3월', '4월', '5월', '6월', '7월', '8월', '9월', '10월', '11월', '12월'];
    
    if (monthOffset === 0) {
      return `${year}년 ${monthNames[month]} 과제`;
    } else if (monthOffset < 0) {
      return `${year}년 ${monthNames[month]} 과제`;
    } else {
      return `${year}년 ${monthNames[month]} 과제`;
    }
  };

  // 전체 타이틀 생성 (주간/월간 통합)
  const getViewTitle = () => {
    switch (viewMode) {
      case 'week':
        return getWeekTitle();
      case 'month':
        return getMonthTitle();
      default:
        return getViewModeTitle();
    }
  };

  const getChipLabel = (mode: typeof viewMode) => {
    switch (mode) {
      case 'week':
        return '주간 학습';
      case 'month':
        return '월간 학습';
      default:
        return '오늘 학습';
    }
  };

  const getWeeklyGroupedAssignments = () => {
    if (viewMode !== 'week') return null;
    
    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
    const weeklyGroups: { [key: string]: TaskAssignment[] } = {};
    
    // 각 요일별로 빈 배열 초기화
    dayNames.forEach(day => {
      weeklyGroups[day] = [];
    });
    
    // 각 과제를 해당하는 요일에 추가
    viewAssignments.forEach(assignment => {
      dayNames.forEach(day => {
        if (assignment.weeklySchedule?.[day]?.isActive) {
          weeklyGroups[day].push(assignment);
        }
      });
    });
    
    return weeklyGroups;
  };

  // 특정 요일의 학습 가능 여부 판단
  const canStudyOnDay = (dayIndex: number, assignment: TaskAssignment): boolean => {
    if (!assignment.startDate || !assignment.weeklySchedule) return false;
    
    const startDate = new Date(assignment.startDate);
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    startDate.setHours(0, 0, 0, 0);
    
    // 시작일 이전이면 학습 불가
    if (today < startDate) return false;
    
    // 주간 스케줄에서 활성화된 요일만 카운트
    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
    let studyDayCount = 0;
    let targetStudyDay = 0;
    
    // 시작일부터 오늘까지 학습일 수 계산
    const currentDate = new Date(startDate);
    while (currentDate <= today) {
      const currentDayIndex = currentDate.getDay();
      const currentDayName = dayNames[currentDayIndex];
      
      if (assignment.weeklySchedule[currentDayName]?.isActive) {
        studyDayCount++;
        
        // 목표 요일의 학습일 번호 계산
        if (currentDayIndex === dayIndex && currentDate.getTime() === today.getTime()) {
          targetStudyDay = studyDayCount;
        }
      }
      
      currentDate.setDate(currentDate.getDate() + 1);
    }
    
    // 이번 주의 특정 요일이 몇 번째 학습일인지 계산
    const thisWeekStart = new Date(today);
    thisWeekStart.setDate(today.getDate() - today.getDay()); // 이번 주 일요일
    
    let weekStudyDay = 0;
    for (let i = 0; i < 7; i++) {
      const checkDate = new Date(thisWeekStart);
      checkDate.setDate(thisWeekStart.getDate() + i);
      
      if (checkDate >= startDate && assignment.weeklySchedule[dayNames[i]]?.isActive) {
        weekStudyDay++;
        
        if (i === dayIndex) {
          // 해당 요일이 전체 학습 진행에서 몇 번째인지
          const totalStudyDays = Math.floor((checkDate.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24));
          let dayNumber = 0;
          
          for (let j = 0; j <= totalStudyDays; j++) {
            const tempDate = new Date(startDate);
            tempDate.setDate(startDate.getDate() + j);
            const tempDayName = dayNames[tempDate.getDay()];
            
            if (assignment.weeklySchedule[tempDayName]?.isActive) {
              dayNumber++;
            }
          }
          
          // 단원이 아직 남아있는지 확인
          // 이 부분은 실제 단원 수와 비교해야 하지만, 일단 true 반환
          return true;
        }
      }
    }
    
    return false;
  };

  // 특정 날짜에 학습할 단원 계산
  const getUnitForDate = (assignment: TaskAssignment, targetDate: Date): string | null => {
    if (!assignment.startDate || !assignment.weeklySchedule || !assignment.vocabularyData) {
      return null;
    }
    
    const startDate = new Date(assignment.startDate);
    startDate.setHours(0, 0, 0, 0);
    targetDate.setHours(0, 0, 0, 0);
    
    // 시작일 이전이면 null
    if (targetDate < startDate) return null;
    
    // 주간 스케줄에서 활성화된 요일만 카운트
    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
    let studyDayCount = 0;
    
    // 시작일부터 목표일까지 학습일 수 계산
    const currentDate = new Date(startDate);
    while (currentDate <= targetDate) {
      const currentDayName = dayNames[currentDate.getDay()];
      
      if (assignment.weeklySchedule[currentDayName]?.isActive) {
        studyDayCount++;
        
        if (currentDate.getTime() === targetDate.getTime()) {
          // 단원 목록 추출
          const unitOrder: string[] = [];
          const unitSet = new Set<string>();
          
          assignment.vocabularyData.forEach((word: any) => {
            if (!unitSet.has(word.unit)) {
              unitSet.add(word.unit);
              unitOrder.push(word.unit);
            }
          });
          
          // 시작 단원 인덱스 찾기
          const startUnitIndex = assignment.startUnit ? unitOrder.indexOf(assignment.startUnit) : 0;
          
          if (startUnitIndex !== -1) {
            const targetUnitIndex = startUnitIndex + (studyDayCount - 1);
            
            if (targetUnitIndex < unitOrder.length) {
              return unitOrder[targetUnitIndex];
            }
          }
          
          return null;
        }
      }
      
      currentDate.setDate(currentDate.getDate() + 1);
    }
    
    return null;
  };

  // 특정 날짜의 단원 진행 정보 가져오기
  const getUnitProgressForDate = (assignment: TaskAssignment, targetDate: Date): { current: number; total: number; isCompleted: boolean } | null => {
    if (!assignment.startDate || !assignment.weeklySchedule || !assignment.vocabularyData) {
      return null;
    }
    
    const startDate = new Date(assignment.startDate);
    startDate.setHours(0, 0, 0, 0);
    targetDate.setHours(0, 0, 0, 0);
    
    // 시작일 이전이면 null
    if (targetDate < startDate) return null;
    
    // 단원 목록 추출
    const unitOrder: string[] = [];
    const unitSet = new Set<string>();
    
    assignment.vocabularyData.forEach((word: any) => {
      if (!unitSet.has(word.unit)) {
        unitSet.add(word.unit);
        unitOrder.push(word.unit);
      }
    });
    
    const totalUnits = unitOrder.length;
    const startUnitIndex = assignment.startUnit ? unitOrder.indexOf(assignment.startUnit) : 0;
    
    // 주간 스케줄에서 활성화된 요일만 카운트
    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
    let totalUnitsStudied = 0;
    
    // 시작일부터 목표일까지 학습한 총 단원 수 계산
    const currentDate = new Date(startDate);
    while (currentDate <= targetDate) {
      const currentDayName = dayNames[currentDate.getDay()];
      const daySchedule = assignment.weeklySchedule[currentDayName];
      
      if (daySchedule?.isActive) {
        const dailyAmount = Number(daySchedule.dailyAmount) || 0;
        
        if (currentDate.getTime() === targetDate.getTime()) {
          // 목표일의 시작 단원 번호 계산 (1부터 시작)
          const currentStartUnit = startUnitIndex + totalUnitsStudied + 1;
          
          // 전체 단원을 완료했는지 확인
          const isCompleted = currentStartUnit > totalUnits;
          
          return {
            current: Math.min(currentStartUnit, totalUnits + 1),
            total: totalUnits,
            isCompleted
          };
        } else {
          // 이전 날짜들의 학습량 누적
          totalUnitsStudied += dailyAmount;
        }
      }
      
      currentDate.setDate(currentDate.getDate() + 1);
    }
    
    return null;
  };

  // 과제가 이미 완료되었는지 확인
  const isAssignmentCompleted = (assignment: TaskAssignment, targetDate: Date): boolean => {
    if (!assignment.startDate || !assignment.weeklySchedule || !assignment.vocabularyData) {
      return false;
    }
    
    const startDate = new Date(assignment.startDate);
    startDate.setHours(0, 0, 0, 0);
    targetDate.setHours(0, 0, 0, 0);
    
    // 단원 목록 추출
    const unitOrder: string[] = [];
    const unitSet = new Set<string>();
    
    assignment.vocabularyData.forEach((word: any) => {
      if (!unitSet.has(word.unit)) {
        unitSet.add(word.unit);
        unitOrder.push(word.unit);
      }
    });
    
    const totalUnits = unitOrder.length;
    const startUnitIndex = assignment.startUnit ? unitOrder.indexOf(assignment.startUnit) : 0;
    
    // 주간 스케줄에서 활성화된 요일만 카운트
    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
    let studyDayCount = 0;
    let completedDate: Date | null = null;
    
    // 시작일부터 목표일까지 학습일 수 계산
    const currentDate = new Date(startDate);
    while (currentDate <= targetDate) {
      const currentDayName = dayNames[currentDate.getDay()];
      
      if (assignment.weeklySchedule[currentDayName]?.isActive) {
        studyDayCount++;
        const targetUnitIndex = startUnitIndex + (studyDayCount - 1);
        
        // 마지막 단원을 학습한 날짜 찾기
        if (targetUnitIndex === totalUnits - 1) {
          completedDate = new Date(currentDate);
          break;
        }
      }
      
      currentDate.setDate(currentDate.getDate() + 1);
    }
    
    // 완료 날짜가 있고, 목표일이 완료 날짜 이후이면 true
    return completedDate !== null && targetDate > completedDate;
  };

  const getUpcomingAssignments = () => {
    if (!student) return [];
    
    console.log('Getting upcoming assignments...');
    
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    
    const assignments: { date: Date; dayName: string; assignments: (TaskAssignment & { targetUnit?: string })[] }[] = [];
    
    // 오늘부터 3일간의 과제 수집
    for (let i = 0; i < 3; i++) {
      const targetDate = new Date(today);
      targetDate.setDate(today.getDate() + i);
      
      const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
      const dayName = dayNames[targetDate.getDay()];
      
      console.log(`Day ${i}: ${targetDate.toLocaleDateString()} (${dayName})`);
      
      // 해당 날짜에 활성화된 과제 필터링
      const dayAssignments = student.taskAssignments.filter(assignment => {
        // 활성 상태인지 확인
        if (assignment.status !== 'active') return false;
        
        // 해당 요일의 스케줄이 활성화되어 있는지 확인
        const daySchedule = assignment.weeklySchedule?.[dayName];
        if (!daySchedule?.isActive) return false;
        
        // 시작일이 대상 날짜보다 늦으면 제외
        const assignmentStartDate = new Date(assignment.startDate);
        assignmentStartDate.setHours(0, 0, 0, 0);
        if (assignmentStartDate > targetDate) return false;
        
        // 종료일이 있고 대상 날짜보다 이전이면 제외
        if (assignment.endDate) {
          const assignmentEndDate = new Date(assignment.endDate);
          assignmentEndDate.setHours(0, 0, 0, 0);
          if (assignmentEndDate < targetDate) return false;
        }
        
        // 전체 학습이 완료된 과제는 제외
        if (isAssignmentCompleted(assignment, targetDate)) return false;
        
        console.log(`  Including assignment: ${assignment.taskTitle} (taskId: ${assignment.taskId})`);
        return true;
      });
      
      // 각 과제를 단원별로 분리
      const unitSeparatedAssignments: (TaskAssignment & { targetUnit?: string })[] = [];
      
      dayAssignments.forEach(assignment => {
        const daySchedule = assignment.weeklySchedule?.[dayName];
        const dailyAmount = Number(daySchedule?.dailyAmount) || 0;
        
        if (dailyAmount > 0 && assignment.vocabularyData) {
          // 단원 목록 추출
          const unitOrder: string[] = [];
          const unitSet = new Set<string>();
          
          assignment.vocabularyData.forEach((word: any) => {
            if (!unitSet.has(word.unit)) {
              unitSet.add(word.unit);
              unitOrder.push(word.unit);
            }
          });
          
          // 학습 진도 계산
          const progress = getUnitProgressForDate(assignment, targetDate);
          if (progress) {
            // 현재 단원 번호를 인덱스로 변환 (단원 번호는 1부터 시작, 인덱스는 0부터 시작)
            const currentUnitIndex = progress.current - 1;
            const endUnitIndex = Math.min(currentUnitIndex + dailyAmount, unitOrder.length);
            
            // 각 단원별로 별도의 카드 생성
            for (let unitIndex = currentUnitIndex; unitIndex < endUnitIndex; unitIndex++) {
              unitSeparatedAssignments.push({
                ...assignment,
                targetUnit: unitOrder[unitIndex]
              });
            }
          }
        } else {
          // 단원 정보가 없으면 그대로 추가
          unitSeparatedAssignments.push(assignment);
        }
      });
      
      if (unitSeparatedAssignments.length > 0 || i === 0) { // 오늘은 과제가 없어도 표시
        assignments.push({
          date: targetDate,
          dayName: dayName,
          assignments: unitSeparatedAssignments
        });
      }
    }
    
    return assignments;
  };

  if (!student) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Typography variant="h6">학생 정보를 불러오는 중...</Typography>
      </Box>
    );
  }

  const todayAssignments = getTodayAssignments();
  const activeAssignments = getActiveAssignments();
  const viewAssignments = getAssignmentsByViewMode();

  return (
    <Box sx={{ pb: isMobile ? 8 : 0 }}>
      {/* 모바일 앱바 */}
      {isMobile && (
        <AppBar position="sticky" elevation={1}>
          <Toolbar>
            <IconButton
              edge="start"
              color="inherit"
              onClick={() => navigate('/students')}
              sx={{ mr: 2 }}
            >
              <ArrowBackIcon />
            </IconButton>
            <Typography variant="h6" sx={{ flex: 1 }}>
              {student.name}
            </Typography>
            <Chip
              label={getGradeText(student.grade)}
              color={getGradeColor(student.grade) as any}
              size="small"
            />
          </Toolbar>
        </AppBar>
      )}

      <Container maxWidth="lg" sx={{ px: isMobile ? 2 : 3, py: isMobile ? 2 : 3 }}>
        {/* 데스크톱 학생 정보 헤더 */}
        {!isMobile && (
          <Box sx={{ mb: 4 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <Avatar sx={{ mr: 2, width: 64, height: 64 }}>
                {student.name.charAt(0)}
              </Avatar>
              <Box>
                <Typography variant="h4" component="h1">
                  {student.name}
                </Typography>
                <Box sx={{ display: 'flex', gap: 1, mt: 1 }}>
                  <Chip
                    label={getGradeText(student.grade)}
                    color={getGradeColor(student.grade) as any}
                    size="small"
                  />
                  <Chip
                    label={student.status === 'active' ? '활성' : '비활성'}
                    color={student.status === 'active' ? 'success' : 'error'}
                    size="small"
                  />
                </Box>
              </Box>
            </Box>
          </Box>
        )}

        {/* 모바일 학생 정보 카드 */}
        {isMobile && (
          <Card sx={{ mb: 3, borderRadius: 2 }}>
            <CardContent sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <Avatar sx={{ mr: 2, width: 48, height: 48 }}>
                  {student.name.charAt(0)}
                </Avatar>
                <Box sx={{ flex: 1 }}>
                  <Typography variant="h6">
                    {student.name}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {student.address}
                  </Typography>
                </Box>
              </Box>
              
              <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                <Chip
                  label={getGradeText(student.grade)}
                  color={getGradeColor(student.grade) as any}
                  size="small"
                />
                <Chip
                  label={student.status === 'active' ? '활성' : '비활성'}
                  color={student.status === 'active' ? 'success' : 'error'}
                  size="small"
                />
              </Box>

              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Box>
                  <Typography variant="body2" color="text.secondary">
                    연락처: {student.phone}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    가입일: {student.joinDate}
                  </Typography>
                </Box>
                <Box sx={{ textAlign: 'right' }}>
                  <Typography variant="h6" color="primary">
                    {student.taskAssignments.length}
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    배정된 과제
                  </Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        )}

        {/* 탭 네비게이션 */}
        <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
          <Tabs 
            value={tabValue} 
            onChange={handleTabChange}
            variant={isMobile ? "fullWidth" : "standard"}
            sx={{
              '& .MuiTab-root': {
                minHeight: isMobile ? 56 : 48,
                fontSize: isMobile ? '0.875rem' : '1rem',
              }
            }}
          >
            <Tab 
              icon={<TodayIcon />} 
              label="오늘의 과제" 
              iconPosition="start"
              sx={{ 
                '& .MuiTab-iconWrapper': { mr: 1 },
                '& .MuiTab-labelIcon': { flexDirection: 'row' }
              }}
            />
            <Tab 
              icon={<ViewModuleIcon />} 
              label="과제 현황" 
              iconPosition="start"
              sx={{ 
                '& .MuiTab-iconWrapper': { mr: 1 },
                '& .MuiTab-labelIcon': { flexDirection: 'row' }
              }}
            />
            <Tab 
              icon={<SettingsIcon />} 
              label="설정" 
              iconPosition="start"
              sx={{ 
                '& .MuiTab-iconWrapper': { mr: 1 },
                '& .MuiTab-labelIcon': { flexDirection: 'row' }
              }}
            />
          </Tabs>
        </Box>

        {/* 오늘의 과제 탭 */}
        <TabPanel value={tabValue} index={0}>
          <Box sx={{ mb: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2, flexWrap: 'wrap', gap: 2 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, flexWrap: 'wrap' }}>
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <TodayIcon sx={{ mr: 1, color: 'primary.main' }} />
                  <Typography variant={isMobile ? "h6" : "h5"} sx={{ fontWeight: 600 }}>
                    {getViewTitle()} ({viewAssignments.length}개)
                  </Typography>
                </Box>
                {viewMode === 'week' && (
                  <Box sx={{ 
                    display: 'flex', 
                    alignItems: 'center',
                    ml: isMobile ? 0 : 2,
                    mt: isMobile ? 1 : 0
                  }}>
                    <IconButton 
                      onClick={() => setWeekOffset(weekOffset - 1)}
                      size="small"
                      sx={{ border: '1px solid', borderColor: 'divider' }}
                    >
                      <NavigateBeforeIcon />
                    </IconButton>
                    <Button
                      size="small"
                      onClick={() => setWeekOffset(0)}
                      disabled={weekOffset === 0}
                      sx={{ mx: 1, minWidth: 'auto' }}
                    >
                      이번 주
                    </Button>
                    <IconButton 
                      onClick={() => setWeekOffset(weekOffset + 1)}
                      size="small"
                      sx={{ border: '1px solid', borderColor: 'divider' }}
                    >
                      <NavigateNextIcon />
                    </IconButton>
                  </Box>
                )}
                {viewMode === 'month' && (
                  <Box sx={{ 
                    display: 'flex', 
                    alignItems: 'center',
                    ml: isMobile ? 0 : 2,
                    mt: isMobile ? 1 : 0
                  }}>
                    <IconButton 
                      onClick={() => setMonthOffset(monthOffset - 1)}
                      size="small"
                      sx={{ border: '1px solid', borderColor: 'divider' }}
                    >
                      <NavigateBeforeIcon />
                    </IconButton>
                    <Button
                      size="small"
                      onClick={() => setMonthOffset(0)}
                      disabled={monthOffset === 0}
                      sx={{ mx: 1, minWidth: 'auto' }}
                    >
                      이번 달
                    </Button>
                    <IconButton 
                      onClick={() => setMonthOffset(monthOffset + 1)}
                      size="small"
                      sx={{ border: '1px solid', borderColor: 'divider' }}
                    >
                      <NavigateNextIcon />
                    </IconButton>
                  </Box>
                )}
              </Box>
              
              <ToggleButtonGroup
                value={viewMode}
                exclusive
                onChange={(event, newMode) => {
                  if (newMode !== null) {
                    setViewMode(newMode);
                    setWeekOffset(0); // 모드 변경 시 주차 초기화
                    setMonthOffset(0); // 모드 변경 시 월 초기화
                  }
                }}
                size={isMobile ? "small" : "medium"}
              >
                <ToggleButton value="today">
                  <TodayIcon sx={{ mr: 0.5 }} />
                  오늘
                </ToggleButton>
                <ToggleButton value="week">
                  <DateRangeIcon sx={{ mr: 0.5 }} />
                  주간
                </ToggleButton>
                <ToggleButton value="month">
                  <CalendarMonthIcon sx={{ mr: 0.5 }} />
                  월간
                </ToggleButton>
              </ToggleButtonGroup>
            </Box>
            
            {viewAssignments.length > 0 ? (
              viewMode === 'week' ? (
                // 주간 보기: 요일별로 그룹화
                <Box>
                  {(() => {
                    const weeklyGroups = getWeeklyGroupedAssignments();
                    const dayNamesKorean = ['일', '월', '화', '수', '목', '금', '토'];
                    const dayNamesDisplay = ['월', '화', '수', '목', '금', '토', '일'];
                    const today = new Date().getDay();
                    const todayIndex = today === 0 ? 6 : today - 1; // 일요일을 마지막으로
                    
                    // 요일 인덱스를 실제 요일 번호로 변환 (월=1, 화=2, ..., 일=0)
                    const dayIndexToWeekday = (index: number) => {
                      return index === 6 ? 0 : index + 1;
                    };
                    
                    return (
                      <Grid container spacing={2}>
                        {dayNamesDisplay.map((day, index) => {
                          const koreanDay = dayNamesKorean[dayIndexToWeekday(index)];
                          const assignments = weeklyGroups?.[koreanDay] || [];
                          const isToday = index === todayIndex;
                          const weekdayNumber = dayIndexToWeekday(index);
                          
                          // 이번 주의 해당 요일 날짜 계산
                          const thisWeekDate = new Date();
                          const currentDay = thisWeekDate.getDay();
                          const diff = weekdayNumber - currentDay;
                          thisWeekDate.setDate(thisWeekDate.getDate() + diff + (weekOffset * 7));
                          thisWeekDate.setHours(0, 0, 0, 0);
                          
                          const todayDate = new Date();
                          todayDate.setHours(0, 0, 0, 0);
                          
                          // 오늘보다 과거인지 확인
                          const isPast = thisWeekDate < todayDate;
                          
                          // 오늘인지 확인 (weekOffset이 0일 때만)
                          const isRealToday = weekOffset === 0 && isToday;
                          
                          // 완료된 이후의 과제는 필터링
                          const activeAssignments = assignments.filter(assignment => 
                            !isAssignmentCompleted(assignment, thisWeekDate)
                          );
                          
                          return (
                            <Grid item xs={12} sm={6} md key={day}>
                              <Box sx={{ 
                                height: '100%',
                                border: isRealToday ? '2px solid' : '1px solid',
                                borderColor: isRealToday ? 'primary.main' : isPast ? 'grey.400' : 'grey.300',
                                borderRadius: 2,
                                p: 2,
                                bgcolor: isRealToday ? 'primary.50' : isPast ? 'grey.50' : 'background.paper',
                                opacity: isPast ? 0.7 : 1
                              }}>
                                <Box sx={{ 
                                  display: 'flex', 
                                  alignItems: 'center', 
                                  mb: 2,
                                  pb: 1,
                                  borderBottom: '1px solid',
                                  borderColor: 'divider'
                                }}>
                                  <Typography 
                                    variant="subtitle1" 
                                    sx={{ 
                                      fontWeight: isRealToday ? 700 : 600,
                                      color: isRealToday ? 'primary.main' : isPast ? 'text.disabled' : 'text.primary'
                                    }}
                                  >
                                    {day}
                                  </Typography>
                                  {isRealToday && (
                                    <Chip 
                                      label="오늘" 
                                      size="small" 
                                      color="primary" 
                                      sx={{ ml: 1 }}
                                    />
                                  )}
                                  {isPast && (
                                    <Chip 
                                      label="지남" 
                                      size="small" 
                                      color="default" 
                                      sx={{ ml: 1 }}
                                    />
                                  )}
                                  <Typography variant="caption" color="text.secondary" sx={{ ml: 'auto' }}>
                                    {activeAssignments.length}개
                                  </Typography>
                                </Box>
                                
                                <Box sx={{ 
                                  maxHeight: 400, 
                                  overflowY: 'auto',
                                  '&::-webkit-scrollbar': {
                                    width: '8px',
                                  },
                                  '&::-webkit-scrollbar-track': {
                                    background: '#f1f1f1',
                                    borderRadius: '4px',
                                  },
                                  '&::-webkit-scrollbar-thumb': {
                                    background: '#888',
                                    borderRadius: '4px',
                                  },
                                  '&::-webkit-scrollbar-thumb:hover': {
                                    background: '#555',
                                  },
                                }}>
                                  {activeAssignments.length > 0 ? (
                                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 0 }}>
                                      {activeAssignments.map((assignment, idx) => (
                                                                                <Box 
                                          key={`${day}-${idx}`}
                                          sx={{ 
                                            display: 'flex',
                                            alignItems: 'center',
                                            gap: 1,
                                            borderBottom: idx < activeAssignments.length - 1 ? '1px solid' : 'none',
                                            borderColor: 'divider',
                                            py: 0.5,
                                            px: 1,
                                            '&:hover': {
                                              bgcolor: isPast ? 'transparent' : 'action.hover',
                                            }
                                          }}
                                        >
                                            {/* 상태 표시 원 */}
                                            <Box
                                              sx={{
                                                width: 10,
                                                height: 10,
                                                borderRadius: '50%',
                                                border: '1px solid rgba(0,0,0,0.1)',
                                                bgcolor: (() => {
                                                  const todayDate = new Date();
                                                  todayDate.setHours(0, 0, 0, 0);
                                                  
                                                  // 미래 날짜는 노란색 (수행전)
                                                  if (thisWeekDate > todayDate) {
                                                    return '#FFD700'; // 진한 노란색
                                                  }
                                                  
                                                  // 오늘이거나 과거 날짜인 경우
                                                  const taskHistory = student?.learningHistory?.filter(
                                                    h => {
                                                      if (h.taskId !== assignment.taskId) return false;
                                                      const historyDate = new Date(h.date);
                                                      historyDate.setHours(0, 0, 0, 0);
                                                      return historyDate.getTime() === thisWeekDate.getTime();
                                                    }
                                                  ) || [];
                                                  
                                                  // 학습 기록이 있으면 파란색 (수행완료)
                                                  if (taskHistory.length > 0) {
                                                    return '#2196F3'; // 파란색
                                                  }
                                                  
                                                  // 학습 기록이 없으면 빨간색 (미수행)
                                                  return '#F44336'; // 빨간색
                                                })(),
                                                flexShrink: 0
                                              }}
                                            />
                                            
                                            {/* 과제 정보 한 줄 표시 */}
                                            <Typography 
                                              variant="caption" 
                                              sx={{ 
                                                color: isPast ? 'text.disabled' : 'text.primary',
                                                fontSize: '0.75rem',
                                                overflow: 'hidden',
                                                textOverflow: 'ellipsis',
                                                whiteSpace: 'nowrap',
                                                flex: 1
                                              }}
                                            >
                                              {assignment.taskTitle} / {assignment.weeklySchedule?.[koreanDay]?.dailyAmount || '0'}개
                                            </Typography>
                                        </Box>
                                      ))}
                                    </Box>
                                  ) : (
                                    <Box sx={{ 
                                      display: 'flex', 
                                      alignItems: 'center', 
                                      justifyContent: 'center',
                                      height: 100,
                                      color: 'text.secondary'
                                    }}>
                                      <Typography variant="caption">
                                        과제 없음
                                      </Typography>
                                    </Box>
                                  )}
                                </Box>
                              </Box>
                            </Grid>
                          );
                        })}
                      </Grid>
                    );
                  })()}
                </Box>
              ) : viewMode === 'month' ? (
                // 월간 보기: 캘린더 형태
                <Box>
                  <TableContainer component={Paper} sx={{ mb: 2 }}>
                    <Table size="small">
                      <TableHead>
                        <TableRow>
                          {['월', '화', '수', '목', '금', '토', '일'].map((day) => (
                            <TableCell key={day} align="center" sx={{ fontWeight: 600, width: '14.28%' }}>
                              {day}
                            </TableCell>
                          ))}
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {getMonthCalendarData().map((week, weekIndex) => (
                          <TableRow key={weekIndex}>
                            {week.map((date, dayIndex) => {
                              if (!date) {
                                return <TableCell key={dayIndex} sx={{ height: 120, bgcolor: 'grey.50' }} />;
                              }
                              
                              const dateString = date.toISOString().split('T')[0];
                              const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
                              const dayName = dayNames[date.getDay()];
                              
                              // 현재 실제 오늘 날짜 (monthOffset과 관계없이)
                              const actualToday = new Date();
                              actualToday.setHours(0, 0, 0, 0);
                              
                              const isToday = date.getTime() === actualToday.getTime() && monthOffset === 0;
                              const isPast = date < actualToday;
                              
                              // 해당 날짜에 활성화된 과제 찾기 (전체 학습 완료된 과제만 제외)
                              const dayAssignments = (() => {
                                // viewAssignments에서 시작 (이미 활성 과제만 필터링됨)
                                const baseAssignments = viewAssignments.filter(assignment => {
                                  // 시작일이 현재 날짜보다 늦으면 제외
                                  const assignmentStartDate = new Date(assignment.startDate);
                                  if (assignmentStartDate > date) return false;
                                  
                                  // 종료일이 있고 현재 날짜보다 이전이면 제외
                                  if (assignment.endDate) {
                                    const assignmentEndDate = new Date(assignment.endDate);
                                    if (assignmentEndDate < date) return false;
                                  }
                                  
                                  // 전체 학습이 완료된 과제만 제외 (주간 일정과 동일한 로직)
                                  if (isAssignmentCompleted(assignment, date)) return false;
                                  
                                  return true;
                                });
                                
                                // 오늘인 경우에도 요일별 스케줄 확인 (주간 일정과 동일하게)
                                // 오늘이 아닌 날짜와 동일한 로직 적용
                                return baseAssignments.filter(assignment => 
                                  assignment.weeklySchedule?.[dayName]?.isActive
                                );
                              })();
                              
                              return (
                                <TableCell 
                                  key={dayIndex} 
                                  sx={{ 
                                    height: 120, 
                                    verticalAlign: 'top',
                                    p: 1,
                                    bgcolor: isToday ? 'primary.50' : isPast ? 'grey.50' : 'background.paper',
                                    border: isToday ? '2px solid' : '1px solid',
                                    borderColor: isToday ? 'primary.main' : 'divider',
                                    position: 'relative',
                                    cursor: dayAssignments.length > 0 ? 'pointer' : 'default',
                                    '&:hover': {
                                      bgcolor: dayAssignments.length > 0 
                                        ? (isToday ? 'primary.100' : isPast ? 'grey.100' : 'grey.50')
                                        : undefined
                                    }
                                  }}
                                >
                                  <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                                    <Typography 
                                      variant="body2" 
                                      sx={{ 
                                        fontWeight: isToday ? 700 : 500,
                                        color: isToday ? 'primary.main' : isPast ? 'text.disabled' : 'text.primary'
                                      }}
                                    >
                                      {date.getDate()}
                                    </Typography>
                                    {dayAssignments.length > 0 && (
                                      <Chip 
                                        label={dayAssignments.length} 
                                        size="small" 
                                        color={isPast ? 'default' : 'primary'}
                                        sx={{ height: 20, fontSize: '0.75rem' }}
                                      />
                                    )}
                                  </Box>
                                  
                                  <Box sx={{ 
                                    display: 'flex', 
                                    flexDirection: 'column', 
                                    gap: 0.5,
                                    overflow: 'hidden'
                                  }}>
                                    {dayAssignments.slice(0, 3).map((assignment, idx) => (
                                      <Box
                                        key={idx}
                                        sx={{
                                          display: 'flex',
                                          alignItems: 'center',
                                          gap: 0.5,
                                          py: 0.25,
                                          borderBottom: idx < Math.min(dayAssignments.length - 1, 2) ? '1px solid' : 'none',
                                          borderColor: 'divider',
                                        }}
                                      >
                                        {/* 상태 표시 원 */}
                                        <Box
                                          sx={{
                                            width: 6,
                                            height: 6,
                                            borderRadius: '50%',
                                            border: '1px solid rgba(0,0,0,0.1)',
                                            bgcolor: (() => {
                                              const todayDate = new Date();
                                              todayDate.setHours(0, 0, 0, 0);
                                              
                                              // 미래 날짜는 노란색 (수행전)
                                              if (date > todayDate) {
                                                return '#FFD700'; // 진한 노란색
                                              }
                                              
                                              // 오늘이거나 과거 날짜인 경우
                                              const taskHistory = student?.learningHistory?.filter(
                                                h => {
                                                  if (h.taskId !== assignment.taskId) return false;
                                                  const historyDate = new Date(h.date);
                                                  historyDate.setHours(0, 0, 0, 0);
                                                  return historyDate.getTime() === date.getTime();
                                                }
                                              ) || [];
                                              
                                              // 학습 기록이 있으면 파란색 (수행완료)
                                              if (taskHistory.length > 0) {
                                                return '#2196F3'; // 파란색
                                              }
                                              
                                              // 학습 기록이 없으면 빨간색 (미수행)
                                              return '#F44336'; // 빨간색
                                            })(),
                                            flexShrink: 0
                                          }}
                                        />
                                        
                                        {/* 과제 정보 한 줄 표시 */}
                                        <Typography 
                                          variant="caption" 
                                          sx={{ 
                                            fontSize: '0.65rem',
                                            overflow: 'hidden',
                                            textOverflow: 'ellipsis',
                                            whiteSpace: 'nowrap',
                                            color: isPast ? 'text.disabled' : 'text.primary',
                                            flex: 1
                                          }}
                                        >
                                          {assignment.taskTitle} / {assignment.weeklySchedule?.[dayName]?.dailyAmount || '0'}개
                                        </Typography>
                                      </Box>
                                    ))}
                                    {dayAssignments.length > 3 && (
                                      <Typography 
                                        variant="caption" 
                                        sx={{ 
                                          fontSize: '0.65rem', 
                                          color: 'text.secondary',
                                          textAlign: 'center',
                                          display: 'block',
                                          mt: 0.5
                                        }}
                                      >
                                        +{dayAssignments.length - 3}개 더
                                      </Typography>
                                    )}
                                  </Box>
                                </TableCell>
                              );
                            })}
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                </Box>
              ) : (
                // 오늘의 과제 보기: 3일간의 과제 표시
                viewMode === 'today' ? (
                  <Box>
                    {(() => {
                      // 더미 데이터인지 확인
                      const isDummy = viewAssignments.length === 1 && viewAssignments[0].taskId === 'dummy';
                      const upcomingDays = getUpcomingAssignments();
                      
                      // 모든 영역 추출
                      const areas = new Set<string>();
                      upcomingDays.forEach(dayData => {
                        dayData.assignments.forEach(assignment => {
                          if (assignment.area) {
                            areas.add(assignment.area);
                          }
                        });
                      });
                      
                      // 영역별로 필터링된 과제
                      const filteredUpcomingDays = upcomingDays.map(dayData => ({
                        ...dayData,
                        assignments: selectedArea === 'all' 
                          ? dayData.assignments 
                          : dayData.assignments.filter(assignment => assignment.area === selectedArea)
                      }));
                      
                      return (
                        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                          {/* 영역별 탭 */}
                          {areas.size > 0 && (
                            <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 1 }}>
                              <ToggleButtonGroup
                                value={selectedArea}
                                exclusive
                                onChange={(event, newArea) => {
                                  if (newArea !== null) {
                                    setSelectedArea(newArea);
                                  }
                                }}
                                aria-label="area filter"
                                size="small"
                              >
                                <ToggleButton value="all" aria-label="all areas">
                                  전체
                                </ToggleButton>
                                {Array.from(areas).map(area => (
                                  <ToggleButton key={area} value={area} aria-label={area}>
                                    {getAreaText(area)}
                                  </ToggleButton>
                                ))}
                              </ToggleButtonGroup>
                            </Box>
                          )}
                          
                          {filteredUpcomingDays.map((dayData, dayIndex) => {
                            const dateLabel = dayIndex === 0 ? '오늘' : 
                                            dayIndex === 1 ? '내일' : '모레';
                            const dateString = `${dayData.date.getMonth() + 1}월 ${dayData.date.getDate()}일 (${dayData.dayName})`;
                            
                            return (
                              <Box key={dayIndex}>
                                <Typography 
                                  variant="h6" 
                                  sx={{ 
                                    mb: 2, 
                                    fontWeight: 600,
                                    color: dayIndex === 0 ? 'primary.main' : 'text.primary'
                                  }}
                                >
                                  {dateLabel} - {dateString}
                                </Typography>
                                
                                {dayData.assignments.length > 0 ? (
                                  <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                                    {dayData.assignments.map((assignment, idx) => (
                                      <Box
                                        key={idx}
                                        sx={{
                                          display: 'flex',
                                          alignItems: 'center',
                                          gap: 2,
                                          p: 2,
                                          border: '1px solid',
                                          borderColor: dayIndex === 0 ? 'primary.light' : 'grey.300',
                                          borderRadius: 1,
                                          bgcolor: 'background.paper',
                                          '&:hover': {
                                            bgcolor: 'grey.50',
                                          }
                                        }}
                                      >
                                        {/* 상태 표시 원 */}
                                        <Box
                                          sx={{
                                            width: 12,
                                            height: 12,
                                            borderRadius: '50%',
                                            border: '1px solid rgba(0,0,0,0.1)',
                                            bgcolor: (() => {
                                              const todayDate = new Date();
                                              todayDate.setHours(0, 0, 0, 0);
                                              
                                              // 미래 날짜는 노란색 (수행전)
                                              if (dayData.date > todayDate) {
                                                return '#FFD700';
                                              }
                                              
                                              // 오늘이거나 과거 날짜인 경우
                                              const taskHistory = student?.learningHistory?.filter(
                                                h => {
                                                  if (h.taskId !== assignment.taskId) return false;
                                                  const historyDate = new Date(h.date);
                                                  historyDate.setHours(0, 0, 0, 0);
                                                  return historyDate.getTime() === dayData.date.getTime();
                                                }
                                              ) || [];
                                              
                                              // 학습 기록이 있으면 파란색 (수행완료)
                                              if (taskHistory.length > 0) {
                                                return '#2196F3';
                                              }
                                              
                                              // 학습 기록이 없으면 빨간색 (미수행)
                                              return '#F44336';
                                            })(),
                                            flexShrink: 0
                                          }}
                                        />
                                        
                                        {/* 과제 정보 */}
                                        <Box sx={{ flex: 1 }}>
                                          <Typography variant="body1" sx={{ fontWeight: 600 }}>
                                            {assignment.taskTitle}
                                            {assignment.targetUnit && (
                                              <Typography component="span" variant="body2" color="text.secondary" sx={{ ml: 1 }}>
                                                ({assignment.targetUnit})
                                              </Typography>
                                            )}
                                          </Typography>
                                          <Typography variant="caption" color="text.secondary">
                                            학습량 {assignment.weeklySchedule?.[['일', '월', '화', '수', '목', '금', '토'][dayData.date.getDay()]]?.dailyAmount || '0'}개 · 
                                            진도 {(() => {
                                              if (assignment.targetUnit) {
                                                const unitOrder: string[] = [];
                                                const unitSet = new Set<string>();
                                                
                                                if (assignment.vocabularyData) {
                                                  assignment.vocabularyData.forEach((word: any) => {
                                                    if (!unitSet.has(word.unit)) {
                                                      unitSet.add(word.unit);
                                                      unitOrder.push(word.unit);
                                                    }
                                                  });
                                                }
                                                
                                                const startUnitIndex = assignment.startUnit ? unitOrder.indexOf(assignment.startUnit) : 0;
                                                const targetUnitIndex = unitOrder.indexOf(assignment.targetUnit);
                                                const unitNumber = targetUnitIndex - startUnitIndex + 1;
                                                
                                                return `${unitNumber}/${unitOrder.length - startUnitIndex}`;
                                              }
                                              
                                              const unitProgress = getUnitProgressForDate(assignment, dayData.date);
                                              if (unitProgress) {
                                                return `${unitProgress.current}/${unitProgress.total}`;
                                              }
                                              
                                              return `0/${assignment.progress.total}`;
                                            })()} · 
                                            학습 {(() => {
                                              const targetDateString = dayData.date.toISOString().split('T')[0];
                                              console.log(`Checking learning history for ${assignment.taskTitle} (${assignment.taskId}) on date ${targetDateString}`);
                                              
                                              const taskHistory = student?.learningHistory?.filter(
                                                h => {
                                                  if (h.taskId !== assignment.taskId) {
                                                    console.log(`Skipping history item with different taskId: ${h.taskId} !== ${assignment.taskId}`);
                                                    return false;
                                                  }
                                                  const historyDate = new Date(h.date);
                                                  historyDate.setHours(0, 0, 0, 0);
                                                  const historyDateString = historyDate.toISOString().split('T')[0];
                                                  const matches = historyDateString === targetDateString;
                                                  console.log(`History item for ${h.taskTitle} (${h.taskId}): date=${historyDateString}, matches=${matches}`);
                                                  return matches;
                                                }
                                              ) || [];
                                              
                                              console.log(`Found ${taskHistory.length} learning records for ${assignment.taskTitle} on ${targetDateString}`);
                                              
                                              return `${taskHistory.length}회`;
                                            })()}
                                          </Typography>
                                        </Box>
                                        
                                        {/* 액션 버튼 */}
                                        <Box sx={{ display: 'flex', gap: 1 }}>
                                          <Button
                                            variant="contained"
                                            size="small"
                                            onClick={() => handleStartVocabularyLearning(assignment, dayData.date)}
                                          >
                                            학습
                                          </Button>
                                          <Button
                                            variant="outlined"
                                            size="small"
                                            onClick={() => handleStartEvaluation(assignment, dayData.date)}
                                          >
                                            평가
                                          </Button>
                                        </Box>
                                      </Box>
                                    ))}
                                  </Box>
                                ) : (
                                  <Card sx={{ borderRadius: isMobile ? 2 : 1, textAlign: 'center', bgcolor: 'grey.50' }}>
                                    <CardContent sx={{ py: 3 }}>
                                      <Typography variant="body1" color="text.secondary">
                                        {dateLabel} 과제가 없습니다
                                      </Typography>
                                    </CardContent>
                                  </Card>
                                )}
                              </Box>
                            );
                          })}
                        </Box>
                      );
                    })()}
                  </Box>
                ) : (
                  // 기존 월간 보기
                  <Grid container spacing={isMobile ? 2 : 3}>
                    {viewAssignments.map((assignment, index) => (
                      <Grid item xs={12} key={index}>
                        <Card 
                          sx={{ 
                            borderRadius: isMobile ? 2 : 1,
                            border: '2px solid',
                            borderColor: 'primary.main',
