                          <CardContent sx={{ p: isMobile ? 3 : 2 }}>
                            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
                              <Box sx={{ flex: 1 }}>
                                <Typography variant="h6" sx={{ mb: 1, fontWeight: 600 }}>
                                  {assignment.taskTitle}
                                </Typography>
                                <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                                  <AssignmentIcon sx={{ fontSize: 16, mr: 0.5, color: 'primary.main' }} />
                                  <Typography variant="body2" color="primary" sx={{ fontWeight: 500 }}>
                                    {getAreaText(assignment.area)} 영역
                                  </Typography>
                                </Box>
                              </Box>
                              <Chip
                                label={getChipLabel(viewMode)}
                                color="primary"
                                size="small"
                                sx={{ fontWeight: 600 }}
                              />
                            </Box>

                            {/* 학습 진도 */}
                            <Box sx={{ mb: 3 }}>
                              <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                                <Typography variant="body2" sx={{ fontWeight: 500 }}>
                                  학습 진도
                                </Typography>
                                <Typography variant="h6" color="primary" sx={{ fontWeight: 600 }}>
                                  {(() => {
                                    const today = new Date();
                                    const unitProgress = getUnitProgressForDate(assignment, today);
                                    if (unitProgress) {
                                      return `${unitProgress.current}/${unitProgress.total}`;
                                    }
                                    // 단원 목록 추출
                                    const unitOrder: string[] = [];
                                    const unitSet = new Set<string>();
                                    
                                    if (assignment.vocabularyData) {
                                      assignment.vocabularyData.forEach((word: any) => {
                                        if (!unitSet.has(word.unit)) {
                                          unitSet.add(word.unit);
                                          unitOrder.push(word.unit);
                                        }
                                      });
                                    }
                                    
                                    return `0/${unitOrder.length || assignment.progress.total}`;
                                  })()}
                                </Typography>
                              </Box>
                              <LinearProgress
                                variant="determinate"
                                value={(() => {
                                  const today = new Date();
                                  const unitProgress = getUnitProgressForDate(assignment, today);
                                  if (unitProgress) {
                                    return (unitProgress.current / unitProgress.total) * 100;
                                  }
                                  return 0;
                                })()}
                                sx={{ 
                                  height: 8, 
                                  borderRadius: 4,
                                  backgroundColor: 'grey.200',
