                                  const today = new Date();
                                  const unit = getUnitForDate(assignment, today);
                                  if (unit) {
                                    return `오늘 학습 단원: ${unit}`;
                                  }
                                  const dayName = ['일', '월', '화', '수', '목', '금', '토'][today.getDay()];
                                  const todaySchedule = assignment.weeklySchedule?.[dayName];
                                  if (!todaySchedule || !todaySchedule.isActive) {
                                    return '오늘은 학습 예정일이 아닙니다';
                                  }
                                  return '오늘 학습 예정';
                                })()}
