                                {(() => {
                                  // 오늘 날짜의 학습 이력만 필터링
                                  const today = new Date();
                                  today.setHours(0, 0, 0, 0);
                                  const todayString = today.toISOString().split('T')[0];
                                  
                                  console.log('Checking learning history for assignment:', assignment.taskTitle, 'taskId:', assignment.taskId);
                                  console.log('Today string:', todayString);
                                  
                                  const taskHistory = student?.learningHistory?.filter(
                                    h => {
                                      if (h.taskId !== assignment.taskId) return false;
                                      
                                      // 학습 날짜가 오늘인지 확인
                                      const historyDate = new Date(h.date);
                                      historyDate.setHours(0, 0, 0, 0);
                                      const historyDateString = historyDate.toISOString().split('T')[0];
                                      
                                      const matches = historyDateString === todayString;
                                      console.log('History item:', h.taskId, 'date:', historyDateString, 'matches today:', matches);
                                      
                                      return matches;
                                    }
                                  ) || [];
                                  
                                  console.log('Task history for', assignment.taskTitle, ':', taskHistory.length, 'items');
                                  
                                  if (taskHistory.length === 0) {
                                    return (
