import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Button,
  Chip,
  LinearProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  useTheme,
  useMediaQuery,
  Container,
  Paper,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  Grid,
} from '@mui/material';
import {
  CheckCircle as CheckCircleIcon,
  Help as HelpIcon,
  Close as CloseIcon,
  ArrowBack as ArrowBackIcon,
  PlayArrow as PlayArrowIcon,
  Pause as PauseIcon,
  Stop as StopIcon,
  Refresh as RefreshIcon,
  Visibility as VisibilityIcon,
  VisibilityOff as VisibilityOffIcon,
} from '@mui/icons-material';

interface VocabularyWord {
  id: string;
  english: string;
  korean: string;
  unit: string;
}

interface LearningSession {
  id: string;
  words: VocabularyWord[];
  currentWordIndex: number;
  completedWords: string[];
  triangleWords: string[];
  xWords: string[];
  isCompleted: boolean;
  startTime: Date;
  endTime?: Date;
}

interface VocabularyLearningProps {
  assignment: any; // TaskAssignment 타입
  onComplete: (session: LearningSession) => void;
  onClose: () => void;
}

const VocabularyLearning: React.FC<VocabularyLearningProps> = ({
  assignment,
  onComplete,
  onClose,
}) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  
  const [session, setSession] = useState<LearningSession | null>(null);
  const [currentWord, setCurrentWord] = useState<VocabularyWord | null>(null);
  const [showMeaning, setShowMeaning] = useState(false);
  const [showDoubleCheck, setShowDoubleCheck] = useState(false);
  const [doubleCheckTimer, setDoubleCheckTimer] = useState<number>(0);
  const [isPaused, setIsPaused] = useState(false);
  const [showResults, setShowResults] = useState(false);

  // 더미 단어 데이터 (실제로는 API에서 가져올 예정)
  const dummyWords: VocabularyWord[] = [
    { id: '1', english: 'apple', korean: '사과', unit: '1' },
    { id: '2', english: 'banana', korean: '바나나', unit: '1' },
    { id: '3', english: 'orange', korean: '오렌지', unit: '1' },
    { id: '4', english: 'grape', korean: '포도', unit: '1' },
    { id: '5', english: 'strawberry', korean: '딸기', unit: '1' },
  ];

  useEffect(() => {
    initializeSession();
  }, []);

  useEffect(() => {
    if (doubleCheckTimer > 0) {
      const timer = setTimeout(() => {
        setDoubleCheckTimer(prev => prev - 1);
      }, 1000);
      return () => clearTimeout(timer);
    } else if (doubleCheckTimer === 0 && showDoubleCheck) {
      handleNextWord();
    }
  }, [doubleCheckTimer, showDoubleCheck]);

  const initializeSession = () => {
    const newSession: LearningSession = {
      id: Date.now().toString(),
      words: dummyWords,
      currentWordIndex: 0,
      completedWords: [],
      triangleWords: [],
      xWords: [],
      isCompleted: false,
      startTime: new Date(),
    };
    setSession(newSession);
    setCurrentWord(dummyWords[0]);
  };

  const handleWordCheck = (checkType: 'circle' | 'triangle' | 'x') => {
    if (!session || !currentWord) return;

    const updatedSession = { ...session };
    
    if (checkType === 'circle') {
      updatedSession.completedWords.push(currentWord.id);
    } else if (checkType === 'triangle') {
      updatedSession.triangleWords.push(currentWord.id);
    } else {
      updatedSession.xWords.push(currentWord.id);
    }

    setSession(updatedSession);
    setShowMeaning(true);
  };

  const handleDoubleCheck = (isCorrect: boolean) => {
    if (!session || !currentWord) return;

    const updatedSession = { ...session };
    
    if (isCorrect) {
      // 원래 체크가 맞았음
      if (updatedSession.completedWords.includes(currentWord.id)) {
        // 이미 완료된 단어
      } else {
        // triangle이나 x에서 circle로 변경
        updatedSession.completedWords.push(currentWord.id);
        updatedSession.triangleWords = updatedSession.triangleWords.filter(id => id !== currentWord.id);
        updatedSession.xWords = updatedSession.xWords.filter(id => id !== currentWord.id);
      }
    } else {
      // 원래 체크가 틀렸음 - triangle으로 변경
      updatedSession.completedWords = updatedSession.completedWords.filter(id => id !== currentWord.id);
      if (!updatedSession.triangleWords.includes(currentWord.id)) {
        updatedSession.triangleWords.push(currentWord.id);
      }
      updatedSession.xWords = updatedSession.xWords.filter(id => id !== currentWord.id);
    }

    setSession(updatedSession);
    setShowDoubleCheck(false);
    setDoubleCheckTimer(0);
    handleNextWord();
  };

  const handleNextWord = () => {
    if (!session) return;

    const nextIndex = session.currentWordIndex + 1;
    
    if (nextIndex >= session.words.length) {
      // 한 사이클 완료
      if (session.triangleWords.length === 0 && session.xWords.length === 0) {
        // 모든 단어 완료
        completeSession();
      } else {
        // triangle과 x 단어들만 다시 학습
        const remainingWords = session.words.filter(word => 
          session.triangleWords.includes(word.id) || session.xWords.includes(word.id)
        );
        
        const newSession: LearningSession = {
          ...session,
          words: remainingWords,
          currentWordIndex: 0,
          triangleWords: [],
          xWords: [],
        };
        
        setSession(newSession);
        setCurrentWord(remainingWords[0]);
        setShowMeaning(false);
      }
    } else {
      // 다음 단어로
      setSession({ ...session, currentWordIndex: nextIndex });
      setCurrentWord(session.words[nextIndex]);
      setShowMeaning(false);
    }
  };

  const completeSession = () => {
    if (!session) return;
    
    const completedSession: LearningSession = {
      ...session,
      isCompleted: true,
      endTime: new Date(),
    };
    
    setSession(completedSession);
    setShowResults(true);
  };

  const getProgress = () => {
    if (!session) return 0;
    const totalWords = session.words.length;
    const completedWords = session.completedWords.length;
    return (completedWords / totalWords) * 100;
  };

  const getRemainingWords = () => {
    if (!session) return 0;
    return session.triangleWords.length + session.xWords.length;
  };

  if (!session || !currentWord) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Typography>학습을 준비하는 중...</Typography>
      </Box>
    );
  }

  if (showResults) {
    return (
      <Container maxWidth="md" sx={{ py: 3 }}>
        <Card sx={{ borderRadius: isMobile ? 2 : 1 }}>
          <CardContent sx={{ p: isMobile ? 3 : 2 }}>
            <Box sx={{ textAlign: 'center', mb: 3 }}>
              <CheckCircleIcon sx={{ fontSize: 64, color: 'success.main', mb: 2 }} />
              <Typography variant="h5" sx={{ mb: 1, fontWeight: 600 }}>
                학습 완료!
              </Typography>
              <Typography variant="body1" color="text.secondary">
                모든 단어를 학습했습니다.
              </Typography>
            </Box>

            <Box sx={{ mb: 3 }}>
              <Typography variant="h6" sx={{ mb: 2, fontWeight: 600 }}>
                학습 결과
              </Typography>
              
              <Grid container spacing={2}>
                <Grid item xs={4}>
                  <Box sx={{ textAlign: 'center', p: 2, bgcolor: 'success.50', borderRadius: 1 }}>
                    <Typography variant="h4" color="success.main" sx={{ fontWeight: 600 }}>
                      {session.completedWords.length}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      완료된 단어
                    </Typography>
                  </Box>
                </Grid>
                <Grid item xs={4}>
                  <Box sx={{ textAlign: 'center', p: 2, bgcolor: 'warning.50', borderRadius: 1 }}>
                    <Typography variant="h4" color="warning.main" sx={{ fontWeight: 600 }}>
                      {session.triangleWords.length}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      재학습 필요
                    </Typography>
                  </Box>
                </Grid>
                <Grid item xs={4}>
                  <Box sx={{ textAlign: 'center', p: 2, bgcolor: 'error.50', borderRadius: 1 }}>
                    <Typography variant="h4" color="error.main" sx={{ fontWeight: 600 }}>
                      {session.xWords.length}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      미완료 단어
                    </Typography>
                  </Box>
                </Grid>
              </Grid>
            </Box>

            <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center' }}>
              <Button
                variant="contained"
                onClick={() => onComplete(session)}
                size={isMobile ? "large" : "medium"}
              >
                학습 완료
              </Button>
              <Button
                variant="outlined"
                onClick={onClose}
                size={isMobile ? "large" : "medium"}
              >
                닫기
              </Button>
            </Box>
          </CardContent>
        </Card>
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ py: 3 }}>
      {/* 헤더 */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <IconButton onClick={onClose} sx={{ mr: 2 }}>
            <ArrowBackIcon />
          </IconButton>
          <Typography variant="h6" sx={{ fontWeight: 600 }}>
            단어 학습
          </Typography>
        </Box>
        
        <Box sx={{ display: 'flex', gap: 1 }}>
          <IconButton onClick={() => setIsPaused(!isPaused)}>
            {isPaused ? <PlayArrowIcon /> : <PauseIcon />}
          </IconButton>
          <IconButton onClick={onClose}>
            <StopIcon />
          </IconButton>
        </Box>
      </Box>

      {/* 진행률 */}
      <Box sx={{ mb: 3 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
          <Typography variant="body2">
            진행률: {Math.round(getProgress())}%
          </Typography>
          <Typography variant="body2">
            {session.currentWordIndex + 1} / {session.words.length}
          </Typography>
        </Box>
        <LinearProgress
          variant="determinate"
          value={getProgress()}
          sx={{ height: 8, borderRadius: 4 }}
        />
      </Box>

      {/* 단어 카드 */}
      <Card sx={{ mb: 3, borderRadius: isMobile ? 2 : 1 }}>
        <CardContent sx={{ p: isMobile ? 4 : 3, textAlign: 'center' }}>
          <Typography variant="h3" sx={{ mb: 3, fontWeight: 600 }}>
            {currentWord.english}
          </Typography>

          {showMeaning ? (
            <Box>
              <Typography variant="h5" color="primary" sx={{ mb: 3, fontWeight: 600 }}>
                {currentWord.korean}
              </Typography>
              
              <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mb: 3 }}>
                <Button
                  variant="outlined"
                  color="success"
                  onClick={() => handleDoubleCheck(true)}
                  sx={{ minWidth: 120 }}
                >
                  맞았어요
                </Button>
                <Button
                  variant="outlined"
                  color="error"
                  onClick={() => handleDoubleCheck(false)}
                  sx={{ minWidth: 120 }}
                >
                  틀렸어요
                </Button>
              </Box>

              {doubleCheckTimer > 0 && (
                <Typography variant="body2" color="text.secondary">
                  {doubleCheckTimer}초 후 자동으로 다음 단어로 넘어갑니다.
                </Typography>
              )}
            </Box>
          ) : (
            <Box>
              <Typography variant="body1" color="text.secondary" sx={{ mb: 4 }}>
                이 단어를 알고 있나요?
              </Typography>
              
              <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, flexWrap: 'wrap' }}>
                <Button
                  variant="contained"
                  color="success"
                  size="large"
                  onClick={() => handleWordCheck('circle')}
                  sx={{ 
                    minWidth: 120, 
                    py: 2,
                    borderRadius: 2,
                    '&:hover': { transform: 'scale(1.05)' }
                  }}
                >
                  <CheckCircleIcon sx={{ mr: 1 }} />
                  완전히 알고 있어요
                </Button>
                
                <Button
                  variant="contained"
                  color="warning"
                  size="large"
                  onClick={() => handleWordCheck('triangle')}
                  sx={{ 
                    minWidth: 120, 
                    py: 2,
                    borderRadius: 2,
                    '&:hover': { transform: 'scale(1.05)' }
                  }}
                >
                  <HelpIcon sx={{ mr: 1 }} />
                  조금 헷갈려요
                </Button>
                
                <Button
                  variant="contained"
                  color="error"
                  size="large"
                  onClick={() => handleWordCheck('x')}
                  sx={{ 
                    minWidth: 120, 
                    py: 2,
                    borderRadius: 2,
                    '&:hover': { transform: 'scale(1.05)' }
                  }}
                >
                  <CloseIcon sx={{ mr: 1 }} />
                  모르겠어요
                </Button>
              </Box>
            </Box>
          )}
        </CardContent>
      </Card>

      {/* 학습 통계 */}
      <Box sx={{ display: 'flex', justifyContent: 'space-around', textAlign: 'center' }}>
        <Box>
          <Typography variant="h6" color="success.main" sx={{ fontWeight: 600 }}>
            {session.completedWords.length}
          </Typography>
          <Typography variant="caption" color="text.secondary">
            완료
          </Typography>
        </Box>
        <Box>
          <Typography variant="h6" color="warning.main" sx={{ fontWeight: 600 }}>
            {session.triangleWords.length}
          </Typography>
          <Typography variant="caption" color="text.secondary">
            재학습
          </Typography>
        </Box>
        <Box>
          <Typography variant="h6" color="error.main" sx={{ fontWeight: 600 }}>
            {session.xWords.length}
          </Typography>
          <Typography variant="caption" color="text.secondary">
            미완료
          </Typography>
        </Box>
      </Box>
    </Container>
  );
};

export default VocabularyLearning;
