import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Chip,
  LinearProgress,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  Avatar,
  useTheme,
  useMediaQuery,
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  AppBar,
  Toolbar,
  Container,
  Tabs,
  Tab,
  Slider,
  ToggleButton,
  ToggleButtonGroup,
  Tooltip,
} from '@mui/material';
import {
  Assignment as AssignmentIcon,
  CheckCircle as CheckCircleIcon,
  ArrowBack as ArrowBackIcon,
  Info as InfoIcon,
  CalendarToday as CalendarIcon,
  TrendingUp as TrendingUpIcon,
  Close as CloseIcon,
  Refresh as RefreshIcon,
  Today as TodayIcon,
  ViewModule as ViewModuleIcon,
  PlayArrow as PlayArrowIcon,
  Settings as SettingsIcon,
  Timer as TimerIcon,
  DateRange as DateRangeIcon,
  CalendarMonth as CalendarMonthIcon,
  NavigateBefore as NavigateBeforeIcon,
  NavigateNext as NavigateNextIcon,
  Cancel as CancelIcon,
} from '@mui/icons-material';
import { useParams, useNavigate } from 'react-router-dom';
import { loadStudents, saveStudents, loadTasks } from '../utils/storage';
import VocabularyLearning from '../components/VocabularyLearning';
import VocabularyEvaluation from '../components/VocabularyEvaluation';

interface TaskAssignment {
  taskId: string;
  taskTitle: string;
  area: 'vocabulary' | 'phrase' | 'grammar' | 'logic';
  weeklySchedule: {
    [day: string]: {
      isActive: boolean;
      dailyAmount: string;
    };
  };
  startDate: string;
  endDate?: string;
  status: 'active' | 'completed' | 'paused';
  progress: {
    completed: number;
    total: number;
  };
  startUnit?: string;
  vocabularyData?: Array<{ unit: string; english: string; meaning: string }>;
  targetDate?: string; // 학습을 수행하는 대상 날짜
  targetUnit?: string; // 특정 단원을 지정하여 학습할 때 사용
  isWrongAnswerLearning?: boolean; // 오답학습 여부
  wrongAnswerSession?: number; // 오답학습 회차
}

interface Student {
  id: string;
  name: string;
  address: string;
  phone: string;
  grade: '초6' | '중1' | '중2' | '중3' | '고1' | '고2' | '고3';
  joinDate: string;
  status: 'active' | 'inactive';
  assignedTasks: string[];
  taskAssignments: TaskAssignment[];
  grades: {
    [year: string]: {
      mockExam3?: string;
      mockExam6?: string;
      mockExam9?: string;
      mockExam11?: string;
      midterm1?: string;
      final1?: string;
      midterm2?: string;
      final2?: string;
    };
  };
  learningHistory?: Array<{
    date: string;
    taskId: string;
    taskTitle: string;
    targetUnit?: string; // 특정 단원 학습 시 단원 정보
    learningSession: number; // 학습 회차 (1, 2, 3...)
    isFirstLearning: boolean; // 최초 학습 여부
    isWrongAnswerLearning?: boolean; // 오답학습 여부
    wrongAnswerSession?: number; // 오답학습 회차
    rounds: any[];
    summary: {
      totalRounds: number;
      totalWords: number;
      finalCompletedWords: number;
      completionRate: number;
    };
  }>;
  evaluationHistory?: Array<{
    date: string;
    taskId: string;
    taskTitle: string;
    targetUnit?: string; // 특정 단원 평가 시 단원 정보
    learningSession: number; // 어떤 학습 회차에 대한 평가인지
    attemptNumber: number; // 평가 차수 (1차, 2차, 3차...)
    results: Array<{
      wordId: string;
      word: string;
      userAnswer: string;
      correctAnswer: string;
      score: number;
      isCorrect: boolean;
    }>;
    summary: {
      totalWords: number;
      correctWords: number;
      incorrectWords: number;
      accuracy: number;
      passed: boolean; // 모두 맞췄는지 여부
    };
  }>;

}

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ pt: 2 }}>{children}</Box>}
    </div>
  );
}

const StudentPage: React.FC = () => {
  const { studentId } = useParams<{ studentId: string }>();
  const navigate = useNavigate();
  const [student, setStudent] = useState<Student | null>(null);
  const [selectedAssignment, setSelectedAssignment] = useState<TaskAssignment | null>(null);
  const [openDetailDialog, setOpenDetailDialog] = useState(false);
  const [openMobileDetail, setOpenMobileDetail] = useState(false);
  const [tabValue, setTabValue] = useState(0);
  const [showVocabularyLearning, setShowVocabularyLearning] = useState(false);
  const [showVocabularyEvaluation, setShowVocabularyEvaluation] = useState(false);
  const [selectedArea, setSelectedArea] = useState<string>('all');
  const [timerDuration, setTimerDuration] = useState(() => {
    // 로컬 스토리지에서 저장된 타이머 시간 불러오기
    const saved = localStorage.getItem(`timer_duration_${studentId}`);
    return saved ? parseFloat(saved) : 1.5;
  });
  const [viewMode, setViewMode] = useState<'today' | 'week' | 'month'>('today');
  const [weekOffset, setWeekOffset] = useState(0); // 0 = 이번 주, -1 = 지난 주, 1 = 다음 주
  const [monthOffset, setMonthOffset] = useState(0); // 0 = 이번 달, -1 = 지난 달, 1 = 다음 달

  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  useEffect(() => {
    if (studentId) {
      const students = loadStudents();
      const foundStudent = students.find(s => s.id === studentId);
      if (foundStudent) {
        setStudent(foundStudent);
      } else {
        // 학생을 찾을 수 없는 경우 관리자 페이지로 리다이렉트
        navigate('/students');
      }
    }
  }, [studentId, navigate]);

  // viewMode가 변경될 때 selectedArea를 리셋
  useEffect(() => {
    setSelectedArea('all');
  }, [viewMode]);

  // 학생 정보 변경 모니터링
  useEffect(() => {
    if (student) {
      console.log('Student data updated:', {
        name: student.name,
        taskAssignments: student.taskAssignments.map(a => ({ 
          taskId: a.taskId, 
          title: a.taskTitle 
        })),
        learningHistoryCount: student.learningHistory?.length || 0
      });

      // 오답학습 상태 복원
      const savedWrongAnswerState = localStorage.getItem('wrongAnswerLearningState');
      if (savedWrongAnswerState) {
        try {
          const wrongAnswerState = JSON.parse(savedWrongAnswerState);

          // 현재 학생의 오답학습 상태인지 확인
          if (wrongAnswerState.studentId === student.id) {
            console.log('Restoring wrong answer learning state:', wrongAnswerState);

            // 저장된 오답학습 assignment 생성
            const wrongAnswerAssignment = {
              taskId: wrongAnswerState.taskId,
              taskTitle: '오답학습',
              area: 'vocabulary' as const,
              weeklySchedule: {
                '월': { isActive: true, dailyAmount: '5' },
                '화': { isActive: true, dailyAmount: '5' },
                '수': { isActive: true, dailyAmount: '5' },
                '목': { isActive: true, dailyAmount: '5' },
                '금': { isActive: true, dailyAmount: '5' },
                '토': { isActive: false, dailyAmount: '0' },
                '일': { isActive: false, dailyAmount: '0' },
              },
              startDate: new Date().toISOString().split('T')[0],
              status: 'active' as const,
              progress: {
                completed: 0,
                total: wrongAnswerState.wrongWords.length,
              },
              targetDate: wrongAnswerState.targetDate,
              targetUnit: wrongAnswerState.targetUnit,
              vocabularyData: wrongAnswerState.wrongWords,
              isWrongAnswerLearning: true
            };

            setSelectedAssignment(wrongAnswerAssignment);
            setShowVocabularyLearning(true);
          }
        } catch (error) {
          console.error('Error restoring wrong answer learning state:', error);
          localStorage.removeItem('wrongAnswerLearningState');
        }
      }
    }
  }, [student]);

  const getAreaText = (area: string) => {
    switch (area) {
      case 'vocabulary': return '단어';
      case 'phrase': return '구문';
      case 'grammar': return '어법';
      case 'logic': return '논리';
      default: return area;
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'active': return '진행중';
      case 'completed': return '완료';
      case 'paused': return '일시정지';
      default: return status;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'success';
      case 'completed': return 'primary';
      case 'paused': return 'warning';
      default: return 'default';
    }
  };

  const getGradeText = (grade: string) => {
    return grade;
  };

  const getGradeColor = (grade: string) => {
    switch (grade) {
      case '초6':
        return 'warning';
      case '중1':
      case '중2':
      case '중3':
        return 'primary';
      case '고1':
      case '고2':
      case '고3':
        return 'success';
      default:
        return 'default';
    }
  };

  const handleOpenDetailDialog = (assignment: TaskAssignment) => {
    console.log('Opening detail dialog for assignment:', assignment.taskTitle, 'taskId:', assignment.taskId);
    setSelectedAssignment(assignment);
    if (isMobile) {
      setOpenMobileDetail(true);
    } else {
      setOpenDetailDialog(true);
    }
  };

  const handleCloseDetailDialog = () => {
    setOpenDetailDialog(false);
    setOpenMobileDetail(false);
    setSelectedAssignment(null);
  };

  const handleStartVocabularyLearning = (assignment: TaskAssignment & { targetUnit?: string }, targetDate?: Date) => {
    console.log('Starting vocabulary learning for assignment:', assignment);
    console.log('Assignment taskId:', assignment.taskId);
    console.log('Assignment title:', assignment.taskTitle);
    console.log('Target date:', targetDate);

    // targetDate가 있으면 assignment에 추가
    const assignmentWithDate = targetDate 
      ? { ...assignment, targetDate: targetDate.toISOString() }
      : assignment;

    console.log('Assignment with date:', assignmentWithDate);

    setSelectedAssignment(assignmentWithDate);
    // 타이머 설정 다이얼로그를 건너뛰고 바로 학습 시작
    setShowVocabularyLearning(true);
  };

  const handleStartWrongAnswerLearning = (assignment: TaskAssignment & { targetUnit?: string }, targetDate?: Date) => {
    console.log('Starting wrong answer learning for assignment:', assignment);

    if (!student) {
      alert('학생 정보를 찾을 수 없습니다.');
      return;
    }

    // 해당 과제의 최초 학습 기록 찾기 (오답학습은 최초 학습의 1라운드에서 어려운 단어들을 사용)
    const targetUnit = assignment.targetUnit;
    const learningRecords = student.learningHistory?.filter(
      history => history.taskId === assignment.taskId && 
                 (!targetUnit || (history as any).targetUnit === targetUnit) &&
                 !history.isWrongAnswerLearning // 오답학습 기록은 제외하고 일반 학습 기록만
    ) || [];

    if (learningRecords.length === 0) {
      alert('먼저 학습을 완료해야 오답학습을 진행할 수 있습니다.');
      return;
    }

    // 최초 학습 기록에서 어려워했던 단어들 추출 (가장 최근이 아닌 최초 학습)
    const firstLearning = learningRecords[0]; // 최초 학습 기록
    const wrongWords: any[] = [];

    console.log('=== 오답학습 디버깅 ===');
    console.log('First learning record:', firstLearning);
    console.log('First learning rounds:', firstLearning.rounds);
    console.log('Learning records count:', learningRecords.length);

    // 1라운드에서 어려워했던 단어들 찾기
    if (firstLearning.rounds && firstLearning.rounds.length > 0) {
      const firstRound = firstLearning.rounds[0];
      console.log('First round:', firstRound);
      console.log('First round has wordStates:', !!firstRound.wordStates);
      console.log('First round wordStates is array:', Array.isArray(firstRound.wordStates));

      if (firstRound.wordStates && Array.isArray(firstRound.wordStates)) {
        console.log('First round wordStates:', firstRound.wordStates);
        console.log('WordStates count:', firstRound.wordStates.length);

        let forgotCount = 0;
        let repeatCount = 0;
        let unknownCount = 0;
        let completeCount = 0;

        firstRound.wordStates.forEach((state: any, index: number) => {
          console.log(`Word ${index + 1}:`, state.word?.english || state.word?.word, 'Status:', state.status);

          // "아몰랐어요"(forgot), "1번더볼게요"(repeat), "모르겠어요"(unknown) 상태인 단어들
          if (state.status === 'forgot' || state.status === 'repeat' || state.status === 'unknown') {
            console.log('Adding wrong word:', state.word);
            wrongWords.push({
              ...state.word,
              id: state.word.id || `${state.word.english || state.word.word}_${state.word.korean || state.word.meaning}`,
              word: state.word.english || state.word.word,
              meaning: state.word.korean || state.word.meaning, // korean 속성을 meaning으로 변환
              example: state.word.example,
              translation: state.word.translation
            });
          }

          // 카운트 업데이트
          switch (state.status) {
            case 'forgot': forgotCount++; break;
            case 'repeat': repeatCount++; break;
            case 'unknown': unknownCount++; break;
            case 'complete': completeCount++; break;
          }
        });

        console.log('Status counts:');
        console.log('  - forgot (아몰랐어요):', forgotCount);
        console.log('  - repeat (1번더볼게요):', repeatCount);
        console.log('  - unknown (모르겠어요):', unknownCount);
        console.log('  - complete (완료):', completeCount);
        console.log('  - Total wrong words found:', wrongWords.length);
      } else {
        console.log('No wordStates found in first round');
      }
    } else {
      console.log('No rounds found in latest learning');
    }

    console.log('Extracted wrong words:', wrongWords);
    console.log('Wrong words count:', wrongWords.length);

    if (wrongWords.length === 0) {
      alert('오답학습할 단어가 없습니다. 모든 단어를 잘 알고 있습니다!');
      return;
    }

    // 오답학습 회차 계산
