import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Chip,
  LinearProgress,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  Avatar,
  useTheme,
  useMediaQuery,
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  AppBar,
  Toolbar,
  Container,
  Tabs,
  Tab,
  Slider,
  ToggleButton,
  ToggleButtonGroup,
} from '@mui/material';
import {
  Assignment as AssignmentIcon,
  CheckCircle as CheckCircleIcon,
  ArrowBack as ArrowBackIcon,
  Info as InfoIcon,
  CalendarToday as CalendarIcon,
  TrendingUp as TrendingUpIcon,
  Close as CloseIcon,
  Refresh as RefreshIcon,
  Today as TodayIcon,
  ViewModule as ViewModuleIcon,
  PlayArrow as PlayArrowIcon,
  Settings as SettingsIcon,
  Timer as TimerIcon,
  DateRange as DateRangeIcon,
  CalendarMonth as CalendarMonthIcon,
} from '@mui/icons-material';
import { useParams, useNavigate } from 'react-router-dom';
import { loadStudents, saveStudents, loadTasks } from '../utils/storage';
import VocabularyLearning from '../components/VocabularyLearning';

interface TaskAssignment {
  taskId: string;
  taskTitle: string;
  area: 'vocabulary' | 'phrase' | 'grammar' | 'logic';
  weeklySchedule: {
    [day: string]: {
      isActive: boolean;
      dailyAmount: string;
    };
  };
  startDate: string;
  endDate?: string;
  status: 'active' | 'completed' | 'paused';
  progress: {
    completed: number;
    total: number;
  };
  startUnit?: string;
  vocabularyData?: Array<{ unit: string; english: string; meaning: string }>;
}

interface Student {
  id: string;
  name: string;
  address: string;
  phone: string;
  grade: '초6' | '중1' | '중2' | '중3' | '고1' | '고2' | '고3';
  joinDate: string;
  status: 'active' | 'inactive';
  assignedTasks: string[];
  taskAssignments: TaskAssignment[];
  grades: {
    [year: string]: {
      mockExam3?: string;
      mockExam6?: string;
      mockExam9?: string;
      mockExam11?: string;
      midterm1?: string;
      final1?: string;
      midterm2?: string;
      final2?: string;
    };
  };
  learningHistory?: Array<{
    date: string;
    taskId: string;
    taskTitle: string;
    rounds: any[];
    summary: {
      totalRounds: number;
      totalWords: number;
      finalCompletedWords: number;
      completionRate: number;
    };
  }>;
}

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ pt: 2 }}>{children}</Box>}
    </div>
  );
}

const StudentPage: React.FC = () => {
  const { studentId } = useParams<{ studentId: string }>();
  const navigate = useNavigate();
  const [student, setStudent] = useState<Student | null>(null);
  const [selectedAssignment, setSelectedAssignment] = useState<TaskAssignment | null>(null);
  const [openDetailDialog, setOpenDetailDialog] = useState(false);
  const [openMobileDetail, setOpenMobileDetail] = useState(false);
  const [tabValue, setTabValue] = useState(0);
  const [showVocabularyLearning, setShowVocabularyLearning] = useState(false);
  const [timerDuration, setTimerDuration] = useState(() => {
    // 로컬 스토리지에서 저장된 타이머 시간 불러오기
    const saved = localStorage.getItem(`timer_duration_${studentId}`);
    return saved ? parseFloat(saved) : 1.5;
  });
  const [viewMode, setViewMode] = useState<'today' | 'week' | 'month'>('today');
  
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  useEffect(() => {
    if (studentId) {
      const students = loadStudents();
      const foundStudent = students.find(s => s.id === studentId);
      if (foundStudent) {
        setStudent(foundStudent);
      } else {
        // 학생을 찾을 수 없는 경우 관리자 페이지로 리다이렉트
        navigate('/students');
      }
    }
  }, [studentId, navigate]);

  const getAreaText = (area: string) => {
    switch (area) {
      case 'vocabulary': return '단어';
      case 'phrase': return '구문';
      case 'grammar': return '어법';
      case 'logic': return '논리';
      default: return area;
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'active': return '진행중';
      case 'completed': return '완료';
      case 'paused': return '일시정지';
      default: return status;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'success';
      case 'completed': return 'primary';
      case 'paused': return 'warning';
      default: return 'default';
    }
  };

  const getGradeText = (grade: string) => {
    return grade;
  };

  const getGradeColor = (grade: string) => {
    switch (grade) {
      case '초6':
        return 'warning';
      case '중1':
      case '중2':
      case '중3':
        return 'primary';
      case '고1':
      case '고2':
      case '고3':
        return 'success';
      default:
        return 'default';
    }
  };

  const handleOpenDetailDialog = (assignment: TaskAssignment) => {
    setSelectedAssignment(assignment);
    if (isMobile) {
      setOpenMobileDetail(true);
    } else {
      setOpenDetailDialog(true);
    }
  };

  const handleCloseDetailDialog = () => {
    setOpenDetailDialog(false);
    setOpenMobileDetail(false);
    setSelectedAssignment(null);
  };

  const handleStartVocabularyLearning = (assignment: TaskAssignment) => {
    setSelectedAssignment(assignment);
    // 타이머 설정 다이얼로그를 건너뛰고 바로 학습 시작
    setShowVocabularyLearning(true);
  };

  const handleCompleteVocabularyLearning = (results: any[]) => {
    // 학습 완료 처리 로직
    console.log('Vocabulary learning completed with results:', results);
    
    if (!student || !selectedAssignment) return;
    
    // 학습 결과 요약
    const totalRounds = results.length;
    const finalResult = results[results.length - 1];
    const totalWords = results[0].totalWords;
    
    // actualFinalCompletedWords가 있으면 사용, 없으면 기존 방식으로 계산
    let finalCompletedWords;
    if (finalResult.actualFinalCompletedWords !== undefined) {
      finalCompletedWords = finalResult.actualFinalCompletedWords;
    } else {
      // 1회차에서 완료한 단어 수
      const firstRoundCompleted = results.length > 1 
        ? totalWords - results[1].totalWords 
        : results[0].completedWords;
      
      // 마지막 회차에서 완료한 단어 수
      const lastRoundCompleted = results.length > 1
        ? finalResult.completedWords
        : 0;
      
      finalCompletedWords = firstRoundCompleted + lastRoundCompleted;
    }
    
    // 학습 기록 저장
    const learningRecord = {
      date: new Date().toISOString(),
      taskId: selectedAssignment.taskId,
      taskTitle: selectedAssignment.taskTitle,
      rounds: results,
      summary: {
        totalRounds,
        totalWords,
        finalCompletedWords,
        completionRate: Math.round((finalCompletedWords / totalWords) * 100),
      }
    };
    
    // 학생 정보 업데이트
    const updatedStudent = { ...student };
    
    // 학습 기록 배열이 없으면 생성
    if (!updatedStudent.learningHistory) {
      updatedStudent.learningHistory = [];
    }
    
    // 학습 기록 추가
    updatedStudent.learningHistory.push(learningRecord);
    
    // 과제 진행률 업데이트
    const assignmentIndex = updatedStudent.taskAssignments.findIndex(
      a => a.taskId === selectedAssignment.taskId
    );
    
    if (assignmentIndex !== -1) {
      // 완료된 단어 수만큼 진행률 증가
      updatedStudent.taskAssignments[assignmentIndex].progress.completed += finalCompletedWords;
      
      // 진행률이 전체를 초과하지 않도록 조정
      const totalProgress = updatedStudent.taskAssignments[assignmentIndex].progress.total;
      if (updatedStudent.taskAssignments[assignmentIndex].progress.completed > totalProgress) {
        updatedStudent.taskAssignments[assignmentIndex].progress.completed = totalProgress;
      }
    }
    
    // 학생 정보 저장
    const students = loadStudents();
    const studentIndex = students.findIndex(s => s.id === student.id);
    if (studentIndex !== -1) {
      students[studentIndex] = updatedStudent;
      saveStudents(students);
      setStudent(updatedStudent);
    }
    
    // 결과 알림
    alert(`학습이 완료되었습니다!\n\n총 ${totalRounds}회차 학습\n전체 ${totalWords}개 단어 중 ${finalCompletedWords}개 완료 (${learningRecord.summary.completionRate}%)`);
    
    setShowVocabularyLearning(false);
    setSelectedAssignment(null);
  };

  const handleCloseVocabularyLearning = () => {
    setShowVocabularyLearning(false);
    setSelectedAssignment(null);
  };

  const handleRestartLearning = () => {
    // 기존 과제 배정 정보에서 startUnit 확인
    const existingAssignment = student?.taskAssignments?.find(
      a => a.taskId === selectedAssignment?.taskId
    );
    const startUnit = existingAssignment?.startUnit || '';
    
    console.log('Restarting learning with:', {
      selectedAssignment,
      existingAssignment,
      startUnit
    });
    
    // 현재 과제 정보 가져오기
    const tasks = loadTasks();
    const targetTask = tasks.find((t: any) => 
      t.id === selectedAssignment?.taskId || t.title === selectedAssignment?.taskTitle
    );
    
    if (!targetTask) {
      console.error('Target task not found');
      alert('과제를 찾을 수 없습니다.');
      return;
    }
    
    console.log('Found target task:', targetTask);
    console.log('Task vocabularyData:', targetTask.vocabularyData);
    
    // 찾은 과제로 배정 생성
    const assignment: TaskAssignment = {
      taskId: targetTask.id,
      taskTitle: targetTask.title,
      area: targetTask.area,
      weeklySchedule: {
        '월': { isActive: true, dailyAmount: '5' },
        '화': { isActive: true, dailyAmount: '5' },
        '수': { isActive: true, dailyAmount: '5' },
        '목': { isActive: true, dailyAmount: '5' },
        '금': { isActive: true, dailyAmount: '5' },
        '토': { isActive: false, dailyAmount: '0' },
        '일': { isActive: false, dailyAmount: '0' },
      },
      startDate: new Date().toISOString().split('T')[0],
      status: 'active',
      progress: {
        completed: 0,
        total: targetTask.vocabularyData?.length || 0,
      },
      vocabularyData: targetTask.vocabularyData,
      startUnit: startUnit, // 기존 startUnit 유지
    };
    
    console.log('Created new assignment:', assignment);
    console.log('Assignment has vocabularyData:', !!assignment.vocabularyData);
    
    if (student) {
      const updatedStudent = {
        ...student,
        taskAssignments: [assignment],
      };
      
      // 로컬 스토리지에 저장
      const students = loadStudents();
      const updatedStudents = students.map(s => 
        s.id === student.id ? updatedStudent : s
      );
      saveStudents(updatedStudents);
      
      // 상태 업데이트
      setStudent(updatedStudent);
      console.log('Student updated with new assignment');
    }
  };

  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setTabValue(newValue);
  };

  const getTodayAssignments = () => {
    if (!student) return [];
    
    // 모든 활성 과제를 오늘의 과제로 표시 (단순화)
    return student.taskAssignments.filter(assignment => 
      assignment.status === 'active'
    );
  };

  const getWeekAssignments = () => {
    if (!student) return [];
    
    // 이번 주에 학습 예정인 모든 과제
    const today = new Date();
    const dayOfWeek = today.getDay();
    const weekStart = new Date(today);
    weekStart.setDate(today.getDate() - dayOfWeek);
    const weekEnd = new Date(weekStart);
    weekEnd.setDate(weekStart.getDate() + 6);
    
    return student.taskAssignments.filter(assignment => {
      if (assignment.status !== 'active') return false;
      
      // 주간 스케줄에서 이번 주에 활성화된 요일이 있는지 확인
      const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
      return Object.entries(assignment.weeklySchedule || {}).some(([day, schedule]) => 
        schedule.isActive
      );
    });
  };

  const getMonthAssignments = () => {
    if (!student) return [];
    
    // 이번 달에 활성화된 모든 과제
    return student.taskAssignments.filter(assignment => 
      assignment.status === 'active'
    );
  };

  const getActiveAssignments = () => {
    if (!student) return [];
    return student.taskAssignments.filter(assignment => assignment.status === 'active');
  };

  const getAssignmentsByViewMode = () => {
    switch (viewMode) {
      case 'week':
        return getWeekAssignments();
      case 'month':
        return getMonthAssignments();
      default:
        return getTodayAssignments();
    }
  };

  const getViewModeTitle = () => {
    switch (viewMode) {
      case 'week':
        return '이번 주 과제';
      case 'month':
        return '이번 달 과제';
      default:
        return '오늘의 과제';
    }
  };

  const getWeeklyGroupedAssignments = () => {
    if (viewMode !== 'week') return null;
    
    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
    const weeklyGroups: { [key: string]: TaskAssignment[] } = {};
    
    // 각 요일별로 빈 배열 초기화
    dayNames.forEach(day => {
      weeklyGroups[day] = [];
    });
    
    // 각 과제를 해당하는 요일에 추가
    viewAssignments.forEach(assignment => {
      dayNames.forEach(day => {
        if (assignment.weeklySchedule?.[day]?.isActive) {
          weeklyGroups[day].push(assignment);
        }
      });
    });
    
    return weeklyGroups;
  };

  if (!student) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Typography variant="h6">학생 정보를 불러오는 중...</Typography>
      </Box>
    );
  }

  const todayAssignments = getTodayAssignments();
  const activeAssignments = getActiveAssignments();
  const viewAssignments = getAssignmentsByViewMode();

  return (
    <Box sx={{ pb: isMobile ? 8 : 0 }}>
      {/* 모바일 앱바 */}
      {isMobile && (
        <AppBar position="sticky" elevation={1}>
          <Toolbar>
            <IconButton
              edge="start"
              color="inherit"
              onClick={() => navigate('/students')}
              sx={{ mr: 2 }}
            >
              <ArrowBackIcon />
            </IconButton>
            <Typography variant="h6" sx={{ flex: 1 }}>
              {student.name}
            </Typography>
            <Chip
              label={getGradeText(student.grade)}
              color={getGradeColor(student.grade) as any}
              size="small"
            />
          </Toolbar>
        </AppBar>
      )}

      <Container maxWidth="lg" sx={{ px: isMobile ? 2 : 3, py: isMobile ? 2 : 3 }}>
        {/* 데스크톱 학생 정보 헤더 */}
        {!isMobile && (
          <Box sx={{ mb: 4 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <Avatar sx={{ mr: 2, width: 64, height: 64 }}>
                {student.name.charAt(0)}
              </Avatar>
              <Box>
                <Typography variant="h4" component="h1">
                  {student.name}
                </Typography>
                <Box sx={{ display: 'flex', gap: 1, mt: 1 }}>
                  <Chip
                    label={getGradeText(student.grade)}
                    color={getGradeColor(student.grade) as any}
                    size="small"
                  />
                  <Chip
                    label={student.status === 'active' ? '활성' : '비활성'}
                    color={student.status === 'active' ? 'success' : 'error'}
                    size="small"
                  />
                </Box>
              </Box>
            </Box>
          </Box>
        )}

        {/* 모바일 학생 정보 카드 */}
        {isMobile && (
          <Card sx={{ mb: 3, borderRadius: 2 }}>
            <CardContent sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <Avatar sx={{ mr: 2, width: 48, height: 48 }}>
                  {student.name.charAt(0)}
                </Avatar>
                <Box sx={{ flex: 1 }}>
                  <Typography variant="h6">
                    {student.name}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {student.address}
                  </Typography>
                </Box>
              </Box>
              
              <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                <Chip
                  label={getGradeText(student.grade)}
                  color={getGradeColor(student.grade) as any}
                  size="small"
                />
                <Chip
                  label={student.status === 'active' ? '활성' : '비활성'}
                  color={student.status === 'active' ? 'success' : 'error'}
                  size="small"
                />
              </Box>

              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Box>
                  <Typography variant="body2" color="text.secondary">
                    연락처: {student.phone}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    가입일: {student.joinDate}
                  </Typography>
                </Box>
                <Box sx={{ textAlign: 'right' }}>
                  <Typography variant="h6" color="primary">
                    {student.taskAssignments.length}
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    배정된 과제
                  </Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        )}

        {/* 탭 네비게이션 */}
        <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
          <Tabs 
            value={tabValue} 
            onChange={handleTabChange}
            variant={isMobile ? "fullWidth" : "standard"}
            sx={{
              '& .MuiTab-root': {
                minHeight: isMobile ? 56 : 48,
                fontSize: isMobile ? '0.875rem' : '1rem',
              }
            }}
          >
            <Tab 
              icon={<TodayIcon />} 
              label="오늘의 과제" 
              iconPosition="start"
              sx={{ 
                '& .MuiTab-iconWrapper': { mr: 1 },
                '& .MuiTab-labelIcon': { flexDirection: 'row' }
              }}
            />
            <Tab 
              icon={<ViewModuleIcon />} 
              label="과제 현황" 
              iconPosition="start"
              sx={{ 
                '& .MuiTab-iconWrapper': { mr: 1 },
                '& .MuiTab-labelIcon': { flexDirection: 'row' }
              }}
            />
            <Tab 
              icon={<SettingsIcon />} 
              label="설정" 
              iconPosition="start"
              sx={{ 
                '& .MuiTab-iconWrapper': { mr: 1 },
                '& .MuiTab-labelIcon': { flexDirection: 'row' }
              }}
            />
          </Tabs>
        </Box>

        {/* 오늘의 과제 탭 */}
        <TabPanel value={tabValue} index={0}>
          <Box sx={{ mb: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2, flexWrap: 'wrap', gap: 2 }}>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <TodayIcon sx={{ mr: 1, color: 'primary.main' }} />
                <Typography variant={isMobile ? "h6" : "h5"} sx={{ fontWeight: 600 }}>
                  {getViewModeTitle()} ({viewAssignments.length}개)
                </Typography>
              </Box>
              
              <ToggleButtonGroup
                value={viewMode}
                exclusive
                onChange={(event, newMode) => {
                  if (newMode !== null) {
                    setViewMode(newMode);
                  }
                }}
                size={isMobile ? "small" : "medium"}
              >
                <ToggleButton value="today">
                  <TodayIcon sx={{ mr: 0.5 }} />
                  오늘
                </ToggleButton>
                <ToggleButton value="week">
                  <DateRangeIcon sx={{ mr: 0.5 }} />
                  주간
                </ToggleButton>
                <ToggleButton value="month">
                  <CalendarMonthIcon sx={{ mr: 0.5 }} />
                  월간
                </ToggleButton>
              </ToggleButtonGroup>
            </Box>
            
            {viewAssignments.length > 0 ? (
              viewMode === 'week' ? (
                // 주간 보기: 요일별로 그룹화
                <Box>
                  {(() => {
                    const weeklyGroups = getWeeklyGroupedAssignments();
                    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
                    const today = new Date().getDay();
                    
                    return dayNames.map((day, index) => {
                      const assignments = weeklyGroups?.[day] || [];
                      const isToday = index === today;
                      
                      return (
                        <Box key={day} sx={{ mb: 3 }}>
                          <Box sx={{ 
                            display: 'flex', 
                            alignItems: 'center', 
                            mb: 2,
                            p: 1,
                            bgcolor: isToday ? 'primary.50' : 'transparent',
                            borderRadius: 1
                          }}>
                            <Typography 
                              variant="h6" 
                              sx={{ 
                                fontWeight: isToday ? 700 : 600,
                                color: isToday ? 'primary.main' : 'text.primary'
                              }}
                            >
                              {day}요일
                            </Typography>
                            {isToday && (
                              <Chip 
                                label="오늘" 
                                size="small" 
                                color="primary" 
                                sx={{ ml: 1 }}
                              />
                            )}
                            <Typography variant="body2" color="text.secondary" sx={{ ml: 'auto' }}>
                              {assignments.length}개 과제
                            </Typography>
                          </Box>
                          
                          {assignments.length > 0 ? (
                            <Grid container spacing={2}>
                              {assignments.map((assignment, idx) => (
                                <Grid item xs={12} key={`${day}-${idx}`}>
                                  <Card sx={{ 
                                    borderRadius: 1,
                                    border: isToday ? '2px solid' : '1px solid',
                                    borderColor: isToday ? 'primary.main' : 'grey.300',
                                    backgroundColor: isToday ? 'primary.50' : 'background.paper'
                                  }}>
                                    <CardContent sx={{ p: 2 }}>
                                      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                        <Box>
                                          <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                                            {assignment.taskTitle}
                                          </Typography>
                                          <Typography variant="body2" color="text.secondary">
                                            {getAreaText(assignment.area)} - {assignment.weeklySchedule?.[day]?.dailyAmount || '0'}개
                                          </Typography>
                                        </Box>
                                        <Box sx={{ display: 'flex', gap: 1 }}>
                                          <Button
                                            size="small"
                                            variant="contained"
                                            onClick={() => handleStartVocabularyLearning(assignment)}
                                          >
                                            학습
                                          </Button>
                                          <Button
                                            size="small"
                                            variant="outlined"
                                            onClick={() => handleOpenDetailDialog(assignment)}
                                          >
                                            상세
                                          </Button>
                                        </Box>
                                      </Box>
                                    </CardContent>
                                  </Card>
                                </Grid>
                              ))}
                            </Grid>
                          ) : (
                            <Typography variant="body2" color="text.secondary" sx={{ ml: 2 }}>
                              과제 없음
                            </Typography>
                          )}
                        </Box>
                      );
                    });
                  })()}
                </Box>
              ) : (
                // 오늘/월간 보기: 기존 방식
                <Grid container spacing={isMobile ? 2 : 3}>
                  {viewAssignments.map((assignment, index) => (
                    <Grid item xs={12} key={index}>
                    <Card 
                      sx={{ 
                        borderRadius: isMobile ? 2 : 1,
                        border: '2px solid',
                        borderColor: 'primary.main',
                        backgroundColor: 'primary.50',
                        '&:hover': { 
                          boxShadow: 3,
                          transform: 'translateY(-2px)',
                          transition: 'all 0.2s ease-in-out'
                        }
                      }}
                    >
                      <CardContent sx={{ p: isMobile ? 3 : 2 }}>
                        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
                          <Box sx={{ flex: 1 }}>
                            <Typography variant="h6" sx={{ mb: 1, fontWeight: 600 }}>
                              {assignment.taskTitle}
                            </Typography>
                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                              <AssignmentIcon sx={{ fontSize: 16, mr: 0.5, color: 'primary.main' }} />
                              <Typography variant="body2" color="primary" sx={{ fontWeight: 500 }}>
                                {getAreaText(assignment.area)} 영역
                              </Typography>
                            </Box>
                          </Box>
                          <Chip
                            label={viewMode === 'today' ? '오늘 학습' : viewMode === 'week' ? '주간 학습' : '월간 학습'}
                            color="primary"
                            size="small"
                            sx={{ fontWeight: 600 }}
                          />
                        </Box>

                        {/* 진행률 */}
                        <Box sx={{ mb: 3 }}>
                          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                            <Typography variant="body2" sx={{ fontWeight: 500 }}>
                              진행률
                            </Typography>
                            <Typography variant="body2" color="primary" sx={{ fontWeight: 600 }}>
                              {assignment.progress.completed} / {assignment.progress.total}
                            </Typography>
                          </Box>
                          <LinearProgress
                            variant="determinate"
                            value={(assignment.progress.completed / assignment.progress.total) * 100}
                            sx={{ 
                              height: 8, 
                              borderRadius: 4,
                              backgroundColor: 'grey.200',
                              '& .MuiLinearProgress-bar': {
                                borderRadius: 4,
                              }
                            }}
                          />
                        </Box>

                        {/* 오늘 학습량 */}
                        <Box sx={{ mb: 2 }}>
                          <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                            오늘 학습량:
                          </Typography>
                          <Typography variant="h6" color="primary" sx={{ fontWeight: 600 }}>
                            {assignment.weeklySchedule[['일', '월', '화', '수', '목', '금', '토'][new Date().getDay()]]?.dailyAmount || '미정'}
                          </Typography>
                        </Box>

                        {/* 액션 버튼 */}
                        <Box sx={{ display: 'flex', gap: 1, mt: 2 }}>
                          <Button
                            variant="contained"
                            startIcon={<PlayArrowIcon />}
                            size={isMobile ? "large" : "medium"}
                            sx={{ flex: 1 }}
                            onClick={() => handleStartVocabularyLearning(assignment)}
                          >
                            학습 시작
                          </Button>
                          <Button
                            variant="outlined"
                            size={isMobile ? "large" : "medium"}
                            onClick={() => handleOpenDetailDialog(assignment)}
                          >
                            상세보기
                          </Button>
                        </Box>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
              )
            ) : (
              <Card sx={{ borderRadius: isMobile ? 2 : 1, textAlign: 'center' }}>
                <CardContent sx={{ py: isMobile ? 6 : 4 }}>
                  <CheckCircleIcon sx={{ 
                    fontSize: isMobile ? 80 : 64, 
                    color: 'success.main', 
                    mb: 2 
                  }} />
                  <Typography variant={isMobile ? "h6" : "h6"} color="success.main" sx={{ mb: 1, fontWeight: 600 }}>
                    {viewMode === 'today' ? '오늘의 과제 완료!' : viewMode === 'week' ? '이번 주 과제가 없습니다' : '이번 달 과제가 없습니다'}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
                    {viewMode === 'today' 
                      ? '오늘 학습할 과제가 없습니다.'
                      : viewMode === 'week'
                      ? '이번 주에 배정된 과제가 없습니다.'
                      : '이번 달에 배정된 과제가 없습니다.'}
                  </Typography>
                  
                  {/* 다시 시작 버튼 */}
                  <Button
                    variant="contained"
                    color="primary"
                    size={isMobile ? "large" : "medium"}
                    onClick={handleRestartLearning}
                    startIcon={<RefreshIcon />}
                    sx={{
                      borderRadius: 2,
                      px: 3,
                      py: 1.5,
                      fontWeight: 600,
                      textTransform: 'none',
                      boxShadow: 2,
                      '&:hover': {
                        boxShadow: 4,
                        transform: 'translateY(-1px)',
                        transition: 'all 0.2s ease-in-out'
                      }
                    }}
                  >
                    학습 시작하기
                  </Button>
                </CardContent>
              </Card>
            )}
          </Box>
        </TabPanel>

        {/* 과제 현황 탭 */}
        <TabPanel value={tabValue} index={1}>
          <Box sx={{ mb: 3 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <ViewModuleIcon sx={{ mr: 1, color: 'primary.main' }} />
              <Typography variant={isMobile ? "h6" : "h5"} sx={{ fontWeight: 600 }}>
                과제 현황 ({activeAssignments.length}개)
              </Typography>
            </Box>

            {activeAssignments.length > 0 ? (
              <Grid container spacing={isMobile ? 2 : 3}>
                {activeAssignments.map((assignment, index) => (
                  <Grid item xs={12} sm={6} md={4} key={index}>
                    <Card 
                      sx={{ 
                        cursor: 'pointer',
                        borderRadius: isMobile ? 2 : 1,
                        '&:hover': { 
                          boxShadow: isMobile ? 2 : 3,
                          transform: isMobile ? 'none' : 'translateY(-2px)',
                          transition: 'all 0.2s ease-in-out'
                        },
                        minHeight: isMobile ? 200 : 'auto'
                      }}
                      onClick={() => handleOpenDetailDialog(assignment)}
                    >
                      <CardContent sx={{ p: isMobile ? 3 : 2 }}>
                        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
                          <Typography 
                            variant={isMobile ? "h6" : "h6"} 
                            sx={{ 
                              flex: 1, 
                              fontSize: isMobile ? '1rem' : '1.25rem',
                              lineHeight: 1.3
                            }}
                          >
                            {assignment.taskTitle}
                          </Typography>
                          <Chip
                            label={getStatusText(assignment.status)}
                            color={getStatusColor(assignment.status) as any}
                            size="small"
                            sx={{ ml: 1 }}
                          />
                        </Box>

                        <Box sx={{ mb: 2 }}>
                          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                            <AssignmentIcon sx={{ fontSize: 16, mr: 0.5, color: 'primary.main' }} />
                            <Typography variant="body2" color="primary" sx={{ fontWeight: 500 }}>
                              {getAreaText(assignment.area)} 영역
                            </Typography>
                          </Box>
                          
                          {/* 진행률 */}
                          <Box sx={{ mb: 2 }}>
                            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                              <Typography variant="body2" sx={{ fontWeight: 500 }}>
                                진행률
                              </Typography>
                              <Typography variant="body2" color="primary" sx={{ fontWeight: 600 }}>
                                {assignment.progress.completed} / {assignment.progress.total}
                              </Typography>
                            </Box>
                            <LinearProgress
                              variant="determinate"
                              value={(assignment.progress.completed / assignment.progress.total) * 100}
                              sx={{ 
                                height: isMobile ? 8 : 6, 
                                borderRadius: 4,
                                backgroundColor: 'grey.200',
                                '& .MuiLinearProgress-bar': {
                                  borderRadius: 4,
                                }
                              }}
                            />
                          </Box>

                          {/* 시작 단원 (단어장인 경우) */}
                          {assignment.area === 'vocabulary' && assignment.startUnit && (
                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                              <InfoIcon sx={{ fontSize: 16, mr: 0.5, color: 'text.secondary' }} />
                              <Typography variant="body2" color="text.secondary">
                                시작 단원: {assignment.startUnit}
                              </Typography>
                            </Box>
                          )}

                          {/* 스케줄 요약 */}
                          <Box sx={{ mt: 2 }}>
                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                              <CalendarIcon sx={{ fontSize: 16, mr: 0.5, color: 'text.secondary' }} />
                              <Typography variant="body2" color="text.secondary">
                                학습 요일:
                              </Typography>
                            </Box>
                            <Box sx={{ display: 'flex', gap: 0.5, flexWrap: 'wrap' }}>
                              {Object.entries(assignment.weeklySchedule)
                                .filter(([_, schedule]) => schedule.isActive)
                                .map(([day, schedule]) => (
                                  <Chip
                                    key={day}
                                    label={`${day} ${schedule.dailyAmount}`}
                                    size="small"
                                    variant="outlined"
                                    sx={{ fontSize: '0.75rem' }}
                                  />
                                ))}
                            </Box>
                          </Box>
                        </Box>

                        <Box sx={{ 
                          display: 'flex', 
                          justifyContent: 'space-between', 
                          alignItems: 'center',
                          mt: 'auto'
                        }}>
                          <Typography variant="caption" color="text.secondary">
                            시작: {assignment.startDate}
                          </Typography>
                          <Button
                            size="small"
                            variant="outlined"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleOpenDetailDialog(assignment);
                            }}
                            sx={{ 
                              minWidth: 'auto',
                              px: isMobile ? 2 : 1.5,
                              py: isMobile ? 1 : 0.5
                            }}
                          >
                            상세보기
                          </Button>
                        </Box>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            ) : (
              <Card sx={{ borderRadius: isMobile ? 2 : 1 }}>
                <CardContent sx={{ 
                  textAlign: 'center', 
                  py: isMobile ? 6 : 4,
                  px: isMobile ? 3 : 2
                }}>
                  <AssignmentIcon sx={{ 
                    fontSize: isMobile ? 80 : 64, 
                    color: 'text.secondary', 
                    mb: 2 
                  }} />
                  <Typography variant={isMobile ? "h6" : "h6"} color="text.secondary" sx={{ mb: 1 }}>
                    활성 과제가 없습니다
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    관리자에게 과제 배정을 요청하세요.
                  </Typography>
                </CardContent>
              </Card>
            )}
          </Box>
        </TabPanel>

        {/* 설정 탭 */}
        <TabPanel value={tabValue} index={2}>
          <Box sx={{ mb: 3 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
              <SettingsIcon sx={{ mr: 1, color: 'primary.main' }} />
              <Typography variant={isMobile ? "h6" : "h5"} sx={{ fontWeight: 600 }}>
                학습 설정
              </Typography>
            </Box>

            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <Card sx={{ borderRadius: isMobile ? 2 : 1 }}>
                  <CardContent sx={{ p: isMobile ? 3 : 2 }}>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                      <TimerIcon sx={{ mr: 1, color: 'primary.main' }} />
                      <Typography variant="h6" sx={{ fontWeight: 600 }}>
                        단어 학습 타이머 설정
                      </Typography>
                    </Box>
                    
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
                      단어의 뜻을 확인한 후 다음 단어로 넘어가는 시간을 설정합니다.
                    </Typography>

                    <Box sx={{ px: 2, mb: 3 }}>
                      <Slider
                        value={timerDuration}
                        onChange={(e, value) => setTimerDuration(value as number)}
                        min={0.5}
                        max={5}
                        step={0.5}
                        marks={[
                          { value: 0.5, label: '0.5초' },
                          { value: 1.5, label: '1.5초' },
                          { value: 3, label: '3초' },
                          { value: 5, label: '5초' },
                        ]}
                        valueLabelDisplay="on"
                        valueLabelFormat={(value) => `${value}초`}
                      />
                    </Box>

                    <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                      <Typography variant="body2" color="text.secondary">
                        현재 설정: {timerDuration}초
                      </Typography>
                      <Button
                        variant="contained"
                        onClick={() => {
                          localStorage.setItem(`timer_duration_${studentId}`, timerDuration.toString());
                          alert('설정이 저장되었습니다.');
                        }}
                      >
                        저장
                      </Button>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>
          </Box>
        </TabPanel>

        {/* 데스크톱 과제 상세 다이얼로그 */}
        <Dialog open={openDetailDialog} onClose={handleCloseDetailDialog} maxWidth="md" fullWidth>
          <DialogTitle>
            {selectedAssignment?.taskTitle} - 상세 정보
          </DialogTitle>
          <DialogContent>
            {selectedAssignment && (
              <Box sx={{ pt: 2 }}>
                <Grid container spacing={3}>
                  <Grid item xs={12} md={6}>
                    <Typography variant="h6" sx={{ mb: 2 }}>
                      기본 정보
                    </Typography>
                    <Box sx={{ mb: 2 }}>
                      <Typography variant="body2" color="text.secondary">
                        영역: {getAreaText(selectedAssignment.area)}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        상태: {getStatusText(selectedAssignment.status)}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        시작일: {selectedAssignment.startDate}
                      </Typography>
                      {selectedAssignment.endDate && (
                        <Typography variant="body2" color="text.secondary">
                          종료일: {selectedAssignment.endDate}
                        </Typography>
                      )}
                      {selectedAssignment.area === 'vocabulary' && selectedAssignment.startUnit && (
                        <Typography variant="body2" color="text.secondary">
                          시작 단원: {selectedAssignment.startUnit}
                        </Typography>
                      )}
                    </Box>

                    <Typography variant="h6" sx={{ mb: 2 }}>
                      진행률
                    </Typography>
                    <Box sx={{ mb: 2 }}>
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                        <Typography variant="body2">
                          {selectedAssignment.progress.completed} / {selectedAssignment.progress.total} 완료
                        </Typography>
                        <Typography variant="body2">
                          {Math.round((selectedAssignment.progress.completed / selectedAssignment.progress.total) * 100)}%
                        </Typography>
                      </Box>
                      <LinearProgress
                        variant="determinate"
                        value={(selectedAssignment.progress.completed / selectedAssignment.progress.total) * 100}
                        sx={{ height: 12, borderRadius: 6 }}
                      />
                    </Box>
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <Typography variant="h6" sx={{ mb: 2 }}>
                      주간 스케줄
                    </Typography>
                    <TableContainer component={Paper} variant="outlined">
                      <Table size="small">
                        <TableHead>
                          <TableRow>
                            <TableCell>요일</TableCell>
                            <TableCell>학습량</TableCell>
                            <TableCell>상태</TableCell>
                          </TableRow>
                        </TableHead>
                        <TableBody>
                          {['월', '화', '수', '목', '금', '토', '일'].map((day) => {
                            const schedule = selectedAssignment.weeklySchedule[day];
                            return (
                              <TableRow key={day}>
                                <TableCell>{day}</TableCell>
                                <TableCell>
                                  {schedule?.isActive ? schedule.dailyAmount : '-'}
                                </TableCell>
                                <TableCell>
                                  {schedule?.isActive ? (
                                    <Chip label="활성" color="success" size="small" />
                                  ) : (
                                    <Chip label="비활성" color="default" size="small" />
                                  )}
                                </TableCell>
                              </TableRow>
                            );
                          })}
                        </TableBody>
                      </Table>
                    </TableContainer>
                  </Grid>
                </Grid>
              </Box>
            )}
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseDetailDialog}>닫기</Button>
          </DialogActions>
        </Dialog>

        {/* 모바일 과제 상세 바텀시트 */}
        <Drawer
          anchor="bottom"
          open={openMobileDetail}
          onClose={handleCloseDetailDialog}
          PaperProps={{
            sx: {
              borderTopLeftRadius: 16,
              borderTopRightRadius: 16,
              maxHeight: '80vh',
              minHeight: '50vh',
            }
          }}
        >
          {selectedAssignment && (
            <Box sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                <Typography variant="h6" sx={{ fontWeight: 600 }}>
                  {selectedAssignment.taskTitle}
                </Typography>
                <IconButton onClick={handleCloseDetailDialog}>
                  <CloseIcon />
                </IconButton>
              </Box>

              <List sx={{ p: 0 }}>
                <ListItem sx={{ px: 0 }}>
                  <ListItemIcon>
                    <AssignmentIcon color="primary" />
                  </ListItemIcon>
                  <ListItemText
                    primary="영역"
                    secondary={getAreaText(selectedAssignment.area)}
                  />
                </ListItem>

                <ListItem sx={{ px: 0 }}>
                  <ListItemIcon>
                    <TrendingUpIcon color="primary" />
                  </ListItemIcon>
                  <ListItemText
                    primary="상태"
                    secondary={getStatusText(selectedAssignment.status)}
                  />
                </ListItem>

                <ListItem sx={{ px: 0 }}>
                  <ListItemIcon>
                    <CalendarIcon color="primary" />
                  </ListItemIcon>
                  <ListItemText
                    primary="시작일"
                    secondary={selectedAssignment.startDate}
                  />
                </ListItem>

                {selectedAssignment.endDate && (
                  <ListItem sx={{ px: 0 }}>
                    <ListItemIcon>
                      <CheckCircleIcon color="primary" />
                    </ListItemIcon>
                    <ListItemText
                      primary="종료일"
                      secondary={selectedAssignment.endDate}
                    />
                  </ListItem>
                )}

                {selectedAssignment.area === 'vocabulary' && selectedAssignment.startUnit && (
                  <ListItem sx={{ px: 0 }}>
                    <ListItemIcon>
                      <InfoIcon color="primary" />
                    </ListItemIcon>
                    <ListItemText
                      primary="시작 단원"
                      secondary={selectedAssignment.startUnit}
                    />
                  </ListItem>
                )}
              </List>

              <Divider sx={{ my: 2 }} />

              <Typography variant="h6" sx={{ mb: 2, fontWeight: 600 }}>
                진행률
              </Typography>
              <Box sx={{ mb: 2 }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                  <Typography variant="body2">
                    {selectedAssignment.progress.completed} / {selectedAssignment.progress.total} 완료
                  </Typography>
                  <Typography variant="body2" color="primary" sx={{ fontWeight: 600 }}>
                    {Math.round((selectedAssignment.progress.completed / selectedAssignment.progress.total) * 100)}%
                  </Typography>
                </Box>
                <LinearProgress
                  variant="determinate"
                  value={(selectedAssignment.progress.completed / selectedAssignment.progress.total) * 100}
                  sx={{ height: 8, borderRadius: 4 }}
                />
              </Box>

              <Typography variant="h6" sx={{ mb: 2, fontWeight: 600 }}>
                주간 스케줄
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                {['월', '화', '수', '목', '금', '토', '일'].map((day) => {
                  const schedule = selectedAssignment.weeklySchedule[day];
                  return (
                    <Box
                      key={day}
                      sx={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                        p: 2,
                        borderRadius: 1,
                        backgroundColor: schedule?.isActive ? 'primary.50' : 'grey.50',
                        border: schedule?.isActive ? '1px solid' : '1px solid',
                        borderColor: schedule?.isActive ? 'primary.200' : 'grey.200',
                      }}
                    >
                      <Typography variant="body1" sx={{ fontWeight: 500 }}>
                        {day}요일
                      </Typography>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <Typography variant="body2" color="text.secondary">
                          {schedule?.isActive ? schedule.dailyAmount : '학습 없음'}
                        </Typography>
                        <Chip
                          label={schedule?.isActive ? '활성' : '비활성'}
                          color={schedule?.isActive ? 'success' : 'default'}
                          size="small"
                        />
                      </Box>
                    </Box>
                  );
                })}
              </Box>
            </Box>
          )}
        </Drawer>



        {/* 단어 학습 컴포넌트 */}
        {showVocabularyLearning && selectedAssignment && (
          <VocabularyLearning
            assignment={selectedAssignment}
            onComplete={handleCompleteVocabularyLearning}
            onClose={handleCloseVocabularyLearning}
            timerDuration={timerDuration}
          />
        )}
      </Container>
    </Box>
  );
};

export default StudentPage;
