  Card,
  CardContent,
  Grid,
  Chip,
  LinearProgress,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  Avatar,
  useTheme,
  useMediaQuery,
  Fab,
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  AppBar,
  Toolbar,
  Container,
} from '@mui/material';
import {
  Assignment as AssignmentIcon,
  Schedule as ScheduleIcon,
  CheckCircle as CheckCircleIcon,
  PlayArrow as PlayArrowIcon,
  Pause as PauseIcon,
  Stop as StopIcon,
  ArrowBack as ArrowBackIcon,
  Info as InfoIcon,
  CalendarToday as CalendarIcon,
  TrendingUp as TrendingUpIcon,
  Close as CloseIcon,
} from '@mui/icons-material';
import { useParams, useNavigate } from 'react-router-dom';
import { loadStudents } from '../utils/storage';

interface TaskAssignment {
  taskId: string;
  taskTitle: string;
  area: 'vocabulary' | 'phrase' | 'grammar' | 'logic';
  weeklySchedule: {
    [day: string]: {
      isActive: boolean;
      dailyAmount: string;
    };
  };
  startDate: string;
  endDate?: string;
  status: 'active' | 'completed' | 'paused';
  progress: {
    completed: number;
    total: number;
  };
  startUnit?: string;
}

interface Student {
  id: string;
  name: string;
  address: string;
  phone: string;
  grade: '초6' | '중1' | '중2' | '중3' | '고1' | '고2' | '고3';
  joinDate: string;
  status: 'active' | 'inactive';
  assignedTasks: string[];
  taskAssignments: TaskAssignment[];
  grades: {
    [year: string]: {
      mockExam3?: string;
      mockExam6?: string;
      mockExam9?: string;
      mockExam11?: string;
      midterm1?: string;
      final1?: string;
      midterm2?: string;
      final2?: string;
    };
  };
}

const StudentPage: React.FC = () => {
  const { studentId } = useParams<{ studentId: string }>();
  const navigate = useNavigate();
  const [student, setStudent] = useState<Student | null>(null);
  const [selectedAssignment, setSelectedAssignment] = useState<TaskAssignment | null>(null);
  const [openDetailDialog, setOpenDetailDialog] = useState(false);
  const [openMobileDetail, setOpenMobileDetail] = useState(false);
  
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  useEffect(() => {
    if (studentId) {
      const students = loadStudents();
      const foundStudent = students.find(s => s.id === studentId);
      if (foundStudent) {
        setStudent(foundStudent);
      } else {
        // 학생을 찾을 수 없는 경우 관리자 페이지로 리다이렉트
        navigate('/students');
      }
    }
  }, [studentId, navigate]);

  const getAreaText = (area: string) => {
    switch (area) {
      case 'vocabulary': return '단어';
      case 'phrase': return '구문';
      case 'grammar': return '어법';
      case 'logic': return '논리';
      default: return area;
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'active': return '진행중';
      case 'completed': return '완료';
      case 'paused': return '일시정지';
      default: return status;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'success';
      case 'completed': return 'primary';
      case 'paused': return 'warning';
      default: return 'default';
    }
  };

  const getGradeText = (grade: string) => {
    return grade;
  };

  const getGradeColor = (grade: string) => {
    switch (grade) {
      case '초6':
        return 'warning';
      case '중1':
      case '중2':
      case '중3':
        return 'primary';
      case '고1':
      case '고2':
      case '고3':
        return 'success';
      default:
        return 'default';
    }
  };

  const handleOpenDetailDialog = (assignment: TaskAssignment) => {
    setSelectedAssignment(assignment);
    if (isMobile) {
      setOpenMobileDetail(true);
    } else {
      setOpenDetailDialog(true);
    }
  };

  const handleCloseDetailDialog = () => {
    setOpenDetailDialog(false);
    setOpenMobileDetail(false);
    setSelectedAssignment(null);
  };

  if (!student) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Typography variant="h6">학생 정보를 불러오는 중...</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ pb: isMobile ? 8 : 0 }}>
      {/* 모바일 앱바 */}
      {isMobile && (
        <AppBar position="sticky" elevation={1}>
          <Toolbar>
            <IconButton
              edge="start"
              color="inherit"
              onClick={() => navigate('/students')}
              sx={{ mr: 2 }}
            >
              <ArrowBackIcon />
            </IconButton>
            <Typography variant="h6" sx={{ flex: 1 }}>
              {student.name}
            </Typography>
            <Chip
              label={getGradeText(student.grade)}
              color={getGradeColor(student.grade) as any}
              size="small"
            />
          </Toolbar>
        </AppBar>
      )}

      <Container maxWidth="lg" sx={{ px: isMobile ? 2 : 3, py: isMobile ? 2 : 3 }}>
        {/* 데스크톱 학생 정보 헤더 */}
        {!isMobile && (
          <Box sx={{ mb: 4 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <Avatar sx={{ mr: 2, width: 64, height: 64 }}>
                {student.name.charAt(0)}
              </Avatar>
              <Box>
                <Typography variant="h4" component="h1">
                  {student.name}
                </Typography>
                <Box sx={{ display: 'flex', gap: 1, mt: 1 }}>
                  <Chip
                    label={getGradeText(student.grade)}
                    color={getGradeColor(student.grade) as any}
                    size="small"
                  />
                  <Chip
                    label={student.status === 'active' ? '활성' : '비활성'}
                    color={student.status === 'active' ? 'success' : 'error'}
                    size="small"
                  />
                </Box>
              </Box>
            </Box>
            
            <Grid container spacing={2}>
              <Grid item xs={12} md={6}>
                <Typography variant="body2" color="text.secondary">
                  <strong>주소:</strong> {student.address}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  <strong>연락처:</strong> {student.phone}
                </Typography>
              </Grid>
              <Grid item xs={12} md={6}>
                <Typography variant="body2" color="text.secondary">
                  <strong>가입일:</strong> {student.joinDate}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  <strong>배정된 과제:</strong> {student.taskAssignments.length}개
                </Typography>
              </Grid>
            </Grid>
          </Box>
        )}

        {/* 모바일 학생 정보 카드 */}
        {isMobile && (
          <Card sx={{ mb: 3, borderRadius: 2 }}>
            <CardContent sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <Avatar sx={{ mr: 2, width: 48, height: 48 }}>
                  {student.name.charAt(0)}
                </Avatar>
                <Box sx={{ flex: 1 }}>
                  <Typography variant="h6">
                    {student.name}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {student.address}
                  </Typography>
                </Box>
              </Box>
              
              <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                <Chip
                  label={getGradeText(student.grade)}
                  color={getGradeColor(student.grade) as any}
                  size="small"
                />
                <Chip
                  label={student.status === 'active' ? '활성' : '비활성'}
                  color={student.status === 'active' ? 'success' : 'error'}
                  size="small"
                />
              </Box>

              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Box>
                  <Typography variant="body2" color="text.secondary">
                    연락처: {student.phone}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    가입일: {student.joinDate}
                  </Typography>
                </Box>
                <Box sx={{ textAlign: 'right' }}>
                  <Typography variant="h6" color="primary">
                    {student.taskAssignments.length}
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    배정된 과제
                  </Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        )}

        {/* 과제 현황 */}
        <Typography variant={isMobile ? "h6" : "h5"} sx={{ mb: 3, fontWeight: 600 }}>
          과제 현황
        </Typography>

        {student.taskAssignments.length > 0 ? (
          <Grid container spacing={isMobile ? 2 : 3}>
            {student.taskAssignments.map((assignment, index) => (
              <Grid item xs={12} sm={6} md={4} key={index}>
                <Card 
                  sx={{ 
                    cursor: 'pointer',
                    borderRadius: isMobile ? 2 : 1,
                    '&:hover': { 
                      boxShadow: isMobile ? 2 : 3,
                      transform: isMobile ? 'none' : 'translateY(-2px)',
                      transition: 'all 0.2s ease-in-out'
                    },
                    minHeight: isMobile ? 200 : 'auto'
                  }}
                  onClick={() => handleOpenDetailDialog(assignment)}
                >
                  <CardContent sx={{ p: isMobile ? 3 : 2 }}>
                    <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
                      <Typography 
                        variant={isMobile ? "h6" : "h6"} 
                        sx={{ 
                          flex: 1, 
                          fontSize: isMobile ? '1rem' : '1.25rem',
                          lineHeight: 1.3
                        }}
                      >
                        {assignment.taskTitle}
                      </Typography>
                      <Chip
                        label={getStatusText(assignment.status)}
                        color={getStatusColor(assignment.status) as any}
                        size="small"
                        sx={{ ml: 1 }}
                      />
                    </Box>

                    <Box sx={{ mb: 2 }}>
                      <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                        <AssignmentIcon sx={{ fontSize: 16, mr: 0.5, color: 'primary.main' }} />
                        <Typography variant="body2" color="primary" sx={{ fontWeight: 500 }}>
                          {getAreaText(assignment.area)} 영역
                        </Typography>
                      </Box>
                      
                      {/* 진행률 */}
                      <Box sx={{ mb: 2 }}>
                        <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                          <Typography variant="body2" sx={{ fontWeight: 500 }}>
                            진행률
                          </Typography>
                          <Typography variant="body2" color="primary" sx={{ fontWeight: 600 }}>
                            {assignment.progress.completed} / {assignment.progress.total}
                          </Typography>
                        </Box>
                        <LinearProgress
                          variant="determinate"
                          value={(assignment.progress.completed / assignment.progress.total) * 100}
                          sx={{ 
                            height: isMobile ? 8 : 6, 
                            borderRadius: 4,
                            backgroundColor: 'grey.200',
                            '& .MuiLinearProgress-bar': {
                              borderRadius: 4,
                            }
                          }}
                        />
                      </Box>

                      {/* 시작 단원 (단어장인 경우) */}
                      {assignment.area === 'vocabulary' && assignment.startUnit && (
                        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                          <InfoIcon sx={{ fontSize: 16, mr: 0.5, color: 'text.secondary' }} />
                          <Typography variant="body2" color="text.secondary">
                            시작 단원: {assignment.startUnit}
                          </Typography>
                        </Box>
                      )}

                      {/* 스케줄 요약 */}
                      <Box sx={{ mt: 2 }}>
                        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                          <CalendarIcon sx={{ fontSize: 16, mr: 0.5, color: 'text.secondary' }} />
                          <Typography variant="body2" color="text.secondary">
                            학습 요일:
                          </Typography>
                        </Box>
                        <Box sx={{ display: 'flex', gap: 0.5, flexWrap: 'wrap' }}>
                          {Object.entries(assignment.weeklySchedule)
                            .filter(([_, schedule]) => schedule.isActive)
                            .map(([day, schedule]) => (
                              <Chip
                                key={day}
                                label={`${day} ${schedule.dailyAmount}`}
                                size="small"
                                variant="outlined"
                                sx={{ fontSize: '0.75rem' }}
                              />
                            ))}
                        </Box>
                      </Box>
                    </Box>

                    <Box sx={{ 
                      display: 'flex', 
                      justifyContent: 'space-between', 
                      alignItems: 'center',
                      mt: 'auto'
                    }}>
                      <Typography variant="caption" color="text.secondary">
                        시작: {assignment.startDate}
                      </Typography>
                      <Button
                        size="small"
                        variant="outlined"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleOpenDetailDialog(assignment);
                        }}
                        sx={{ 
                          minWidth: 'auto',
                          px: isMobile ? 2 : 1.5,
                          py: isMobile ? 1 : 0.5
                        }}
                      >
                        상세보기
                      </Button>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        ) : (
          <Card sx={{ borderRadius: isMobile ? 2 : 1 }}>
            <CardContent sx={{ 
              textAlign: 'center', 
              py: isMobile ? 6 : 4,
              px: isMobile ? 3 : 2
            }}>
              <AssignmentIcon sx={{ 
                fontSize: isMobile ? 80 : 64, 
                color: 'text.secondary', 
                mb: 2 
              }} />
              <Typography variant={isMobile ? "h6" : "h6"} color="text.secondary" sx={{ mb: 1 }}>
                배정된 과제가 없습니다
              </Typography>
              <Typography variant="body2" color="text.secondary">
                관리자에게 과제 배정을 요청하세요.
              </Typography>
            </CardContent>
          </Card>
        )}

        {/* 데스크톱 과제 상세 다이얼로그 */}
        <Dialog open={openDetailDialog} onClose={handleCloseDetailDialog} maxWidth="md" fullWidth>
          <DialogTitle>
            {selectedAssignment?.taskTitle} - 상세 정보
          </DialogTitle>
          <DialogContent>
            {selectedAssignment && (
              <Box sx={{ pt: 2 }}>
                <Grid container spacing={3}>
                  <Grid item xs={12} md={6}>
                    <Typography variant="h6" sx={{ mb: 2 }}>
                      기본 정보
                    </Typography>
                    <Box sx={{ mb: 2 }}>
                      <Typography variant="body2" color="text.secondary">
                        영역: {getAreaText(selectedAssignment.area)}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        상태: {getStatusText(selectedAssignment.status)}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        시작일: {selectedAssignment.startDate}
                      </Typography>
                      {selectedAssignment.endDate && (
                        <Typography variant="body2" color="text.secondary">
                          종료일: {selectedAssignment.endDate}
                        </Typography>
                      )}
                      {selectedAssignment.area === 'vocabulary' && selectedAssignment.startUnit && (
                        <Typography variant="body2" color="text.secondary">
                          시작 단원: {selectedAssignment.startUnit}
                        </Typography>
                      )}
                    </Box>

                    <Typography variant="h6" sx={{ mb: 2 }}>
                      진행률
                    </Typography>
                    <Box sx={{ mb: 2 }}>
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                        <Typography variant="body2">
                          {selectedAssignment.progress.completed} / {selectedAssignment.progress.total} 완료
                        </Typography>
                        <Typography variant="body2">
                          {Math.round((selectedAssignment.progress.completed / selectedAssignment.progress.total) * 100)}%
                        </Typography>
                      </Box>
                      <LinearProgress
                        variant="determinate"
                        value={(selectedAssignment.progress.completed / selectedAssignment.progress.total) * 100}
                        sx={{ height: 12, borderRadius: 6 }}
                      />
                    </Box>
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <Typography variant="h6" sx={{ mb: 2 }}>
                      주간 스케줄
                    </Typography>
                    <TableContainer component={Paper} variant="outlined">
                      <Table size="small">
                        <TableHead>
                          <TableRow>
                            <TableCell>요일</TableCell>
                            <TableCell>학습량</TableCell>
                            <TableCell>상태</TableCell>
                          </TableRow>
                        </TableHead>
                        <TableBody>
                          {['월', '화', '수', '목', '금', '토', '일'].map((day) => {
                            const schedule = selectedAssignment.weeklySchedule[day];
                            return (
                              <TableRow key={day}>
                                <TableCell>{day}</TableCell>
                                <TableCell>
                                  {schedule?.isActive ? schedule.dailyAmount : '-'}
                                </TableCell>
                                <TableCell>
                                  {schedule?.isActive ? (
                                    <Chip label="활성" color="success" size="small" />
                                  ) : (
                                    <Chip label="비활성" color="default" size="small" />
                                  )}
                                </TableCell>
                              </TableRow>
                            );
                          })}
                        </TableBody>
                      </Table>
                    </TableContainer>
                  </Grid>
                </Grid>
              </Box>
            )}
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseDetailDialog}>닫기</Button>
          </DialogActions>
        </Dialog>

        {/* 모바일 과제 상세 바텀시트 */}
        <Drawer
          anchor="bottom"
          open={openMobileDetail}
          onClose={handleCloseDetailDialog}
          PaperProps={{
            sx: {
              borderTopLeftRadius: 16,
              borderTopRightRadius: 16,
              maxHeight: '80vh',
              minHeight: '50vh',
            }
          }}
        >
          {selectedAssignment && (
            <Box sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                <Typography variant="h6" sx={{ fontWeight: 600 }}>
                  {selectedAssignment.taskTitle}
                </Typography>
                <IconButton onClick={handleCloseDetailDialog}>
                  <CloseIcon />
                </IconButton>
              </Box>

              <List sx={{ p: 0 }}>
                <ListItem sx={{ px: 0 }}>
                  <ListItemIcon>
                    <AssignmentIcon color="primary" />
                  </ListItemIcon>
                  <ListItemText
                    primary="영역"
                    secondary={getAreaText(selectedAssignment.area)}
                  />
                </ListItem>

                <ListItem sx={{ px: 0 }}>
                  <ListItemIcon>
                    <TrendingUpIcon color="primary" />
                  </ListItemIcon>
                  <ListItemText
                    primary="상태"
                    secondary={getStatusText(selectedAssignment.status)}
                  />
                </ListItem>

                <ListItem sx={{ px: 0 }}>
                  <ListItemIcon>
                    <CalendarIcon color="primary" />
                  </ListItemIcon>
                  <ListItemText
                    primary="시작일"
                    secondary={selectedAssignment.startDate}
                  />
                </ListItem>

                {selectedAssignment.endDate && (
                  <ListItem sx={{ px: 0 }}>
                    <ListItemIcon>
                      <CheckCircleIcon color="primary" />
                    </ListItemIcon>
                    <ListItemText
                      primary="종료일"
                      secondary={selectedAssignment.endDate}
                    />
                  </ListItem>
                )}

                {selectedAssignment.area === 'vocabulary' && selectedAssignment.startUnit && (
                  <ListItem sx={{ px: 0 }}>
                    <ListItemIcon>
                      <InfoIcon color="primary" />
                    </ListItemIcon>
                    <ListItemText
                      primary="시작 단원"
                      secondary={selectedAssignment.startUnit}
                    />
                  </ListItem>
                )}
              </List>

              <Divider sx={{ my: 2 }} />

              <Typography variant="h6" sx={{ mb: 2, fontWeight: 600 }}>
                진행률
              </Typography>
              <Box sx={{ mb: 2 }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                  <Typography variant="body2">
                    {selectedAssignment.progress.completed} / {selectedAssignment.progress.total} 완료
                  </Typography>
                  <Typography variant="body2" color="primary" sx={{ fontWeight: 600 }}>
                    {Math.round((selectedAssignment.progress.completed / selectedAssignment.progress.total) * 100)}%
                  </Typography>
                </Box>
                <LinearProgress
                  variant="determinate"
                  value={(selectedAssignment.progress.completed / selectedAssignment.progress.total) * 100}
                  sx={{ height: 8, borderRadius: 4 }}
                />
              </Box>

              <Typography variant="h6" sx={{ mb: 2, fontWeight: 600 }}>
                주간 스케줄
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                {['월', '화', '수', '목', '금', '토', '일'].map((day) => {
                  const schedule = selectedAssignment.weeklySchedule[day];
                  return (
                    <Box
                      key={day}
                      sx={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                        p: 2,
                        borderRadius: 1,
                        backgroundColor: schedule?.isActive ? 'primary.50' : 'grey.50',
                        border: schedule?.isActive ? '1px solid' : '1px solid',
                        borderColor: schedule?.isActive ? 'primary.200' : 'grey.200',
                      }}
                    >
                      <Typography variant="body1" sx={{ fontWeight: 500 }}>
                        {day}요일
                      </Typography>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <Typography variant="body2" color="text.secondary">
                          {schedule?.isActive ? schedule.dailyAmount : '학습 없음'}
                        </Typography>
                        <Chip
                          label={schedule?.isActive ? '활성' : '비활성'}
                          color={schedule?.isActive ? 'success' : 'default'}
                          size="small"
                        />
                      </Box>
                    </Box>
                  );
                })}
                             </Box>
             </Box>
           )}
         </Drawer>
       </Container>
     </Box>
   );
 };

export default StudentPage;
