import React, { useState, useEffect, useCallback } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Button,
  LinearProgress,
  IconButton,
  useTheme,
  useMediaQuery,
  Container,
  Grid,
} from '@mui/material';
import {
  CheckCircle as CheckCircleIcon,
  Help as HelpIcon,
  Close as CloseIcon,
  ArrowBack as ArrowBackIcon,
  PlayArrow as PlayArrowIcon,
  Pause as PauseIcon,
  Stop as StopIcon,
} from '@mui/icons-material';
import { getAssignedWords, VocabularyWord } from '../utils/vocabularyData';

interface LearningSession {
  id: string;
  words: VocabularyWord[];
  currentWordIndex: number;
  completedWords: string[];
  triangleWords: string[];
  xWords: string[];
  isCompleted: boolean;
  startTime: Date;
  endTime?: Date;
}

interface VocabularyLearningProps {
  assignment: any; // TaskAssignment 타입
  onComplete: (session: LearningSession) => void;
  onClose: () => void;
  timerDuration?: number; // 타이머 시간 (초)
}

const VocabularyLearning: React.FC<VocabularyLearningProps> = ({
  assignment,
  onComplete,
  onClose,
  timerDuration = 1.5, // 기본값 1.5초
}) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  
  const [session, setSession] = useState<LearningSession | null>(null);
  const [currentWord, setCurrentWord] = useState<VocabularyWord | null>(null);
  const [showMeaning, setShowMeaning] = useState(false);
  const [doubleCheckTimer, setDoubleCheckTimer] = useState<number>(0);
  const [isPaused, setIsPaused] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [firstChoice, setFirstChoice] = useState<'circle' | 'triangle' | 'x' | null>(null);

  useEffect(() => {
    initializeSession();
  }, [assignment]);

  const initializeSession = useCallback(() => {
    // 배정된 과제의 단어 목록 가져오기
    console.log('=== VocabularyLearning initializeSession ===');
    console.log('Assignment object:', assignment);
    console.log('Assignment type:', typeof assignment);
    console.log('Assignment keys:', Object.keys(assignment || {}));
    
    const assignedWords = getAssignedWords(assignment);
    
    console.log('Assignment vocabularyData:', assignment?.vocabularyData);
    console.log('Assignment taskTitle:', assignment?.taskTitle);
    console.log('Assignment taskId:', assignment?.taskId);
    console.log('Assigned words:', assignedWords);
    console.log('First word:', assignedWords[0]);
    console.log('All words:', assignedWords.map(w => w.english));
    console.log('=== End initializeSession ===');
    
    if (assignedWords.length === 0) {
      console.error('No words assigned to this task');
      return;
    }
    
    const newSession: LearningSession = {
      id: Date.now().toString(),
      words: assignedWords,
      currentWordIndex: 0,
      completedWords: [],
      triangleWords: [],
      xWords: [],
      isCompleted: false,
      startTime: new Date(),
    };
    setSession(newSession);
    setCurrentWord(assignedWords[0]);
  }, [assignment]);

  const handleWordCheck = (checkType: 'circle' | 'triangle' | 'x') => {
    if (!session || !currentWord) return;

    setFirstChoice(checkType);
    setShowMeaning(true);
    
    // 타이머 시작 (1.5초)
    setDoubleCheckTimer(1.5);
  };

  const handleMistaken = () => {
    if (!session || !currentWord || !firstChoice) return;

    const updatedSession = { ...session };
    
    // 기존 선택을 제거하고 x(모르겠어요)로 변경
    updatedSession.completedWords = updatedSession.completedWords.filter(id => id !== currentWord.id);
    updatedSession.triangleWords = updatedSession.triangleWords.filter(id => id !== currentWord.id);
    updatedSession.xWords = updatedSession.xWords.filter(id => id !== currentWord.id);
    
    // 모르겠어요로 분류
    updatedSession.xWords.push(currentWord.id);

    setSession(updatedSession);
    setShowMeaning(false);
    setDoubleCheckTimer(0);
    setFirstChoice(null);
    handleNextWord();
  };

  const confirmChoice = () => {
    if (!session || !currentWord || !firstChoice) return;

    const updatedSession = { ...session };
    
    // 첫 번째 선택을 확정
    if (firstChoice === 'circle') {
      updatedSession.completedWords.push(currentWord.id);
    } else if (firstChoice === 'triangle') {
      updatedSession.triangleWords.push(currentWord.id);
    } else {
      updatedSession.xWords.push(currentWord.id);
    }

    setSession(updatedSession);
    setShowMeaning(false);
    setDoubleCheckTimer(0);
    setFirstChoice(null);
    handleNextWord();
  };

  const handleNextWord = useCallback(() => {
    if (!session) return;

    const nextIndex = session.currentWordIndex + 1;
    
    if (nextIndex >= session.words.length) {
      // 한 사이클 완료
      if (session.triangleWords.length === 0 && session.xWords.length === 0) {
        // 모든 단어 완료
        completeSession();
      } else {
        // triangle과 x 단어들만 다시 학습
        const remainingWords = session.words.filter(word => 
          session.triangleWords.includes(word.id) || session.xWords.includes(word.id)
        );
        
        const newSession: LearningSession = {
          ...session,
          words: remainingWords,
          currentWordIndex: 0,
          triangleWords: [],
          xWords: [],
        };
        
        setSession(newSession);
        setCurrentWord(remainingWords[0]);
        setShowMeaning(false);
      }
    } else {
      // 다음 단어로
      setSession({ ...session, currentWordIndex: nextIndex });
      setCurrentWord(session.words[nextIndex]);
      setShowMeaning(false);
    }
  }, [session]);

  useEffect(() => {
    if (doubleCheckTimer > 0) {
      const timer = setTimeout(() => {
        setDoubleCheckTimer(prev => Math.max(0, prev - 0.1));
      }, 100);
      return () => clearTimeout(timer);
          } else if (doubleCheckTimer === 0 && showMeaning) {
      confirmChoice();
    }
      }, [doubleCheckTimer, showMeaning, confirmChoice]);

  const completeSession = () => {
    if (!session) return;
    
    const completedSession: LearningSession = {
      ...session,
      isCompleted: true,
      endTime: new Date(),
    };
    
    setSession(completedSession);
    setShowResults(true);
  };

  const getProgress = () => {
    if (!session) return 0;
    const totalWords = session.words.length;
    const completedWords = session.completedWords.length;
    return (completedWords / totalWords) * 100;
  };

  if (!session || !currentWord) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Typography>학습을 준비하는 중...</Typography>
      </Box>
    );
  }

  if (showResults) {
    return (
      <Container maxWidth="md" sx={{ py: 3 }}>
        <Card sx={{ borderRadius: isMobile ? 2 : 1 }}>
          <CardContent sx={{ p: isMobile ? 3 : 2 }}>
            <Box sx={{ textAlign: 'center', mb: 3 }}>
              <CheckCircleIcon sx={{ fontSize: 64, color: 'success.main', mb: 2 }} />
              <Typography variant="h5" sx={{ mb: 1, fontWeight: 600 }}>
                학습 완료!
              </Typography>
              <Typography variant="body1" color="text.secondary">
                모든 단어를 학습했습니다.
              </Typography>
            </Box>

            <Box sx={{ mb: 3 }}>
              <Typography variant="h6" sx={{ mb: 2, fontWeight: 600 }}>
                학습 결과
              </Typography>
              
              <Grid container spacing={2}>
                <Grid item xs={4}>
                  <Box sx={{ textAlign: 'center', p: 2, bgcolor: 'success.50', borderRadius: 1 }}>
                    <Typography variant="h4" color="success.main" sx={{ fontWeight: 600 }}>
                      {session.completedWords.length}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      완료된 단어
                    </Typography>
                  </Box>
                </Grid>
                <Grid item xs={4}>
                  <Box sx={{ textAlign: 'center', p: 2, bgcolor: 'warning.50', borderRadius: 1 }}>
                    <Typography variant="h4" color="warning.main" sx={{ fontWeight: 600 }}>
                      {session.triangleWords.length}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      재학습 필요
                    </Typography>
                  </Box>
                </Grid>
                <Grid item xs={4}>
                  <Box sx={{ textAlign: 'center', p: 2, bgcolor: 'error.50', borderRadius: 1 }}>
                    <Typography variant="h4" color="error.main" sx={{ fontWeight: 600 }}>
                      {session.xWords.length}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      미완료 단어
                    </Typography>
                  </Box>
                </Grid>
              </Grid>
            </Box>

            <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center' }}>
              <Button
                variant="contained"
                onClick={() => onComplete(session)}
                size={isMobile ? "large" : "medium"}
              >
                학습 완료
              </Button>
              <Button
                variant="outlined"
                onClick={onClose}
                size={isMobile ? "large" : "medium"}
              >
                닫기
              </Button>
            </Box>
          </CardContent>
        </Card>
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ py: 3 }}>
      {/* 헤더 */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <IconButton onClick={onClose} sx={{ mr: 2 }}>
            <ArrowBackIcon />
          </IconButton>
          <Typography variant="h6" sx={{ fontWeight: 600 }}>
            단어 학습
          </Typography>
        </Box>
        
        <Box sx={{ display: 'flex', gap: 1 }}>
          <IconButton onClick={() => setIsPaused(!isPaused)}>
            {isPaused ? <PlayArrowIcon /> : <PauseIcon />}
          </IconButton>
          <IconButton onClick={onClose}>
            <StopIcon />
          </IconButton>
        </Box>
      </Box>

      {/* 진행률 */}
      <Box sx={{ mb: 3 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
          <Typography variant="body2">
            진행률: {Math.round(getProgress())}%
          </Typography>
          <Typography variant="body2">
            {session.currentWordIndex + 1} / {session.words.length}
          </Typography>
        </Box>
        <LinearProgress
          variant="determinate"
          value={getProgress()}
          sx={{ height: 8, borderRadius: 4 }}
        />
      </Box>

      {/* 단어 카드 */}
      <Card sx={{ mb: 3, borderRadius: isMobile ? 2 : 1 }}>
        <CardContent sx={{ p: isMobile ? 4 : 3, textAlign: 'center' }}>
          <Typography variant="h3" sx={{ mb: 3, fontWeight: 600 }}>
            {currentWord.english}
          </Typography>

          {showMeaning ? (
            <Box>
              <Typography variant="h5" color="primary" sx={{ mb: 3, fontWeight: 600 }}>
                {currentWord.korean}
              </Typography>
              
              {firstChoice === 'circle' && (
                <Box sx={{ mb: 3 }}>
                  <Button
                    variant="contained"
                    color="inherit"
                    size="large"
                    fullWidth
                    onClick={handleMistaken}
                    sx={{ 
                      py: 2,
                      borderRadius: 2,
                      backgroundColor: 'black',
                      color: 'white',
                      '&:hover': { 
                        backgroundColor: 'grey.900',
                        transform: 'scale(1.02)' 
                      }
                    }}
                  >
                    아 몰랐어요
                  </Button>
                </Box>
              )}

              {doubleCheckTimer > 0 && (
                <Typography variant="body2" color="text.secondary">
                  {doubleCheckTimer.toFixed(1)}초 후 자동으로 다음 단어로 넘어갑니다.
                </Typography>
              )}
            </Box>
          ) : (
            <Box>
              <Box sx={{ mb: 4, p: 2, backgroundColor: 'grey.100', borderRadius: 2 }}>
                <Typography variant="h6" color="text.primary" sx={{ mb: 1 }}>
                  이 단어를 알고 있나요?
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  단어의 뜻을 생각해보세요
                </Typography>
              </Box>
              
              <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, flexWrap: 'wrap' }}>
                <Button
                  variant="contained"
                  color="success"
                  size="large"
                  onClick={() => handleWordCheck('circle')}
                  sx={{ 
                    minWidth: 120, 
                    py: 2,
                    borderRadius: 2,
                    '&:hover': { transform: 'scale(1.05)' }
                  }}
                >
                  <CheckCircleIcon sx={{ mr: 1 }} />
                  완전히 알고 있어요
                </Button>
                
                <Button
                  variant="contained"
                  color="warning"
                  size="large"
                  onClick={() => handleWordCheck('triangle')}
                  sx={{ 
                    minWidth: 120, 
                    py: 2,
                    borderRadius: 2,
                    '&:hover': { transform: 'scale(1.05)' }
                  }}
                >
                  <HelpIcon sx={{ mr: 1 }} />
                  조금 헷갈려요
                </Button>
                
                <Button
                  variant="contained"
                  color="error"
                  size="large"
                  onClick={() => handleWordCheck('x')}
                  sx={{ 
                    minWidth: 120, 
                    py: 2,
                    borderRadius: 2,
                    '&:hover': { transform: 'scale(1.05)' }
                  }}
                >
                  <CloseIcon sx={{ mr: 1 }} />
                  모르겠어요
                </Button>
              </Box>
            </Box>
          )}
        </CardContent>
      </Card>

      {/* 학습 통계 */}
      <Box sx={{ display: 'flex', justifyContent: 'space-around', textAlign: 'center' }}>
        <Box>
          <Typography variant="h6" color="success.main" sx={{ fontWeight: 600 }}>
            {session.completedWords.length}
          </Typography>
          <Typography variant="caption" color="text.secondary">
            완료
          </Typography>
        </Box>
        <Box>
          <Typography variant="h6" color="warning.main" sx={{ fontWeight: 600 }}>
            {session.triangleWords.length}
          </Typography>
          <Typography variant="caption" color="text.secondary">
            재학습
          </Typography>
        </Box>
        <Box>
          <Typography variant="h6" color="error.main" sx={{ fontWeight: 600 }}>
            {session.xWords.length}
          </Typography>
          <Typography variant="caption" color="text.secondary">
            미완료
          </Typography>
        </Box>
      </Box>
    </Container>
  );
};

export default VocabularyLearning;
