import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Chip,
  LinearProgress,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  Avatar,
  useTheme,
  useMediaQuery,
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  AppBar,
  Toolbar,
  Container,
  Tabs,
  Tab,
  Slider,
  ToggleButton,
  ToggleButtonGroup,
  Tooltip,
} from '@mui/material';
import {
  Assignment as AssignmentIcon,
  CheckCircle as CheckCircleIcon,
  ArrowBack as ArrowBackIcon,
  Info as InfoIcon,
  CalendarToday as CalendarIcon,
  TrendingUp as TrendingUpIcon,
  Close as CloseIcon,
  Refresh as RefreshIcon,
  Today as TodayIcon,
  ViewModule as ViewModuleIcon,
  PlayArrow as PlayArrowIcon,
  Settings as SettingsIcon,
  Timer as TimerIcon,
  DateRange as DateRangeIcon,
  CalendarMonth as CalendarMonthIcon,
  NavigateBefore as NavigateBeforeIcon,
  NavigateNext as NavigateNextIcon,
} from '@mui/icons-material';
import { useParams, useNavigate } from 'react-router-dom';
import { loadStudents, saveStudents, loadTasks } from '../utils/storage';
import VocabularyLearning from '../components/VocabularyLearning';
import VocabularyEvaluation from '../components/VocabularyEvaluation';

interface TaskAssignment {
  taskId: string;
  taskTitle: string;
  area: 'vocabulary' | 'phrase' | 'grammar' | 'logic';
  weeklySchedule: {
    [day: string]: {
      isActive: boolean;
      dailyAmount: string;
    };
  };
  startDate: string;
  endDate?: string;
  status: 'active' | 'completed' | 'paused';
  progress: {
    completed: number;
    total: number;
  };
  startUnit?: string;
  vocabularyData?: Array<{ unit: string; english: string; meaning: string }>;
  targetDate?: string; // í•™ìŠµì´ë‚˜ í‰ê°€ë¥¼ ìˆ˜í–‰í•˜ëŠ” ëŒ€ìƒ ë‚ ì§œ
  targetUnit?: string; // íŠ¹ì • ë‹¨ì›ì„ ì§€ì •í•˜ì—¬ í•™ìŠµí•  ë•Œ ì‚¬ìš©
}

interface Student {
  id: string;
  name: string;
  address: string;
  phone: string;
  grade: 'ì´ˆ6' | 'ì¤‘1' | 'ì¤‘2' | 'ì¤‘3' | 'ê³ 1' | 'ê³ 2' | 'ê³ 3';
  joinDate: string;
  status: 'active' | 'inactive';
  assignedTasks: string[];
  taskAssignments: TaskAssignment[];
  grades: {
    [year: string]: {
      mockExam3?: string;
      mockExam6?: string;
      mockExam9?: string;
      mockExam11?: string;
      midterm1?: string;
      final1?: string;
      midterm2?: string;
      final2?: string;
    };
  };
  learningHistory?: Array<{
    date: string;
    taskId: string;
    taskTitle: string;
    learningSession: number; // í•™ìŠµ íšŒì°¨ (1, 2, 3...)
    isFirstLearning: boolean; // ìµœì´ˆ í•™ìŠµ ì—¬ë¶€
    rounds: any[];
    summary: {
      totalRounds: number;
      totalWords: number;
      finalCompletedWords: number;
      completionRate: number;
    };
  }>;
  evaluationHistory?: Array<{
    date: string;
    taskId: string;
    taskTitle: string;
    evaluationLevel: number;
    results: any;
  }>;
}

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ pt: 2 }}>{children}</Box>}
    </div>
  );
}

const StudentPage: React.FC = () => {
  const { studentId } = useParams<{ studentId: string }>();
  const navigate = useNavigate();
  const [student, setStudent] = useState<Student | null>(null);
  const [selectedAssignment, setSelectedAssignment] = useState<TaskAssignment | null>(null);
  const [openDetailDialog, setOpenDetailDialog] = useState(false);
