  // íŠ¹ì • ìš”ì¼ì˜ í•™ìŠµ ê°€ëŠ¥ ì—¬ë¶€ íŒë‹¨
  const canStudyOnDay = (dayIndex: number, assignment: TaskAssignment): boolean => {
    if (!assignment.startDate || !assignment.weeklySchedule) return false;

    const startDate = new Date(assignment.startDate);
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    startDate.setHours(0, 0, 0, 0);

    // ì‹œì‘ì¼ ì´ì „ì´ë©´ í•™ìŠµ ë¶ˆê°€
    if (today < startDate) return false;

    // ì£¼ê°„ ìŠ¤ì¼€ì¤„ì—ì„œ í™œì„±í™”ëœ ìš”ì¼ë§Œ ì¹´ìš´íŠ¸
    const dayNames = ['ì¼', 'ì›”', 'í™”', 'ìˆ˜', 'ëª©', 'ê¸ˆ', 'í† '];
    let studyDayCount = 0;
    let targetStudyDay = 0;

    // ì‹œì‘ì¼ë¶€í„° ì˜¤ëŠ˜ê¹Œì§€ í•™ìŠµì¼ ìˆ˜ ê³„ì‚°
    const currentDate = new Date(startDate);
    while (currentDate <= today) {
      const currentDayIndex = currentDate.getDay();
      const currentDayName = dayNames[currentDayIndex];

      if (assignment.weeklySchedule[currentDayName]?.isActive) {
        studyDayCount++;

        // ëª©í‘œ ìš”ì¼ì˜ í•™ìŠµì¼ ë²ˆí˜¸ ê³„ì‚°
        if (currentDayIndex === dayIndex && currentDate.getTime() === today.getTime()) {
          targetStudyDay = studyDayCount;
        }
      }

      currentDate.setDate(currentDate.getDate() + 1);
    }

    // ì´ë²ˆ ì£¼ì˜ íŠ¹ì • ìš”ì¼ì´ ëª‡ ë²ˆì§¸ í•™ìŠµì¼ì¸ì§€ ê³„ì‚°
    const thisWeekStart = new Date(today);
    thisWeekStart.setDate(today.getDate() - today.getDay()); // ì´ë²ˆ ì£¼ ì¼ìš”ì¼

    let weekStudyDay = 0;
    for (let i = 0; i < 7; i++) {
      const checkDate = new Date(thisWeekStart);
      checkDate.setDate(thisWeekStart.getDate() + i);

      if (checkDate >= startDate && assignment.weeklySchedule[dayNames[i]]?.isActive) {
        weekStudyDay++;

        if (i === dayIndex) {
          // í•´ë‹¹ ìš”ì¼ì´ ì „ì²´ í•™ìŠµ ì§„í–‰ì—ì„œ ëª‡ ë²ˆì§¸ì¸ì§€
          const totalStudyDays = Math.floor((checkDate.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24));
          let dayNumber = 0;

          for (let j = 0; j <= totalStudyDays; j++) {
            const tempDate = new Date(startDate);
            tempDate.setDate(startDate.getDate() + j);
            const tempDayName = dayNames[tempDate.getDay()];

            if (assignment.weeklySchedule[tempDayName]?.isActive) {
              dayNumber++;
            }
          }

          // ë‹¨ì›ì´ ì•„ì§ ë‚¨ì•„ìˆëŠ”ì§€ í™•ì¸
          // ì´ ë¶€ë¶„ì€ ì‹¤ì œ ë‹¨ì› ìˆ˜ì™€ ë¹„êµí•´ì•¼ í•˜ì§€ë§Œ, ì¼ë‹¨ true ë°˜í™˜
          return true;
        }
      }
    }

    return false;
  };

  // íŠ¹ì • ë‚ ì§œì— í•™ìŠµí•  ë‹¨ì› ê³„ì‚°
  const getUnitForDate = (assignment: TaskAssignment, targetDate: Date): string | null => {
    if (!assignment.startDate || !assignment.weeklySchedule || !assignment.vocabularyData) {
