    const totalUnits = unitOrder.length;
    const startUnitIndex = assignment.startUnit ? unitOrder.indexOf(assignment.startUnit) : 0;

    // ì£¼ê°„ ìŠ¤ì¼€ì¤„ì—ì„œ í™œì„±í™”ëœ ìš”ì¼ë§Œ ì¹´ìš´íŠ¸
    const dayNames = ['ì¼', 'ì›”', 'í™”', 'ìˆ˜', 'ëª©', 'ê¸ˆ', 'í† '];
    let totalUnitsStudied = 0;

    // ì‹œì‘ì¼ë¶€í„° ëª©í‘œì¼ê¹Œì§€ í•™ìŠµí•œ ì´ ë‹¨ì› ìˆ˜ ê³„ì‚°
    const currentDate = new Date(startDate);
    while (currentDate <= targetDate) {
      const currentDayName = dayNames[currentDate.getDay()];
      const daySchedule = assignment.weeklySchedule[currentDayName];

      if (daySchedule?.isActive) {
        const dailyAmount = Number(daySchedule.dailyAmount) || 0;

        if (currentDate.getTime() === targetDate.getTime()) {
          // ëª©í‘œì¼ì˜ ì‹œì‘ ë‹¨ì› ë²ˆí˜¸ ê³„ì‚° (1ë¶€í„° ì‹œì‘)
          const currentStartUnit = startUnitIndex + totalUnitsStudied + 1;

          // ì „ì²´ ë‹¨ì›ì„ ì™„ë£Œí–ˆëŠ”ì§€ í™•ì¸
          const isCompleted = currentStartUnit > totalUnits;

          return {
            current: Math.min(currentStartUnit, totalUnits + 1),
            total: totalUnits,
            isCompleted
          };
        } else {
          // ì´ì „ ë‚ ì§œë“¤ì˜ í•™ìŠµëŸ‰ ëˆ„ì 
          totalUnitsStudied += dailyAmount;
        }
      }

      currentDate.setDate(currentDate.getDate() + 1);
    }

    return null;
  };

  // ê³¼ì œê°€ ì´ë¯¸ ì™„ë£Œë˜ì—ˆëŠ”ì§€ í™•ì¸
  const isAssignmentCompleted = (assignment: TaskAssignment, targetDate: Date): boolean => {
