import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Chip,
  LinearProgress,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  Avatar,
  useTheme,
  useMediaQuery,
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  AppBar,
  Toolbar,
  Container,
  Tabs,
  Tab,
  Slider,
  ToggleButton,
  ToggleButtonGroup,
} from '@mui/material';
import {
  Assignment as AssignmentIcon,
  CheckCircle as CheckCircleIcon,
  ArrowBack as ArrowBackIcon,
  Info as InfoIcon,
  CalendarToday as CalendarIcon,
  TrendingUp as TrendingUpIcon,
  Close as CloseIcon,
  Refresh as RefreshIcon,
  Today as TodayIcon,
  ViewModule as ViewModuleIcon,
  PlayArrow as PlayArrowIcon,
  Settings as SettingsIcon,
  Timer as TimerIcon,
  DateRange as DateRangeIcon,
  CalendarMonth as CalendarMonthIcon,
  NavigateBefore as NavigateBeforeIcon,
  NavigateNext as NavigateNextIcon,
} from '@mui/icons-material';
import { useParams, useNavigate } from 'react-router-dom';
import { loadStudents, saveStudents, loadTasks } from '../utils/storage';
import VocabularyLearning from '../components/VocabularyLearning';
import VocabularyEvaluation from '../components/VocabularyEvaluation';

interface TaskAssignment {
  taskId: string;
  taskTitle: string;
  area: 'vocabulary' | 'phrase' | 'grammar' | 'logic';
  weeklySchedule: {
    [day: string]: {
      isActive: boolean;
      dailyAmount: string;
    };
  };
  startDate: string;
  endDate?: string;
  status: 'active' | 'completed' | 'paused';
  progress: {
    completed: number;
    total: number;
  };
  startUnit?: string;
  vocabularyData?: Array<{ unit: string; english: string; meaning: string }>;
  targetDate?: string; // 학습이나 평가를 수행하는 대상 날짜
}

interface Student {
  id: string;
  name: string;
  address: string;
  phone: string;
  grade: '초6' | '중1' | '중2' | '중3' | '고1' | '고2' | '고3';
  joinDate: string;
  status: 'active' | 'inactive';
  assignedTasks: string[];
  taskAssignments: TaskAssignment[];
  grades: {
    [year: string]: {
      mockExam3?: string;
      mockExam6?: string;
      mockExam9?: string;
      mockExam11?: string;
      midterm1?: string;
      final1?: string;
      midterm2?: string;
      final2?: string;
    };
  };
  learningHistory?: Array<{
    date: string;
    taskId: string;
    taskTitle: string;
    learningSession: number; // 학습 회차 (1, 2, 3...)
    isFirstLearning: boolean; // 최초 학습 여부
    rounds: any[];
    summary: {
      totalRounds: number;
      totalWords: number;
      finalCompletedWords: number;
      completionRate: number;
    };
  }>;
  evaluationHistory?: Array<{
    date: string;
    taskId: string;
    taskTitle: string;
    evaluationLevel: number;
    results: any;
  }>;
}

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ pt: 2 }}>{children}</Box>}
    </div>
  );
}

const StudentPage: React.FC = () => {
  const { studentId } = useParams<{ studentId: string }>();
  const navigate = useNavigate();
  const [student, setStudent] = useState<Student | null>(null);
  const [selectedAssignment, setSelectedAssignment] = useState<TaskAssignment | null>(null);
  const [openDetailDialog, setOpenDetailDialog] = useState(false);
  const [openMobileDetail, setOpenMobileDetail] = useState(false);
  const [tabValue, setTabValue] = useState(0);
  const [showVocabularyLearning, setShowVocabularyLearning] = useState(false);
  const [showVocabularyEvaluation, setShowVocabularyEvaluation] = useState(false);
  const [showEvaluationSelectDialog, setShowEvaluationSelectDialog] = useState(false);
  const [evaluationLevel, setEvaluationLevel] = useState<1 | 2>(1);
  const [timerDuration, setTimerDuration] = useState(() => {
    // 로컬 스토리지에서 저장된 타이머 시간 불러오기
    const saved = localStorage.getItem(`timer_duration_${studentId}`);
    return saved ? parseFloat(saved) : 1.5;
  });
  const [viewMode, setViewMode] = useState<'today' | 'week' | 'month'>('today');
  const [weekOffset, setWeekOffset] = useState(0); // 0 = 이번 주, -1 = 지난 주, 1 = 다음 주
  const [monthOffset, setMonthOffset] = useState(0); // 0 = 이번 달, -1 = 지난 달, 1 = 다음 달
  
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  useEffect(() => {
    if (studentId) {
      const students = loadStudents();
      const foundStudent = students.find(s => s.id === studentId);
      if (foundStudent) {
        setStudent(foundStudent);
      } else {
        // 학생을 찾을 수 없는 경우 관리자 페이지로 리다이렉트
        navigate('/students');
      }
    }
  }, [studentId, navigate]);

  const getAreaText = (area: string) => {
    switch (area) {
      case 'vocabulary': return '단어';
      case 'phrase': return '구문';
      case 'grammar': return '어법';
      case 'logic': return '논리';
      default: return area;
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'active': return '진행중';
      case 'completed': return '완료';
      case 'paused': return '일시정지';
      default: return status;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'success';
      case 'completed': return 'primary';
      case 'paused': return 'warning';
      default: return 'default';
    }
  };

  const getGradeText = (grade: string) => {
    return grade;
  };

  const getGradeColor = (grade: string) => {
    switch (grade) {
      case '초6':
        return 'warning';
      case '중1':
      case '중2':
      case '중3':
        return 'primary';
      case '고1':
      case '고2':
      case '고3':
        return 'success';
      default:
        return 'default';
    }
  };

  const handleOpenDetailDialog = (assignment: TaskAssignment) => {
    setSelectedAssignment(assignment);
    if (isMobile) {
      setOpenMobileDetail(true);
    } else {
      setOpenDetailDialog(true);
    }
  };

  const handleCloseDetailDialog = () => {
    setOpenDetailDialog(false);
    setOpenMobileDetail(false);
    setSelectedAssignment(null);
  };

  const handleStartVocabularyLearning = (assignment: TaskAssignment, targetDate?: Date) => {
    // targetDate가 있으면 assignment에 추가
    const assignmentWithDate = targetDate 
      ? { ...assignment, targetDate: targetDate.toISOString() }
      : assignment;
    
    setSelectedAssignment(assignmentWithDate);
    // 타이머 설정 다이얼로그를 건너뛰고 바로 학습 시작
    setShowVocabularyLearning(true);
  };

  const handleStartEvaluation = (assignment: TaskAssignment, targetDate?: Date) => {
    // 해당 과제의 학습 기록 확인
    const taskLearningHistory = student?.learningHistory?.filter(
      history => history.taskId === assignment.taskId
    ) || [];
    
    if (taskLearningHistory.length === 0) {
      alert('먼저 학습을 완료해주세요. 평가는 학습 완료 후에 가능합니다.');
      return;
    }
    
    // 학습 횟수 정보 표시
    const learningCount = taskLearningHistory.length;
    const firstLearning = taskLearningHistory.find(h => h.isFirstLearning) || taskLearningHistory[0];
    const firstLearningDate = new Date(firstLearning.date).toLocaleDateString('ko-KR');
    
    console.log(`Starting evaluation for task ${assignment.taskTitle}`);
    console.log(`Total learning sessions: ${learningCount}`);
    console.log(`First learning date: ${firstLearningDate}`);
    console.log(`Using first learning session's 1st round for evaluation`);
    
    // targetDate가 있으면 assignment에 추가
    const assignmentWithDate = targetDate 
      ? { ...assignment, targetDate: targetDate.toISOString() }
      : assignment;
    
    setSelectedAssignment(assignmentWithDate);
    setShowVocabularyEvaluation(true);
  };

  const handleCompleteVocabularyLearning = (results: any[]) => {
    // 학습 완료 처리 로직
    console.log('Vocabulary learning completed with results:', results);
    
    if (!student || !selectedAssignment) return;
    
    // targetDate가 있으면 해당 날짜 사용, 없으면 현재 날짜 사용
    const learningDate = selectedAssignment.targetDate 
      ? new Date(selectedAssignment.targetDate) 
      : new Date();
    
    // 학습 결과 요약
    const totalRounds = results.length;
    const finalResult = results[results.length - 1];
    const totalWords = results[0].totalWords;
    
    // actualFinalCompletedWords가 있으면 사용, 없으면 기존 방식으로 계산
    let finalCompletedWords;
    if (finalResult.actualFinalCompletedWords !== undefined) {
      finalCompletedWords = finalResult.actualFinalCompletedWords;
    } else {
      // 1회차에서 완료한 단어 수
      const firstRoundCompleted = results.length > 1 
        ? totalWords - results[1].totalWords 
        : results[0].completedWords;
      
      // 마지막 회차에서 완료한 단어 수
      const lastRoundCompleted = results.length > 1
        ? finalResult.completedWords
        : 0;
      
      finalCompletedWords = firstRoundCompleted + lastRoundCompleted;
    }
    
    // 학생 정보 업데이트
    const updatedStudent = { ...student };
    
    // 학습 기록 배열이 없으면 생성
    if (!updatedStudent.learningHistory) {
      updatedStudent.learningHistory = [];
    }
    
    // 해당 과제의 기존 학습 횟수 계산
    const previousLearningCount = updatedStudent.learningHistory.filter(
      history => history.taskId === selectedAssignment.taskId
    ).length;
    
    // 학습 기록 저장 (학습 회차 추가)
    const learningRecord = {
      date: learningDate.toISOString(), // targetDate 사용
      taskId: selectedAssignment.taskId,
      taskTitle: selectedAssignment.taskTitle,
      learningSession: previousLearningCount + 1, // 학습 회차 (1회차, 2회차...)
      isFirstLearning: previousLearningCount === 0, // 최초 학습 여부
      rounds: results,
      summary: {
        totalRounds,
        totalWords,
        finalCompletedWords,
        completionRate: Math.round((finalCompletedWords / totalWords) * 100),
      }
    };
    
    // 학습 기록 추가
    updatedStudent.learningHistory.push(learningRecord);
    
    // 과제 진행률 업데이트
    const assignmentIndex = updatedStudent.taskAssignments.findIndex(
      a => a.taskId === selectedAssignment.taskId
    );
    
    if (assignmentIndex !== -1) {
      // 완료된 단어 수만큼 진행률 증가
      updatedStudent.taskAssignments[assignmentIndex].progress.completed += finalCompletedWords;
      
      // 진행률이 전체를 초과하지 않도록 조정
      const totalProgress = updatedStudent.taskAssignments[assignmentIndex].progress.total;
      if (updatedStudent.taskAssignments[assignmentIndex].progress.completed > totalProgress) {
        updatedStudent.taskAssignments[assignmentIndex].progress.completed = totalProgress;
      }
    }
    
    // 학생 정보 저장
    const students = loadStudents();
    const studentIndex = students.findIndex(s => s.id === student.id);
    if (studentIndex !== -1) {
      students[studentIndex] = updatedStudent;
      saveStudents(students);
      setStudent(updatedStudent);
    }
    
    // 결과 알림
    const sessionText = learningRecord.learningSession === 1 ? '최초 학습' : `${learningRecord.learningSession}회차 학습`;
    alert(`${sessionText}이 완료되었습니다!\n\n총 ${totalRounds}회차 학습\n전체 ${totalWords}개 단어 중 ${finalCompletedWords}개 완료 (${learningRecord.summary.completionRate}%)`);
    
    setShowVocabularyLearning(false);
    setSelectedAssignment(null);
  };

