  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setTabValue(newValue);
  };

  const getTodayAssignments = () => {
    if (!student) return [];
    
    // 오늘 요일 확인
    const today = new Date();
    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
    const todayDayName = dayNames[today.getDay()];
    
    // 오늘 요일에 활성화된 과제만 필터링
    const todayTasks = student.taskAssignments.filter(assignment => {
      // 활성 상태인지 확인
      if (assignment.status !== 'active') return false;
      
      // 오늘 요일의 스케줄이 활성화되어 있는지 확인
      const todaySchedule = assignment.weeklySchedule?.[todayDayName];
      if (!todaySchedule?.isActive) return false;
      
      // 시작일이 오늘보다 늦으면 제외
      const assignmentStartDate = new Date(assignment.startDate);
      assignmentStartDate.setHours(0, 0, 0, 0);
      today.setHours(0, 0, 0, 0);
      if (assignmentStartDate > today) return false;
      
      // 종료일이 있고 오늘보다 이전이면 제외
      if (assignment.endDate) {
        const assignmentEndDate = new Date(assignment.endDate);
        assignmentEndDate.setHours(0, 0, 0, 0);
        if (assignmentEndDate < today) return false;
      }
      
      // 전체 학습이 완료된 과제는 제외
      if (isAssignmentCompleted(assignment, today)) return false;
      
      return true;
    });
    
    // 오늘의 과제 뷰에서는 항상 더미 데이터를 반환하여 UI가 표시되도록 함
    if (todayTasks.length === 0) {
      return [{
        taskId: 'dummy',
        taskTitle: 'dummy',
        area: 'vocabulary' as const,
        weeklySchedule: {},
        startDate: '',
        status: 'active' as const,
        progress: { completed: 0, total: 0 },
      }];
    }
    
    return todayTasks;
  };

  const getWeekAssignments = () => {
    if (!student) return [];
    
    // 이번 주에 학습 예정인 모든 과제
    const today = new Date();
    const dayOfWeek = today.getDay();
    const weekStart = new Date(today);
    weekStart.setDate(today.getDate() - dayOfWeek);
    const weekEnd = new Date(weekStart);
    weekEnd.setDate(weekStart.getDate() + 6);
    
    return student.taskAssignments.filter(assignment => {
      if (assignment.status !== 'active') return false;
      
      // 주간 스케줄에서 이번 주에 활성화된 요일이 있는지 확인
      const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
      return Object.entries(assignment.weeklySchedule || {}).some(([day, schedule]) => 
        schedule.isActive
      );
    });
  };

  const getMonthAssignments = () => {
    if (!student) return [];
    
    // 이번 달에 활성화된 모든 과제
    return student.taskAssignments.filter(assignment => 
      assignment.status === 'active'
    );
  };

  const getActiveAssignments = () => {
    if (!student) return [];
    return student.taskAssignments.filter(assignment => assignment.status === 'active');
  };

  const getAssignmentsByViewMode = () => {
    switch (viewMode) {
      case 'week':
        return getWeekAssignments();
      case 'month':
        return getMonthAssignments();
      default:
        return getTodayAssignments();
    }
  };

  const getViewModeTitle = () => {
    switch (viewMode) {
      case 'week':
        return '이번 주 과제';
      case 'month':
        return '이번 달 과제';
      default:
        return '오늘의 과제';
    }
  };

  // 주간 날짜 범위 계산
  const getWeekDateRange = (offset: number = 0) => {
    const today = new Date();
    const currentDay = today.getDay();
    const diff = currentDay === 0 ? -6 : 1 - currentDay; // 월요일 기준
    
    const monday = new Date(today);
    monday.setDate(today.getDate() + diff + (offset * 7));
    
    const sunday = new Date(monday);
    sunday.setDate(monday.getDate() + 6);
    
    return { monday, sunday };
  };

  // 주간 타이틀 생성
  const getWeekTitle = () => {
    if (viewMode !== 'week') return getViewModeTitle();
    
    const { monday, sunday } = getWeekDateRange(weekOffset);
    const formatDate = (date: Date) => `${date.getMonth() + 1}/${date.getDate()}`;
    
    if (weekOffset === 0) {
      return `이번 주 과제 (${formatDate(monday)} - ${formatDate(sunday)})`;
    } else if (weekOffset < 0) {
      return `${Math.abs(weekOffset)}주 전 과제 (${formatDate(monday)} - ${formatDate(sunday)})`;
    } else {
      return `${weekOffset}주 후 과제 (${formatDate(monday)} - ${formatDate(sunday)})`;
    }
  };

  // 월간 날짜 계산
  const getMonthDateRange = (offset: number = 0) => {
    const today = new Date();
    const year = today.getFullYear();
    const month = today.getMonth() + offset;
    
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    
    return { firstDay, lastDay, year: firstDay.getFullYear(), month: firstDay.getMonth() };
  };

  // 월간 캘린더 데이터 생성
  const getMonthCalendarData = () => {
    const { firstDay, lastDay, year, month } = getMonthDateRange(monthOffset);
    const startDayOfWeek = (firstDay.getDay() + 6) % 7; // 0 = 월요일, 6 = 일요일
    const daysInMonth = lastDay.getDate();
    
    const calendar = [];
    let week = [];
    
    // 첫 주의 빈 날짜 채우기
    for (let i = 0; i < startDayOfWeek; i++) {
      week.push(null);
    }
    
    // 날짜 채우기
    for (let day = 1; day <= daysInMonth; day++) {
      week.push(new Date(year, month, day));
      
      if (week.length === 7) {
        calendar.push(week);
        week = [];
      }
    }
    
    // 마지막 주의 빈 날짜 채우기
    if (week.length > 0) {
      while (week.length < 7) {
        week.push(null);
      }
      calendar.push(week);
    }
    
    return calendar;
  };

  // 월간 타이틀 생성
  const getMonthTitle = () => {
    if (viewMode !== 'month') return getViewModeTitle();
    
    const { year, month } = getMonthDateRange(monthOffset);
    const monthNames = ['1월', '2월', '3월', '4월', '5월', '6월', '7월', '8월', '9월', '10월', '11월', '12월'];
    
    if (monthOffset === 0) {
      return `${year}년 ${monthNames[month]} 과제`;
    } else if (monthOffset < 0) {
      return `${year}년 ${monthNames[month]} 과제`;
    } else {
      return `${year}년 ${monthNames[month]} 과제`;
    }
  };

  // 전체 타이틀 생성 (주간/월간 통합)
  const getViewTitle = () => {
    switch (viewMode) {
      case 'week':
        return getWeekTitle();
      case 'month':
        return getMonthTitle();
      default:
        return getViewModeTitle();
    }
  };

  const getChipLabel = (mode: typeof viewMode) => {
    switch (mode) {
      case 'week':
        return '주간 학습';
      case 'month':
        return '월간 학습';
      default:
        return '오늘 학습';
    }
  };

  const getWeeklyGroupedAssignments = () => {
    if (viewMode !== 'week') return null;
    
    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
    const weeklyGroups: { [key: string]: TaskAssignment[] } = {};
    
    // 각 요일별로 빈 배열 초기화
    dayNames.forEach(day => {
      weeklyGroups[day] = [];
    });
    
    // 각 과제를 해당하는 요일에 추가
    viewAssignments.forEach(assignment => {
      dayNames.forEach(day => {
        if (assignment.weeklySchedule?.[day]?.isActive) {
          weeklyGroups[day].push(assignment);
        }
      });
    });
    
    return weeklyGroups;
  };

  // 특정 요일의 학습 가능 여부 판단
  const canStudyOnDay = (dayIndex: number, assignment: TaskAssignment): boolean => {
    if (!assignment.startDate || !assignment.weeklySchedule) return false;
    
    const startDate = new Date(assignment.startDate);
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    startDate.setHours(0, 0, 0, 0);
    
    // 시작일 이전이면 학습 불가
    if (today < startDate) return false;
    
    // 주간 스케줄에서 활성화된 요일만 카운트
    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
    let studyDayCount = 0;
    let targetStudyDay = 0;
    
    // 시작일부터 오늘까지 학습일 수 계산
    const currentDate = new Date(startDate);
    while (currentDate <= today) {
      const currentDayIndex = currentDate.getDay();
      const currentDayName = dayNames[currentDayIndex];
      
      if (assignment.weeklySchedule[currentDayName]?.isActive) {
        studyDayCount++;
        
        // 목표 요일의 학습일 번호 계산
        if (currentDayIndex === dayIndex && currentDate.getTime() === today.getTime()) {
          targetStudyDay = studyDayCount;
        }
      }
      
      currentDate.setDate(currentDate.getDate() + 1);
    }
    
    // 이번 주의 특정 요일이 몇 번째 학습일인지 계산
    const thisWeekStart = new Date(today);
    thisWeekStart.setDate(today.getDate() - today.getDay()); // 이번 주 일요일
    
    let weekStudyDay = 0;
    for (let i = 0; i < 7; i++) {
      const checkDate = new Date(thisWeekStart);
      checkDate.setDate(thisWeekStart.getDate() + i);
      
      if (checkDate >= startDate && assignment.weeklySchedule[dayNames[i]]?.isActive) {
        weekStudyDay++;
        
        if (i === dayIndex) {
          // 해당 요일이 전체 학습 진행에서 몇 번째인지
          const totalStudyDays = Math.floor((checkDate.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24));
          let dayNumber = 0;
          
          for (let j = 0; j <= totalStudyDays; j++) {
            const tempDate = new Date(startDate);
            tempDate.setDate(startDate.getDate() + j);
            const tempDayName = dayNames[tempDate.getDay()];
            
            if (assignment.weeklySchedule[tempDayName]?.isActive) {
              dayNumber++;
            }
          }
          
          // 단원이 아직 남아있는지 확인
          // 이 부분은 실제 단원 수와 비교해야 하지만, 일단 true 반환
          return true;
        }
      }
    }
    
    return false;
  };

  // 특정 날짜에 학습할 단원 계산
  const getUnitForDate = (assignment: TaskAssignment, targetDate: Date): string | null => {
    if (!assignment.startDate || !assignment.weeklySchedule || !assignment.vocabularyData) {
      return null;
    }
    
    const startDate = new Date(assignment.startDate);
    startDate.setHours(0, 0, 0, 0);
    targetDate.setHours(0, 0, 0, 0);
    
    // 시작일 이전이면 null
    if (targetDate < startDate) return null;
    
    // 주간 스케줄에서 활성화된 요일만 카운트
    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
    let studyDayCount = 0;
    
    // 시작일부터 목표일까지 학습일 수 계산
    const currentDate = new Date(startDate);
    while (currentDate <= targetDate) {
      const currentDayName = dayNames[currentDate.getDay()];
      
      if (assignment.weeklySchedule[currentDayName]?.isActive) {
        studyDayCount++;
        
        if (currentDate.getTime() === targetDate.getTime()) {
          // 단원 목록 추출
          const unitOrder: string[] = [];
          const unitSet = new Set<string>();
          
          assignment.vocabularyData.forEach((word: any) => {
            if (!unitSet.has(word.unit)) {
              unitSet.add(word.unit);
              unitOrder.push(word.unit);
            }
          });
          
          // 시작 단원 인덱스 찾기
          const startUnitIndex = assignment.startUnit ? unitOrder.indexOf(assignment.startUnit) : 0;
          
          if (startUnitIndex !== -1) {
            const targetUnitIndex = startUnitIndex + (studyDayCount - 1);
            
            if (targetUnitIndex < unitOrder.length) {
              return unitOrder[targetUnitIndex];
            }
          }
          
          return null;
        }
      }
      
      currentDate.setDate(currentDate.getDate() + 1);
    }
    
    return null;
  };

  // 특정 날짜의 단원 진행 정보 가져오기
  const getUnitProgressForDate = (assignment: TaskAssignment, targetDate: Date): { current: number; total: number; isCompleted: boolean } | null => {
    if (!assignment.startDate || !assignment.weeklySchedule || !assignment.vocabularyData) {
      return null;
    }
    
    const startDate = new Date(assignment.startDate);
    startDate.setHours(0, 0, 0, 0);
    targetDate.setHours(0, 0, 0, 0);
    
    // 시작일 이전이면 null
    if (targetDate < startDate) return null;
    
    // 단원 목록 추출
    const unitOrder: string[] = [];
    const unitSet = new Set<string>();
    
    assignment.vocabularyData.forEach((word: any) => {
      if (!unitSet.has(word.unit)) {
        unitSet.add(word.unit);
        unitOrder.push(word.unit);
      }
    });
    
    const totalUnits = unitOrder.length;
    const startUnitIndex = assignment.startUnit ? unitOrder.indexOf(assignment.startUnit) : 0;
    
    // 주간 스케줄에서 활성화된 요일만 카운트
    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
    let studyDayCount = 0;
    
    // 시작일부터 목표일까지 학습일 수 계산
    const currentDate = new Date(startDate);
    while (currentDate <= targetDate) {
      const currentDayName = dayNames[currentDate.getDay()];
      
      if (assignment.weeklySchedule[currentDayName]?.isActive) {
        studyDayCount++;
        
        if (currentDate.getTime() === targetDate.getTime()) {
          const targetUnitIndex = startUnitIndex + (studyDayCount - 1);
          const currentUnitNumber = targetUnitIndex - startUnitIndex + 1;
          
          // 전체 단원을 완료했는지 확인
          const isCompleted = targetUnitIndex >= unitOrder.length;
          
          return {
            current: Math.min(currentUnitNumber, totalUnits),
            total: totalUnits,
            isCompleted
          };
        }
      }
      
      currentDate.setDate(currentDate.getDate() + 1);
    }
    
    return null;
  };

  // 과제가 이미 완료되었는지 확인
  const isAssignmentCompleted = (assignment: TaskAssignment, targetDate: Date): boolean => {
    if (!assignment.startDate || !assignment.weeklySchedule || !assignment.vocabularyData) {
      return false;
    }
    
    const startDate = new Date(assignment.startDate);
    startDate.setHours(0, 0, 0, 0);
    targetDate.setHours(0, 0, 0, 0);
    
    // 단원 목록 추출
    const unitOrder: string[] = [];
    const unitSet = new Set<string>();
    
    assignment.vocabularyData.forEach((word: any) => {
      if (!unitSet.has(word.unit)) {
        unitSet.add(word.unit);
        unitOrder.push(word.unit);
      }
    });
    
    const totalUnits = unitOrder.length;
    const startUnitIndex = assignment.startUnit ? unitOrder.indexOf(assignment.startUnit) : 0;
    
    // 주간 스케줄에서 활성화된 요일만 카운트
    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
    let studyDayCount = 0;
    let completedDate: Date | null = null;
    
    // 시작일부터 목표일까지 학습일 수 계산
    const currentDate = new Date(startDate);
    while (currentDate <= targetDate) {
      const currentDayName = dayNames[currentDate.getDay()];
      
      if (assignment.weeklySchedule[currentDayName]?.isActive) {
        studyDayCount++;
        const targetUnitIndex = startUnitIndex + (studyDayCount - 1);
        
        // 마지막 단원을 학습한 날짜 찾기
        if (targetUnitIndex === totalUnits - 1) {
          completedDate = new Date(currentDate);
          break;
        }
      }
      
      currentDate.setDate(currentDate.getDate() + 1);
    }
    
    // 완료 날짜가 있고, 목표일이 완료 날짜 이후이면 true
    return completedDate !== null && targetDate > completedDate;
  };

  const getUpcomingAssignments = () => {
    if (!student) return [];
    
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    
    const assignments: { date: Date; dayName: string; assignments: TaskAssignment[] }[] = [];
    
    // 오늘부터 3일간의 과제 수집
    for (let i = 0; i < 3; i++) {
      const targetDate = new Date(today);
      targetDate.setDate(today.getDate() + i);
      
      const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
      const dayName = dayNames[targetDate.getDay()];
      
      // 해당 날짜에 활성화된 과제 필터링
      const dayAssignments = student.taskAssignments.filter(assignment => {
        // 활성 상태인지 확인
        if (assignment.status !== 'active') return false;
        
        // 해당 요일의 스케줄이 활성화되어 있는지 확인
        const daySchedule = assignment.weeklySchedule?.[dayName];
        if (!daySchedule?.isActive) return false;
        
        // 시작일이 대상 날짜보다 늦으면 제외
        const assignmentStartDate = new Date(assignment.startDate);
        assignmentStartDate.setHours(0, 0, 0, 0);
        if (assignmentStartDate > targetDate) return false;
        
        // 종료일이 있고 대상 날짜보다 이전이면 제외
        if (assignment.endDate) {
          const assignmentEndDate = new Date(assignment.endDate);
          assignmentEndDate.setHours(0, 0, 0, 0);
          if (assignmentEndDate < targetDate) return false;
        }
        
        // 전체 학습이 완료된 과제는 제외
        if (isAssignmentCompleted(assignment, targetDate)) return false;
        
        return true;
      });
      
      if (dayAssignments.length > 0 || i === 0) { // 오늘은 과제가 없어도 표시
        assignments.push({
          date: targetDate,
          dayName: dayName,
          assignments: dayAssignments
        });
      }
    }
    
    return assignments;
  };

  if (!student) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Typography variant="h6">학생 정보를 불러오는 중...</Typography>
      </Box>
    );
  }

  const todayAssignments = getTodayAssignments();
  const activeAssignments = getActiveAssignments();
  const viewAssignments = getAssignmentsByViewMode();

  return (
    <Box sx={{ pb: isMobile ? 8 : 0 }}>
      {/* 모바일 앱바 */}
      {isMobile && (
        <AppBar position="sticky" elevation={1}>
          <Toolbar>
            <IconButton
              edge="start"
              color="inherit"
              onClick={() => navigate('/students')}
              sx={{ mr: 2 }}
            >
              <ArrowBackIcon />
            </IconButton>
            <Typography variant="h6" sx={{ flex: 1 }}>
              {student.name}
            </Typography>
            <Chip
              label={getGradeText(student.grade)}
              color={getGradeColor(student.grade) as any}
              size="small"
            />
          </Toolbar>
        </AppBar>
      )}

      <Container maxWidth="lg" sx={{ px: isMobile ? 2 : 3, py: isMobile ? 2 : 3 }}>
        {/* 데스크톱 학생 정보 헤더 */}
        {!isMobile && (
          <Box sx={{ mb: 4 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <Avatar sx={{ mr: 2, width: 64, height: 64 }}>
                {student.name.charAt(0)}
              </Avatar>
              <Box>
                <Typography variant="h4" component="h1">
                  {student.name}
                </Typography>
                <Box sx={{ display: 'flex', gap: 1, mt: 1 }}>
                  <Chip
                    label={getGradeText(student.grade)}
                    color={getGradeColor(student.grade) as any}
                    size="small"
                  />
                  <Chip
                    label={student.status === 'active' ? '활성' : '비활성'}
                    color={student.status === 'active' ? 'success' : 'error'}
                    size="small"
                  />
                </Box>
              </Box>
            </Box>
          </Box>
        )}

        {/* 모바일 학생 정보 카드 */}
        {isMobile && (
          <Card sx={{ mb: 3, borderRadius: 2 }}>
            <CardContent sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <Avatar sx={{ mr: 2, width: 48, height: 48 }}>
                  {student.name.charAt(0)}
                </Avatar>
                <Box sx={{ flex: 1 }}>
                  <Typography variant="h6">
                    {student.name}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {student.address}
                  </Typography>
                </Box>
              </Box>
              
              <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                <Chip
                  label={getGradeText(student.grade)}
                  color={getGradeColor(student.grade) as any}
                  size="small"
                />
                <Chip
                  label={student.status === 'active' ? '활성' : '비활성'}
                  color={student.status === 'active' ? 'success' : 'error'}
                  size="small"
                />
              </Box>

              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Box>
                  <Typography variant="body2" color="text.secondary">
                    연락처: {student.phone}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    가입일: {student.joinDate}
                  </Typography>
                </Box>
                <Box sx={{ textAlign: 'right' }}>
                  <Typography variant="h6" color="primary">
                    {student.taskAssignments.length}
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    배정된 과제
                  </Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        )}

        {/* 탭 네비게이션 */}
        <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
          <Tabs 
            value={tabValue} 
            onChange={handleTabChange}
            variant={isMobile ? "fullWidth" : "standard"}
            sx={{
              '& .MuiTab-root': {
                minHeight: isMobile ? 56 : 48,
                fontSize: isMobile ? '0.875rem' : '1rem',
              }
            }}
          >
            <Tab 
              icon={<TodayIcon />} 
              label="오늘의 과제" 
              iconPosition="start"
              sx={{ 
                '& .MuiTab-iconWrapper': { mr: 1 },
                '& .MuiTab-labelIcon': { flexDirection: 'row' }
              }}
            />
            <Tab 
              icon={<ViewModuleIcon />} 
              label="과제 현황" 
              iconPosition="start"
              sx={{ 
                '& .MuiTab-iconWrapper': { mr: 1 },
                '& .MuiTab-labelIcon': { flexDirection: 'row' }
              }}
            />
            <Tab 
              icon={<SettingsIcon />} 
              label="설정" 
              iconPosition="start"
              sx={{ 
                '& .MuiTab-iconWrapper': { mr: 1 },
                '& .MuiTab-labelIcon': { flexDirection: 'row' }
              }}
            />
          </Tabs>
        </Box>

        {/* 오늘의 과제 탭 */}
        <TabPanel value={tabValue} index={0}>
          <Box sx={{ mb: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2, flexWrap: 'wrap', gap: 2 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, flexWrap: 'wrap' }}>
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <TodayIcon sx={{ mr: 1, color: 'primary.main' }} />
                  <Typography variant={isMobile ? "h6" : "h5"} sx={{ fontWeight: 600 }}>
                    {getViewTitle()} ({viewAssignments.length}개)
                  </Typography>
                </Box>
                {viewMode === 'week' && (
                  <Box sx={{ 
                    display: 'flex', 
                    alignItems: 'center',
                    ml: isMobile ? 0 : 2,
                    mt: isMobile ? 1 : 0
                  }}>
                    <IconButton 
                      onClick={() => setWeekOffset(weekOffset - 1)}
                      size="small"
                      sx={{ border: '1px solid', borderColor: 'divider' }}
                    >
                      <NavigateBeforeIcon />
                    </IconButton>
                    <Button
                      size="small"
                      onClick={() => setWeekOffset(0)}
                      disabled={weekOffset === 0}
                      sx={{ mx: 1, minWidth: 'auto' }}
                    >
                      이번 주
                    </Button>
                    <IconButton 
                      onClick={() => setWeekOffset(weekOffset + 1)}
                      size="small"
                      sx={{ border: '1px solid', borderColor: 'divider' }}
                    >
                      <NavigateNextIcon />
                    </IconButton>
                  </Box>
                )}
                {viewMode === 'month' && (
                  <Box sx={{ 
                    display: 'flex', 
                    alignItems: 'center',
                    ml: isMobile ? 0 : 2,
                    mt: isMobile ? 1 : 0
                  }}>
                    <IconButton 
                      onClick={() => setMonthOffset(monthOffset - 1)}
                      size="small"
                      sx={{ border: '1px solid', borderColor: 'divider' }}
                    >
                      <NavigateBeforeIcon />
                    </IconButton>
                    <Button
                      size="small"
                      onClick={() => setMonthOffset(0)}
                      disabled={monthOffset === 0}
                      sx={{ mx: 1, minWidth: 'auto' }}
                    >
                      이번 달
                    </Button>
                    <IconButton 
                      onClick={() => setMonthOffset(monthOffset + 1)}
                      size="small"
                      sx={{ border: '1px solid', borderColor: 'divider' }}
                    >
                      <NavigateNextIcon />
                    </IconButton>
                  </Box>
                )}
              </Box>
              
              <ToggleButtonGroup
                value={viewMode}
                exclusive
                onChange={(event, newMode) => {
                  if (newMode !== null) {
                    setViewMode(newMode);
                    setWeekOffset(0); // 모드 변경 시 주차 초기화
                    setMonthOffset(0); // 모드 변경 시 월 초기화
                  }
                }}
                size={isMobile ? "small" : "medium"}
              >
                <ToggleButton value="today">
                  <TodayIcon sx={{ mr: 0.5 }} />
                  오늘
                </ToggleButton>
                <ToggleButton value="week">
                  <DateRangeIcon sx={{ mr: 0.5 }} />
                  주간
                </ToggleButton>
                <ToggleButton value="month">
                  <CalendarMonthIcon sx={{ mr: 0.5 }} />
                  월간
                </ToggleButton>
              </ToggleButtonGroup>
            </Box>
            
            {viewAssignments.length > 0 ? (
              viewMode === 'week' ? (
                // 주간 보기: 요일별로 그룹화
                <Box>
                  {(() => {
                    const weeklyGroups = getWeeklyGroupedAssignments();
                    const dayNamesKorean = ['일', '월', '화', '수', '목', '금', '토'];
                    const dayNamesDisplay = ['월', '화', '수', '목', '금', '토', '일'];
                    const today = new Date().getDay();
                    const todayIndex = today === 0 ? 6 : today - 1; // 일요일을 마지막으로
                    
                    // 요일 인덱스를 실제 요일 번호로 변환 (월=1, 화=2, ..., 일=0)
                    const dayIndexToWeekday = (index: number) => {
                      return index === 6 ? 0 : index + 1;
                    };
                    
                    return (
                      <Grid container spacing={2}>
                        {dayNamesDisplay.map((day, index) => {
                          const koreanDay = dayNamesKorean[dayIndexToWeekday(index)];
                          const assignments = weeklyGroups?.[koreanDay] || [];
                          const isToday = index === todayIndex;
                          const weekdayNumber = dayIndexToWeekday(index);
                          
                          // 이번 주의 해당 요일 날짜 계산
                          const thisWeekDate = new Date();
                          const currentDay = thisWeekDate.getDay();
                          const diff = weekdayNumber - currentDay;
                          thisWeekDate.setDate(thisWeekDate.getDate() + diff + (weekOffset * 7));
                          thisWeekDate.setHours(0, 0, 0, 0);
                          
                          const todayDate = new Date();
                          todayDate.setHours(0, 0, 0, 0);
                          
                          // 오늘보다 과거인지 확인
                          const isPast = thisWeekDate < todayDate;
                          
                          // 오늘인지 확인 (weekOffset이 0일 때만)
                          const isRealToday = weekOffset === 0 && isToday;
                          
                          // 완료된 이후의 과제는 필터링
                          const activeAssignments = assignments.filter(assignment => 
                            !isAssignmentCompleted(assignment, thisWeekDate)
                          );
                          
                          return (
                            <Grid item xs={12} sm={6} md key={day}>
                              <Box sx={{ 
                                height: '100%',
                                border: isRealToday ? '2px solid' : '1px solid',
                                borderColor: isRealToday ? 'primary.main' : isPast ? 'grey.400' : 'grey.300',
                                borderRadius: 2,
                                p: 2,
                                bgcolor: isRealToday ? 'primary.50' : isPast ? 'grey.50' : 'background.paper',
                                opacity: isPast ? 0.7 : 1
                              }}>
                                <Box sx={{ 
                                  display: 'flex', 
                                  alignItems: 'center', 
                                  mb: 2,
                                  pb: 1,
                                  borderBottom: '1px solid',
                                  borderColor: 'divider'
                                }}>
                                  <Typography 
                                    variant="subtitle1" 
                                    sx={{ 
                                      fontWeight: isRealToday ? 700 : 600,
                                      color: isRealToday ? 'primary.main' : isPast ? 'text.disabled' : 'text.primary'
                                    }}
                                  >
                                    {day}
                                  </Typography>
                                  {isRealToday && (
                                    <Chip 
                                      label="오늘" 
                                      size="small" 
                                      color="primary" 
                                      sx={{ ml: 1 }}
                                    />
                                  )}
                                  {isPast && (
                                    <Chip 
                                      label="지남" 
                                      size="small" 
                                      color="default" 
                                      sx={{ ml: 1 }}
                                    />
                                  )}
                                  <Typography variant="caption" color="text.secondary" sx={{ ml: 'auto' }}>
                                    {activeAssignments.length}개
                                  </Typography>
                                </Box>
                                
                                <Box sx={{ 
                                  maxHeight: 400, 
                                  overflowY: 'auto',
                                  '&::-webkit-scrollbar': {
                                    width: '8px',
                                  },
                                  '&::-webkit-scrollbar-track': {
                                    background: '#f1f1f1',
                                    borderRadius: '4px',
                                  },
                                  '&::-webkit-scrollbar-thumb': {
                                    background: '#888',
                                    borderRadius: '4px',
                                  },
                                  '&::-webkit-scrollbar-thumb:hover': {
                                    background: '#555',
                                  },
                                }}>
                                  {activeAssignments.length > 0 ? (
                                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                                      {activeAssignments.map((assignment, idx) => (
                                        <Card 
                                          key={`${day}-${idx}`}
                                          sx={{ 
                                            borderRadius: 1,
                                            boxShadow: 1,
                                            '&:hover': {
                                              boxShadow: isPast ? 1 : 3,
                                            }
                                          }}
                                        >
                                          <CardContent sx={{ p: 2 }}>
                                            <Typography 
                                              variant="subtitle2" 
                                              sx={{ 
                                                fontWeight: 600, 
                                                mb: 0.5,
                                                color: isPast ? 'text.disabled' : 'text.primary',
                                                fontSize: '0.875rem',
                                                textDecoration: (() => {
                                                  const progress = getUnitProgressForDate(assignment, thisWeekDate);
                                                  return progress?.isCompleted ? 'line-through' : 'none';
                                                })()
                                              }}
                                            >
                                              {(() => {
                                                const unit = getUnitForDate(assignment, thisWeekDate);
                                                const area = getAreaText(assignment.area);
                                                const taskTitle = assignment.taskTitle;
                                                const progress = getUnitProgressForDate(assignment, thisWeekDate);
                                                
                                                // "영역/교재명 (진행상황)" 형식으로 표시
                                                if (progress) {
                                                  return progress.isCompleted 
                                                    ? `${area}/${taskTitle} (${progress.current}/${progress.total}✓)`
                                                    : `${area}/${taskTitle} (${progress.current}/${progress.total})`;
                                                }
                                                
                                                return `${area}/${taskTitle}`;
                                              })()}
                                            </Typography>
                                            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
                                              <Typography 
                                                variant="caption" 
                                                color={isPast ? 'text.disabled' : 'text.secondary'} 
                                                display="block"
                                              >
                                                일일 학습량: {assignment.weeklySchedule?.[koreanDay]?.dailyAmount || '0'}개
                                              </Typography>
                                              {(() => {
                                                const taskHistory = student?.learningHistory?.filter(
                                                  h => h.taskId === assignment.taskId
                                                ) || [];
                                                
                                                if (taskHistory.length > 0) {
                                                  return (
                                                    <Chip 
                                                      label={`${taskHistory.length}회`}
                                                      size="small" 
                                                      color="info"
                                                      sx={{ height: 16, fontSize: '0.6rem' }}
                                                    />
                                                  );
                                                }
                                                
                                                const progress = getUnitProgressForDate(assignment, thisWeekDate);
                                                if (progress?.isCompleted) {
                                                  return (
                                                    <Chip 
                                                      label="완료" 
                                                      size="small" 
                                                      color="success"
                                                      sx={{ height: 20, fontSize: '0.7rem' }}
                                                    />
                                                  );
                                                }
                                                return null;
                                              })()}
                                            </Box>
                                            <Box sx={{ display: 'flex', gap: 1 }}>
                                              <Button
                                                size="small"
                                                variant="contained"
                                                disabled={isPast}
                                                onClick={() => handleStartVocabularyLearning(assignment, thisWeekDate)}
                                                sx={{
                                                  flex: 1,
                                                  ...(isPast && {
                                                    bgcolor: 'grey.300',
                                                    color: 'grey.600',
                                                    '&.Mui-disabled': {
                                                      bgcolor: 'grey.300',
                                                      color: 'grey.600',
                                                    }
                                                  })
                                                }}
                                              >
                                                {isPast ? '학습 종료' : '학습'}
                                              </Button>
                                              <Button
                                                size="small"
                                                variant="outlined"
                                                disabled={isPast}
                                                onClick={() => handleStartEvaluation(assignment, thisWeekDate)}
                                                sx={{
                                                  flex: 1,
                                                  ...(isPast && {
                                                    borderColor: 'grey.300',
                                                    color: 'grey.600',
                                                    '&.Mui-disabled': {
                                                      borderColor: 'grey.300',
                                                      color: 'grey.600',
                                                    }
                                                  })
                                                }}
                                              >
                                                평가
                                              </Button>
                                              <IconButton
                                                size="small"
                                                onClick={() => handleOpenDetailDialog(assignment)}
                                                disabled={isPast}
                                              >
                                                <InfoIcon fontSize="small" />
                                              </IconButton>
                                            </Box>
                                          </CardContent>
                                        </Card>
                                      ))}
                                    </Box>
                                  ) : (
                                    <Box sx={{ 
                                      display: 'flex', 
                                      alignItems: 'center', 
                                      justifyContent: 'center',
                                      height: 100,
                                      color: 'text.secondary'
                                    }}>
                                      <Typography variant="caption">
                                        과제 없음
                                      </Typography>
                                    </Box>
                                  )}
                                </Box>
                              </Box>
                            </Grid>
                          );
                        })}
                      </Grid>
                    );
                  })()}
                </Box>
              ) : viewMode === 'month' ? (
                // 월간 보기: 캘린더 형태
                <Box>
                  <TableContainer component={Paper} sx={{ mb: 2 }}>
                    <Table size="small">
                      <TableHead>
                        <TableRow>
                          {['월', '화', '수', '목', '금', '토', '일'].map((day) => (
                            <TableCell key={day} align="center" sx={{ fontWeight: 600, width: '14.28%' }}>
                              {day}
                            </TableCell>
                          ))}
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {getMonthCalendarData().map((week, weekIndex) => (
                          <TableRow key={weekIndex}>
                            {week.map((date, dayIndex) => {
                              if (!date) {
                                return <TableCell key={dayIndex} sx={{ height: 120, bgcolor: 'grey.50' }} />;
                              }
                              
                              const dateString = date.toISOString().split('T')[0];
                              const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
                              const dayName = dayNames[date.getDay()];
                              
                              // 현재 실제 오늘 날짜 (monthOffset과 관계없이)
                              const actualToday = new Date();
                              actualToday.setHours(0, 0, 0, 0);
                              
                              const isToday = date.getTime() === actualToday.getTime() && monthOffset === 0;
                              const isPast = date < actualToday;
                              
                              // 해당 날짜에 활성화된 과제 찾기 (전체 학습 완료된 과제만 제외)
                              const dayAssignments = (() => {
                                // viewAssignments에서 시작 (이미 활성 과제만 필터링됨)
                                const baseAssignments = viewAssignments.filter(assignment => {
                                  // 시작일이 현재 날짜보다 늦으면 제외
                                  const assignmentStartDate = new Date(assignment.startDate);
                                  if (assignmentStartDate > date) return false;
                                  
                                  // 종료일이 있고 현재 날짜보다 이전이면 제외
                                  if (assignment.endDate) {
                                    const assignmentEndDate = new Date(assignment.endDate);
                                    if (assignmentEndDate < date) return false;
                                  }
                                  
                                  // 전체 학습이 완료된 과제만 제외 (주간 일정과 동일한 로직)
                                  if (isAssignmentCompleted(assignment, date)) return false;
                                  
                                  return true;
                                });
                                
                                // 오늘인 경우에도 요일별 스케줄 확인 (주간 일정과 동일하게)
                                // 오늘이 아닌 날짜와 동일한 로직 적용
                                return baseAssignments.filter(assignment => 
                                  assignment.weeklySchedule?.[dayName]?.isActive
                                );
                              })();
                              
                              return (
                                <TableCell 
                                  key={dayIndex} 
                                  sx={{ 
                                    height: 120, 
                                    verticalAlign: 'top',
                                    p: 1,
                                    bgcolor: isToday ? 'primary.50' : isPast ? 'grey.50' : 'background.paper',
                                    border: isToday ? '2px solid' : '1px solid',
                                    borderColor: isToday ? 'primary.main' : 'divider',
                                    position: 'relative',
                                    cursor: dayAssignments.length > 0 ? 'pointer' : 'default',
                                    '&:hover': {
                                      bgcolor: dayAssignments.length > 0 
                                        ? (isToday ? 'primary.100' : isPast ? 'grey.100' : 'grey.50')
                                        : undefined
                                    }
                                  }}
                                >
                                  <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                                    <Typography 
                                      variant="body2" 
                                      sx={{ 
                                        fontWeight: isToday ? 700 : 500,
                                        color: isToday ? 'primary.main' : isPast ? 'text.disabled' : 'text.primary'
                                      }}
                                    >
                                      {date.getDate()}
                                    </Typography>
                                    {dayAssignments.length > 0 && (
                                      <Chip 
                                        label={dayAssignments.length} 
                                        size="small" 
                                        color={isPast ? 'default' : 'primary'}
                                        sx={{ height: 20, fontSize: '0.75rem' }}
                                      />
                                    )}
                                  </Box>
                                  
                                  <Box sx={{ 
                                    display: 'flex', 
                                    flexDirection: 'column', 
                                    gap: 0.5,
                                    overflow: 'hidden'
                                  }}>
                                    {dayAssignments.slice(0, 2).map((assignment, idx) => (
                                      <Box
                                        key={idx}
                                        sx={{
                                          p: 0.5,
                                          bgcolor: isPast ? 'grey.200' : (isToday ? 'warning.100' : 'primary.100'),
                                          borderRadius: 0.5,
                                          border: isToday ? '1px solid' : 'none',
                                          borderColor: isToday ? 'warning.main' : 'transparent',
                                        }}
                                      >
                                        <Typography 
                                          variant="caption" 
                                          sx={{ 
                                            fontSize: '0.75rem',
                                            display: 'block',
                                            overflow: 'hidden',
                                            textOverflow: 'ellipsis',
                                            whiteSpace: 'nowrap',
                                            color: isPast ? 'text.disabled' : 'text.primary',
                                            fontWeight: 500,
                                            lineHeight: 1.2,
                                            mb: 0.5,
                                            textDecoration: (() => {
                                              const progress = getUnitProgressForDate(assignment, date);
                                              return progress?.isCompleted ? 'line-through' : 'none';
                                            })()
                                          }}
                                        >
                                          {(() => {
                                            const area = getAreaText(assignment.area);
                                            const taskTitle = assignment.taskTitle;
                                            
                                            // 오늘 날짜인 경우 전체 진행상황 표시
                                            if (isToday && monthOffset === 0) {
                                              const { completed, total } = assignment.progress;
                                              return `${area}/${taskTitle} (${completed}/${total})`;
                                            }
                                            
                                            // 다른 날짜는 기존 로직 사용
                                            const progress = getUnitProgressForDate(assignment, date);
                                            if (progress) {
                                              return progress.isCompleted 
                                                ? `${area}/${taskTitle} (${progress.current}/${progress.total}✓)`
                                                : `${area}/${taskTitle} (${progress.current}/${progress.total})`;
                                            }
                                            
                                            return `${area}/${taskTitle}`;
                                          })()}
                                        </Typography>
                                        
                                        {/* 학습/평가 버튼 */}
                                        <Box sx={{ display: 'flex', gap: 0.5 }}>
                                          <Button
                                            size="small"
                                            variant="contained"
                                            disabled={isPast}
                                            onClick={(e) => {
                                              e.stopPropagation();
                                              if (!isPast) {
                                                handleStartVocabularyLearning(assignment, date);
                                              }
                                            }}
                                            sx={{
                                              fontSize: '0.6rem',
                                              minWidth: '35px',
                                              height: '20px',
                                              px: 0.5,
                                              py: 0,
                                            }}
                                          >
                                            학습
                                          </Button>
                                          <Button
                                            size="small"
                                            variant="outlined"
                                            disabled={isPast}
                                            onClick={(e) => {
                                              e.stopPropagation();
                                              if (!isPast) {
                                                handleStartEvaluation(assignment, date);
                                              }
                                            }}
                                            sx={{
                                              fontSize: '0.6rem',
                                              minWidth: '35px',
                                              height: '20px',
                                              px: 0.5,
                                              py: 0,
                                            }}
                                          >
                                            평가
                                          </Button>
                                        </Box>
                                      </Box>
                                    ))}
                                    {dayAssignments.length > 2 && (
                                      <Typography 
                                        variant="caption" 
                                        sx={{ 
                                          fontSize: '0.7rem', 
                                          color: 'text.secondary',
                                          textAlign: 'center'
                                        }}
                                      >
                                        +{dayAssignments.length - 2}개 더
                                      </Typography>
                                    )}
                                  </Box>
                                </TableCell>
                              );
                            })}
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                </Box>
              ) : (
                // 오늘의 과제 보기: 3일간의 과제 표시
                viewMode === 'today' ? (
                  <Box>
                    {(() => {
                      // 더미 데이터인지 확인
                      const isDummy = viewAssignments.length === 1 && viewAssignments[0].taskId === 'dummy';
                      const upcomingDays = getUpcomingAssignments();
                      
                      return (
                        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                          {upcomingDays.map((dayData, dayIndex) => {
                            const dateLabel = dayIndex === 0 ? '오늘' : 
                                            dayIndex === 1 ? '내일' : '모레';
                            const dateString = `${dayData.date.getMonth() + 1}월 ${dayData.date.getDate()}일 (${dayData.dayName})`;
                            
                            return (
                              <Box key={dayIndex}>
                                <Typography 
                                  variant="h6" 
                                  sx={{ 
                                    mb: 2, 
                                    fontWeight: 600,
                                    color: dayIndex === 0 ? 'primary.main' : 'text.primary'
                                  }}
                                >
                                  {dateLabel} - {dateString}
                                </Typography>
                                
                                {dayData.assignments.length > 0 ? (
                                  <Grid container spacing={2}>
                                    {dayData.assignments.map((assignment, idx) => (
                                      <Grid item xs={12} key={idx}>
                                        <Card 
                                          sx={{ 
                                            borderRadius: isMobile ? 2 : 1,
                                            border: dayIndex === 0 ? '2px solid' : '1px solid',
                                            borderColor: dayIndex === 0 ? 'primary.main' : 'grey.300',
                                            backgroundColor: dayIndex === 0 ? 'primary.50' : 'background.paper',
                                            '&:hover': { 
                                              boxShadow: 3,
                                              transform: 'translateY(-2px)',
                                              transition: 'all 0.2s ease-in-out'
                                            }
                                          }}
                                        >
                                          <CardContent sx={{ p: isMobile ? 3 : 2 }}>
                                            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
                                              <Box sx={{ flex: 1 }}>
                                                <Typography variant="h6" sx={{ mb: 1, fontWeight: 600 }}>
                                                  {assignment.taskTitle}
                                                </Typography>
                                                <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                                                  <AssignmentIcon sx={{ fontSize: 16, mr: 0.5, color: 'primary.main' }} />
                                                  <Typography variant="body2" color="primary" sx={{ fontWeight: 500 }}>
                                                    {getAreaText(assignment.area)} 영역
                                                  </Typography>
                                                </Box>
                                              </Box>
                                              <Chip
                                                label={`${dateLabel} 학습`}
                                                color={dayIndex === 0 ? 'primary' : 'default'}
                                                size="small"
                                                sx={{ fontWeight: 600 }}
                                              />
                                            </Box>

                                            {/* 진행률 */}
                                            <Box sx={{ mb: 3 }}>
                                              <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                                                <Typography variant="body2" sx={{ fontWeight: 500 }}>
                                                  진행률
                                                </Typography>
                                                <Typography variant="body2" color="primary" sx={{ fontWeight: 600 }}>
                                                  {assignment.progress.completed} / {assignment.progress.total}
                                                </Typography>
                                              </Box>
                                              <LinearProgress
                                                variant="determinate"
                                                value={(assignment.progress.completed / assignment.progress.total) * 100}
                                                sx={{ 
                                                  height: 8, 
                                                  borderRadius: 4,
                                                  backgroundColor: 'grey.200',
                                                  '& .MuiLinearProgress-bar': {
                                                    borderRadius: 4,
                                                  }
                                                }}
                                              />
                                            </Box>

                                            {/* 학습량 */}
                                            <Box sx={{ mb: 2 }}>
                                              <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                                                일일 학습량:
                                              </Typography>
                                              <Typography variant="h6" color="primary" sx={{ fontWeight: 600 }}>
                                                {assignment.weeklySchedule?.[dayData.dayName]?.dailyAmount || '0'}개
                                              </Typography>
                                            </Box>

                                            {/* 학습 이력 */}
                                            <Box sx={{ mb: 2 }}>
                                              <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                                                학습 이력:
                                              </Typography>
                                              <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                                                {(() => {
                                                  // 해당 날짜의 학습 이력만 필터링
                                                  const targetDateString = dayData.date.toISOString().split('T')[0];
                                                  
                                                  const taskHistory = student?.learningHistory?.filter(
                                                    h => {
                                                      if (h.taskId !== assignment.taskId) return false;
                                                      
                                                      const historyDate = new Date(h.date);
                                                      historyDate.setHours(0, 0, 0, 0);
                                                      const historyDateString = historyDate.toISOString().split('T')[0];
                                                      
                                                      return historyDateString === targetDateString;
                                                    }
                                                  ) || [];
                                                  
                                                  if (taskHistory.length === 0) {
                                                    return (
                                                      <Chip 
                                                        label={`${dateLabel} 학습 전`}
                                                        size="small" 
                                                        variant="outlined"
                                                        color="default"
                                                      />
                                                    );
                                                  }
                                                  
                                                  return taskHistory.map((history, idx) => (
                                                    <Chip 
                                                      key={idx}
                                                      label={`${dateLabel} ${history.learningSession || idx + 1}회차 완료`}
                                                      size="small" 
                                                      color="success"
                                                      variant="filled"
                                                    />
                                                  ));
                                                })()}
                                              </Box>
                                            </Box>

                                            {/* 액션 버튼 */}
                                            <Box sx={{ display: 'flex', gap: 1, mt: 2 }}>
                                              <Button
                                                variant="contained"
                                                startIcon={<PlayArrowIcon />}
                                                size={isMobile ? "large" : "medium"}
                                                sx={{ flex: 1 }}
                                                onClick={() => handleStartVocabularyLearning(assignment, dayData.date)}
                                              >
                                                학습 시작
                                              </Button>
                                              <Button
                                                variant="outlined"
                                                size={isMobile ? "large" : "medium"}
                                                sx={{ flex: 1 }}
                                                onClick={() => handleStartEvaluation(assignment, dayData.date)}
                                              >
                                                평가
                                              </Button>
                                            </Box>
                                          </CardContent>
                                        </Card>
                                      </Grid>
                                    ))}
                                  </Grid>
                                ) : (
                                  <Card sx={{ borderRadius: isMobile ? 2 : 1, textAlign: 'center', bgcolor: 'grey.50' }}>
                                    <CardContent sx={{ py: 3 }}>
                                      <Typography variant="body1" color="text.secondary">
                                        {dateLabel} 과제가 없습니다
                                      </Typography>
                                    </CardContent>
                                  </Card>
                                )}
                              </Box>
                            );
                          })}
                        </Box>
                      );
                    })()}
                  </Box>
                ) : (
                  // 기존 월간 보기
                  <Grid container spacing={isMobile ? 2 : 3}>
                    {viewAssignments.map((assignment, index) => (
                      <Grid item xs={12} key={index}>
                        <Card 
                          sx={{ 
                            borderRadius: isMobile ? 2 : 1,
                            border: '2px solid',
                            borderColor: 'primary.main',
                            backgroundColor: 'primary.50',
                            '&:hover': { 
                              boxShadow: 3,
                              transform: 'translateY(-2px)',
                              transition: 'all 0.2s ease-in-out'
                            }
                          }}
                        >
                          <CardContent sx={{ p: isMobile ? 3 : 2 }}>
                            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
                              <Box sx={{ flex: 1 }}>
                                <Typography variant="h6" sx={{ mb: 1, fontWeight: 600 }}>
                                  {assignment.taskTitle}
                                </Typography>
                                <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                                  <AssignmentIcon sx={{ fontSize: 16, mr: 0.5, color: 'primary.main' }} />
                                  <Typography variant="body2" color="primary" sx={{ fontWeight: 500 }}>
                                    {getAreaText(assignment.area)} 영역
                                  </Typography>
                                </Box>
                              </Box>
                              <Chip
                                label={getChipLabel(viewMode)}
                                color="primary"
                                size="small"
                                sx={{ fontWeight: 600 }}
                              />
                            </Box>

                            {/* 진행률 */}
                            <Box sx={{ mb: 3 }}>
                              <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                                <Typography variant="body2" sx={{ fontWeight: 500 }}>
                                  진행률
                                </Typography>
                                <Typography variant="body2" color="primary" sx={{ fontWeight: 600 }}>
                                  {assignment.progress.completed} / {assignment.progress.total}
                                </Typography>
                              </Box>
                              <LinearProgress
                                variant="determinate"
                                value={(assignment.progress.completed / assignment.progress.total) * 100}
                                sx={{ 
                                  height: 8, 
                                  borderRadius: 4,
                                  backgroundColor: 'grey.200',
                                  '& .MuiLinearProgress-bar': {
                                    borderRadius: 4,
                                  }
                                }}
                              />
                            </Box>

                            {/* 오늘 학습량 */}
                            <Box sx={{ mb: 2 }}>
                              <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                                오늘 학습량:
                              </Typography>
                              <Typography variant="h6" color="primary" sx={{ fontWeight: 600 }}>
                                {(() => {
                                  const today = new Date().getDay();
                                  const dayName = ['일', '월', '화', '수', '목', '금', '토'][today];
                                  const todaySchedule = assignment.weeklySchedule?.[dayName];
                                  
                                  if (!todaySchedule || !todaySchedule.isActive) {
                                    return '과제 없음';
                                  }
                                  
                                  return todaySchedule.dailyAmount || '0';
                                })()}
                              </Typography>
                            </Box>

                            {/* 학습 회차 표시 */}
                            <Box sx={{ mb: 2 }}>
                              <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                                학습 이력:
                              </Typography>
                              <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                                {(() => {
                                  // 오늘 날짜의 학습 이력만 필터링
                                  const today = new Date();
                                  today.setHours(0, 0, 0, 0);
                                  const todayString = today.toISOString().split('T')[0];
                                  
                                  const taskHistory = student?.learningHistory?.filter(
                                    h => {
                                      if (h.taskId !== assignment.taskId) return false;
                                      
                                      // 학습 날짜가 오늘인지 확인
                                      const historyDate = new Date(h.date);
                                      historyDate.setHours(0, 0, 0, 0);
                                      const historyDateString = historyDate.toISOString().split('T')[0];
                                      
                                      return historyDateString === todayString;
                                    }
                                  ) || [];
                                  
                                  if (taskHistory.length === 0) {
                                    return (
                                      <Chip 
                                        label="오늘 학습 전" 
                                        size="small" 
                                        variant="outlined"
                                        color="default"
                                      />
                                    );
                                  }
                                  
                                  return taskHistory.map((history, idx) => (
                                    <Chip 
                                      key={idx}
                                      label={`오늘 ${history.learningSession || idx + 1}회차 완료`}
                                      size="small" 
                                      color="success"
                                      variant="filled"
                                    />
                                  ));
                                })()}
                              </Box>
                            </Box>

                            {/* 액션 버튼 */}
                            <Box sx={{ display: 'flex', gap: 1, mt: 2 }}>
                              {(() => {
                                const today = new Date().getDay();
                                const dayName = ['일', '월', '화', '수', '목', '금', '토'][today];
                                const todaySchedule = assignment.weeklySchedule?.[dayName];
                                const hasTodayTask = todaySchedule && todaySchedule.isActive;

                                if (hasTodayTask) {
                                  return (
                                    <>
                                      <Button
                                        variant="contained"
                                        startIcon={<PlayArrowIcon />}
                                        size={isMobile ? "large" : "medium"}
                                        sx={{ flex: 1 }}
                                        onClick={() => handleStartVocabularyLearning(assignment)}
                                      >
                                        학습 시작
                                      </Button>
                                      <Button
                                        variant="outlined"
                                        size={isMobile ? "large" : "medium"}
                                        sx={{ flex: 1 }}
                                        onClick={() => handleStartEvaluation(assignment)}
                                      >
                                        평가
                                      </Button>
                                    </>
                                  );
                                }
                                
                                return (
                                  <Typography variant="body2" color="text.secondary" sx={{ flex: 1, textAlign: 'center', py: 1 }}>
                                    오늘은 학습 예정일이 아닙니다
                                  </Typography>
                                );
                              })()}
                              <Button
                                variant="text"
                                size={isMobile ? "large" : "medium"}
                                onClick={() => handleOpenDetailDialog(assignment)}
                              >
                                상세보기
                              </Button>
                            </Box>
                          </CardContent>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>
                )
              )
            ) : (
              <Card sx={{ borderRadius: isMobile ? 2 : 1, textAlign: 'center' }}>
                <CardContent sx={{ py: isMobile ? 6 : 4 }}>
                  <CalendarIcon sx={{ 
                    fontSize: isMobile ? 80 : 64, 
                    color: 'text.secondary', 
                    mb: 2 
                  }} />
                  <Typography variant={isMobile ? "h6" : "h6"} color="text.secondary" sx={{ mb: 1, fontWeight: 600 }}>
                    {viewMode === 'today' ? '오늘의 과제가 없습니다' : viewMode === 'week' ? '이번 주 과제가 없습니다' : '이번 달 과제가 없습니다'}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
                    {viewMode === 'today' 
                      ? '오늘은 학습이 예정되어 있지 않습니다.'
                      : viewMode === 'week'
                      ? '이번 주에 배정된 과제가 없습니다.'
                      : '이번 달에 배정된 과제가 없습니다.'}
                  </Typography>
                  
                  {/* 다시 시작 버튼 */}
                  <Button
                    variant="contained"
                    color="primary"
                    size={isMobile ? "large" : "medium"}
                    onClick={handleRestartLearning}
                    startIcon={<RefreshIcon />}
                    sx={{
                      borderRadius: 2,
                      px: 3,
                      py: 1.5,
                      fontWeight: 600,
                      textTransform: 'none',
                      boxShadow: 2,
                      '&:hover': {
                        boxShadow: 4,
                        transform: 'translateY(-1px)',
                        transition: 'all 0.2s ease-in-out'
                      }
                    }}
                  >
                    학습 시작하기
                  </Button>
                </CardContent>
              </Card>
            )}
          </Box>
        </TabPanel>

        {/* 과제 현황 탭 */}
        <TabPanel value={tabValue} index={1}>
          <Box sx={{ mb: 3 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <ViewModuleIcon sx={{ mr: 1, color: 'primary.main' }} />
              <Typography variant={isMobile ? "h6" : "h5"} sx={{ fontWeight: 600 }}>
                과제 현황 ({activeAssignments.length}개)
              </Typography>
            </Box>

            {activeAssignments.length > 0 ? (
              <Grid container spacing={isMobile ? 2 : 3}>
                {activeAssignments.map((assignment, index) => (
                  <Grid item xs={12} sm={6} md={4} key={index}>
                    <Card 
                      sx={{ 
                        cursor: 'pointer',
                        borderRadius: isMobile ? 2 : 1,
                        '&:hover': { 
                          boxShadow: isMobile ? 2 : 3,
                          transform: isMobile ? 'none' : 'translateY(-2px)',
                          transition: 'all 0.2s ease-in-out'
                        },
                        minHeight: isMobile ? 200 : 'auto'
                      }}
                      onClick={() => handleOpenDetailDialog(assignment)}
                    >
                      <CardContent sx={{ p: isMobile ? 3 : 2 }}>
                        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
                          <Typography 
                            variant={isMobile ? "h6" : "h6"} 
                            sx={{ 
                              flex: 1, 
                              fontSize: isMobile ? '1rem' : '1.25rem',
                              lineHeight: 1.3
                            }}
                          >
                            {assignment.taskTitle}
                          </Typography>
                          <Chip
                            label={getStatusText(assignment.status)}
                            color={getStatusColor(assignment.status) as any}
                            size="small"
                            sx={{ ml: 1 }}
                          />
                        </Box>

                        <Box sx={{ mb: 2 }}>
                          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                            <AssignmentIcon sx={{ fontSize: 16, mr: 0.5, color: 'primary.main' }} />
                            <Typography variant="body2" color="primary" sx={{ fontWeight: 500 }}>
                              {getAreaText(assignment.area)} 영역
                            </Typography>
                          </Box>
                          
                          {/* 진행률 */}
                          <Box sx={{ mb: 2 }}>
                            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                              <Typography variant="body2" sx={{ fontWeight: 500 }}>
                                진행률
                              </Typography>
                              <Typography variant="body2" color="primary" sx={{ fontWeight: 600 }}>
                                {assignment.progress.completed} / {assignment.progress.total}
                              </Typography>
                            </Box>
                            <LinearProgress
                              variant="determinate"
                              value={(assignment.progress.completed / assignment.progress.total) * 100}
                              sx={{ 
                                height: isMobile ? 8 : 6, 
                                borderRadius: 4,
                                backgroundColor: 'grey.200',
                                '& .MuiLinearProgress-bar': {
                                  borderRadius: 4,
                                }
                              }}
                            />
                          </Box>

                          {/* 시작 단원 (단어장인 경우) */}
                          {assignment.area === 'vocabulary' && assignment.startUnit && (
                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                              <InfoIcon sx={{ fontSize: 16, mr: 0.5, color: 'text.secondary' }} />
                              <Typography variant="body2" color="text.secondary">
                                시작 단원: {assignment.startUnit}
                              </Typography>
                            </Box>
                          )}

                          {/* 학습 회차 표시 */}
                          <Box sx={{ mb: 2 }}>
                            <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mb: 0.5 }}>
                              학습 이력:
                            </Typography>
                            <Box sx={{ display: 'flex', gap: 0.5, flexWrap: 'wrap' }}>
                              {(() => {
                                // 오늘 날짜의 학습 이력만 필터링
                                const today = new Date();
                                today.setHours(0, 0, 0, 0);
                                const todayString = today.toISOString().split('T')[0];
                                
                                const taskHistory = student?.learningHistory?.filter(
                                  h => {
                                    if (h.taskId !== assignment.taskId) return false;
                                    
                                    // 학습 날짜가 오늘인지 확인
                                    const historyDate = new Date(h.date);
                                    historyDate.setHours(0, 0, 0, 0);
                                    const historyDateString = historyDate.toISOString().split('T')[0];
                                    
                                    return historyDateString === todayString;
                                  }
                                ) || [];
                                
                                if (taskHistory.length === 0) {
                                  return (
                                    <Chip 
                                      label="오늘 미학습" 
                                      size="small" 
                                      variant="outlined"
                                      color="default"
                                      sx={{ height: 20, fontSize: '0.75rem' }}
                                    />
                                  );
                                }
                                
                                return (
                                  <Chip 
                                    label={`오늘 ${taskHistory.length}회 학습 완료`}
                                    size="small" 
                                    color="success"
                                    variant="outlined"
                                    sx={{ height: 20, fontSize: '0.75rem' }}
                                  />
                                );
                              })()}
                            </Box>
                          </Box>

                          {/* 스케줄 요약 */}
                          <Box sx={{ mt: 2 }}>
                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                              <CalendarIcon sx={{ fontSize: 16, mr: 0.5, color: 'text.secondary' }} />
                              <Typography variant="body2" color="text.secondary">
                                학습 요일:
                              </Typography>
                            </Box>
                            <Box sx={{ display: 'flex', gap: 0.5, flexWrap: 'wrap' }}>
                              {Object.entries(assignment.weeklySchedule)
                                .filter(([_, schedule]) => schedule.isActive)
                                .map(([day, schedule]) => (
                                  <Chip
                                    key={day}
                                    label={`${day} ${schedule.dailyAmount}`}
                                    size="small"
                                    variant="outlined"
                                    sx={{ fontSize: '0.75rem' }}
                                  />
                                ))}
                            </Box>
                          </Box>
                        </Box>

                        <Box sx={{ 
                          display: 'flex', 
                          justifyContent: 'space-between', 
                          alignItems: 'center',
                          mt: 'auto'
                        }}>
                          <Typography variant="caption" color="text.secondary">
                            시작: {assignment.startDate}
                          </Typography>
                          <Button
                            size="small"
                            variant="outlined"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleOpenDetailDialog(assignment);
                            }}
                            sx={{ 
                              minWidth: 'auto',
                              px: isMobile ? 2 : 1.5,
                              py: isMobile ? 1 : 0.5
                            }}
                          >
                            상세보기
                          </Button>
                        </Box>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            ) : (
              <Card sx={{ borderRadius: isMobile ? 2 : 1 }}>
                <CardContent sx={{ 
                  textAlign: 'center', 
                  py: isMobile ? 6 : 4,
                  px: isMobile ? 3 : 2
                }}>
                  <AssignmentIcon sx={{ 
                    fontSize: isMobile ? 80 : 64, 
                    color: 'text.secondary', 
                    mb: 2 
                  }} />
                  <Typography variant={isMobile ? "h6" : "h6"} color="text.secondary" sx={{ mb: 1 }}>
                    활성 과제가 없습니다
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    관리자에게 과제 배정을 요청하세요.
                  </Typography>
                </CardContent>
              </Card>
            )}
          </Box>
        </TabPanel>

        {/* 설정 탭 */}
        <TabPanel value={tabValue} index={2}>
          <Box sx={{ mb: 3 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
              <SettingsIcon sx={{ mr: 1, color: 'primary.main' }} />
              <Typography variant={isMobile ? "h6" : "h5"} sx={{ fontWeight: 600 }}>
                학습 설정
              </Typography>
            </Box>

            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <Card sx={{ borderRadius: isMobile ? 2 : 1 }}>
                  <CardContent sx={{ p: isMobile ? 3 : 2 }}>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                      <TimerIcon sx={{ mr: 1, color: 'primary.main' }} />
                      <Typography variant="h6" sx={{ fontWeight: 600 }}>
                        단어 학습 타이머 설정
                      </Typography>
                    </Box>
                    
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
                      단어의 뜻을 확인한 후 다음 단어로 넘어가는 시간을 설정합니다.
                    </Typography>

                    <Box sx={{ px: 2, mb: 3 }}>
                      <Slider
                        value={timerDuration}
                        onChange={(e, value) => setTimerDuration(value as number)}
                        min={0.5}
                        max={5}
                        step={0.5}
                        marks={[
                          { value: 0.5, label: '0.5초' },
                          { value: 1.5, label: '1.5초' },
                          { value: 3, label: '3초' },
                          { value: 5, label: '5초' },
                        ]}
                        valueLabelDisplay="on"
                        valueLabelFormat={(value) => `${value}초`}
                      />
                    </Box>

                    <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                      <Typography variant="body2" color="text.secondary">
                        현재 설정: {timerDuration}초
                      </Typography>
                      <Button
                        variant="contained"
                        onClick={() => {
                          localStorage.setItem(`timer_duration_${studentId}`, timerDuration.toString());
                          alert('설정이 저장되었습니다.');
                        }}
                      >
                        저장
                      </Button>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>

              {/* 학습 이력 초기화 (개발/테스트용) */}
              <Grid item xs={12} md={6}>
                <Card sx={{ borderRadius: isMobile ? 2 : 1 }}>
                  <CardContent sx={{ p: isMobile ? 3 : 2 }}>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                      <RefreshIcon sx={{ mr: 1, color: 'warning.main' }} />
                      <Typography variant="h6" sx={{ fontWeight: 600 }}>
                        학습 이력 초기화 (테스트용)
                      </Typography>
                    </Box>
                    
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
                      테스트를 위해 이 학생의 모든 학습 이력을 초기화합니다.
                      <br />
                      <strong style={{ color: '#d32f2f' }}>주의: 이 작업은 되돌릴 수 없습니다!</strong>
                    </Typography>

                    <Box sx={{ display: 'flex', gap: 2 }}>
                      <Button
                        variant="outlined"
                        color="warning"
                        onClick={() => {
                          if (window.confirm('정말로 이 학생의 모든 학습 이력을 초기화하시겠습니까?\n\n이 작업은 되돌릴 수 없습니다!')) {
                            if (!student) return;
                            
                            const updatedStudent = {
                              ...student,
                              learningHistory: [],
                              evaluationHistory: []
                            };
                            
                            // 과제 진행률도 초기화
                            updatedStudent.taskAssignments = updatedStudent.taskAssignments.map(assignment => ({
                              ...assignment,
                              progress: {
                                ...assignment.progress,
                                completed: 0
                              }
                            }));
                            
                            const students = loadStudents();
                            const studentIndex = students.findIndex(s => s.id === student.id);
                            if (studentIndex !== -1) {
                              students[studentIndex] = updatedStudent;
                              saveStudents(students);
                              setStudent(updatedStudent);
                              alert('학습 이력이 초기화되었습니다.');
                            }
                          }
                        }}
                      >
                        학습 이력 초기화
                      </Button>

                      <Button
                        variant="outlined"
                        color="info"
                        onClick={() => {
                          if (!student) return;
                          console.log('Current Learning History:', student.learningHistory);
                          console.log('Current Evaluation History:', student.evaluationHistory);
                          alert(`학습 이력: ${student.learningHistory?.length || 0}개\n평가 이력: ${student.evaluationHistory?.length || 0}개\n\n자세한 내용은 개발자 도구 콘솔을 확인하세요.`);
                        }}
                      >
                        이력 확인
                      </Button>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>
          </Box>
        </TabPanel>

        {/* 데스크톱 과제 상세 다이얼로그 */}
        <Dialog open={openDetailDialog} onClose={handleCloseDetailDialog} maxWidth="md" fullWidth>
          <DialogTitle>
            {selectedAssignment?.taskTitle} - 상세 정보
          </DialogTitle>
          <DialogContent>
            {selectedAssignment && (
              <Box sx={{ pt: 2 }}>
                <Grid container spacing={3}>
                  <Grid item xs={12} md={6}>
                    <Typography variant="h6" sx={{ mb: 2 }}>
                      기본 정보
                    </Typography>
                    <Box sx={{ mb: 2 }}>
                      <Typography variant="body2" color="text.secondary">
                        영역: {getAreaText(selectedAssignment.area)}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        상태: {getStatusText(selectedAssignment.status)}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        시작일: {selectedAssignment.startDate}
                      </Typography>
                      {selectedAssignment.endDate && (
                        <Typography variant="body2" color="text.secondary">
                          종료일: {selectedAssignment.endDate}
                        </Typography>
                      )}
                      {selectedAssignment.area === 'vocabulary' && selectedAssignment.startUnit && (
                        <Typography variant="body2" color="text.secondary">
                          시작 단원: {selectedAssignment.startUnit}
                        </Typography>
                      )}
                    </Box>

                    <Typography variant="h6" sx={{ mb: 2 }}>
                      진행률
                    </Typography>
                    <Box sx={{ mb: 2 }}>
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                        <Typography variant="body2">
                          {selectedAssignment.progress.completed} / {selectedAssignment.progress.total} 완료
                        </Typography>
                        <Typography variant="body2">
                          {Math.round((selectedAssignment.progress.completed / selectedAssignment.progress.total) * 100)}%
                        </Typography>
                      </Box>
                      <LinearProgress
                        variant="determinate"
                        value={(selectedAssignment.progress.completed / selectedAssignment.progress.total) * 100}
                        sx={{ height: 12, borderRadius: 6 }}
                      />
                    </Box>
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <Typography variant="h6" sx={{ mb: 2 }}>
                      주간 스케줄
                    </Typography>
                    <TableContainer component={Paper} variant="outlined">
                      <Table size="small">
                        <TableHead>
                          <TableRow>
                            <TableCell>요일</TableCell>
                            <TableCell>학습량</TableCell>
                            <TableCell>상태</TableCell>
                          </TableRow>
                        </TableHead>
                        <TableBody>
                          {['월', '화', '수', '목', '금', '토', '일'].map((day) => {
                            const schedule = selectedAssignment.weeklySchedule[day];
                            return (
                              <TableRow key={day}>
                                <TableCell>{day}</TableCell>
                                <TableCell>
                                  {schedule?.isActive ? schedule.dailyAmount : '-'}
                                </TableCell>
                                <TableCell>
                                  {schedule?.isActive ? (
                                    <Chip label="활성" color="success" size="small" />
                                  ) : (
                                    <Chip label="비활성" color="default" size="small" />
                                  )}
                                </TableCell>
                              </TableRow>
                            );
                          })}
                        </TableBody>
                      </Table>
                    </TableContainer>
                  </Grid>
                </Grid>
              </Box>
            )}
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseDetailDialog}>닫기</Button>
          </DialogActions>
        </Dialog>

        {/* 모바일 과제 상세 바텀시트 */}
        <Drawer
          anchor="bottom"
          open={openMobileDetail}
          onClose={handleCloseDetailDialog}
          PaperProps={{
            sx: {
              borderTopLeftRadius: 16,
              borderTopRightRadius: 16,
              maxHeight: '80vh',
              minHeight: '50vh',
            }
          }}
        >
          {selectedAssignment && (
            <Box sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                <Typography variant="h6" sx={{ fontWeight: 600 }}>
                  {selectedAssignment.taskTitle}
                </Typography>
                <IconButton onClick={handleCloseDetailDialog}>
                  <CloseIcon />
                </IconButton>
              </Box>

              <List sx={{ p: 0 }}>
                <ListItem sx={{ px: 0 }}>
                  <ListItemIcon>
                    <AssignmentIcon color="primary" />
                  </ListItemIcon>
                  <ListItemText
                    primary="영역"
                    secondary={getAreaText(selectedAssignment.area)}
                  />
                </ListItem>

                <ListItem sx={{ px: 0 }}>
                  <ListItemIcon>
                    <TrendingUpIcon color="primary" />
                  </ListItemIcon>
                  <ListItemText
                    primary="상태"
                    secondary={getStatusText(selectedAssignment.status)}
                  />
                </ListItem>

                <ListItem sx={{ px: 0 }}>
                  <ListItemIcon>
                    <CalendarIcon color="primary" />
                  </ListItemIcon>
                  <ListItemText
                    primary="시작일"
                    secondary={selectedAssignment.startDate}
                  />
                </ListItem>

                {selectedAssignment.endDate && (
                  <ListItem sx={{ px: 0 }}>
                    <ListItemIcon>
                      <CheckCircleIcon color="primary" />
                    </ListItemIcon>
                    <ListItemText
                      primary="종료일"
                      secondary={selectedAssignment.endDate}
                    />
                  </ListItem>
                )}

                {selectedAssignment.area === 'vocabulary' && selectedAssignment.startUnit && (
                  <ListItem sx={{ px: 0 }}>
                    <ListItemIcon>
                      <InfoIcon color="primary" />
                    </ListItemIcon>
                    <ListItemText
                      primary="시작 단원"
                      secondary={selectedAssignment.startUnit}
                    />
                  </ListItem>
                )}
              </List>

              <Divider sx={{ my: 2 }} />

              <Typography variant="h6" sx={{ mb: 2, fontWeight: 600 }}>
                진행률
              </Typography>
              <Box sx={{ mb: 2 }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                  <Typography variant="body2">
                    {selectedAssignment.progress.completed} / {selectedAssignment.progress.total} 완료
                  </Typography>
                  <Typography variant="body2" color="primary" sx={{ fontWeight: 600 }}>
                    {Math.round((selectedAssignment.progress.completed / selectedAssignment.progress.total) * 100)}%
                  </Typography>
                </Box>
                <LinearProgress
                  variant="determinate"
                  value={(selectedAssignment.progress.completed / selectedAssignment.progress.total) * 100}
                  sx={{ height: 8, borderRadius: 4 }}
                />
              </Box>

              <Typography variant="h6" sx={{ mb: 2, fontWeight: 600 }}>
                주간 스케줄
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                {['월', '화', '수', '목', '금', '토', '일'].map((day) => {
                  const schedule = selectedAssignment.weeklySchedule[day];
                  return (
                    <Box
                      key={day}
                      sx={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                        p: 2,
                        borderRadius: 1,
                        backgroundColor: schedule?.isActive ? 'primary.50' : 'grey.50',
                        border: schedule?.isActive ? '1px solid' : '1px solid',
                        borderColor: schedule?.isActive ? 'primary.200' : 'grey.200',
                      }}
                    >
                      <Typography variant="body1" sx={{ fontWeight: 500 }}>
                        {day}요일
                      </Typography>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <Typography variant="body2" color="text.secondary">
                          {schedule?.isActive ? schedule.dailyAmount : '학습 없음'}
                        </Typography>
                        <Chip
                          label={schedule?.isActive ? '활성' : '비활성'}
                          color={schedule?.isActive ? 'success' : 'default'}
                          size="small"
                        />
                      </Box>
                    </Box>
                  );
                })}
              </Box>
            </Box>
          )}
        </Drawer>



        {/* 단어 학습 컴포넌트 */}
        {showVocabularyLearning && selectedAssignment && (
          <VocabularyLearning
            assignment={selectedAssignment}
            onComplete={handleCompleteVocabularyLearning}
            onClose={handleCloseVocabularyLearning}
            timerDuration={timerDuration}
          />
        )}

        {/* 단어 평가 컴포넌트 */}
        {showVocabularyEvaluation && selectedAssignment && (
          <VocabularyEvaluation
            assignment={selectedAssignment}
            learningHistory={student?.learningHistory?.filter(
              history => history.taskId === selectedAssignment.taskId
            ) || []}
            onComplete={handleCompleteVocabularyEvaluation}
            onClose={handleCloseVocabularyEvaluation}
          />
        )}
      </Container>
    </Box>
  );
};

export default StudentPage;
