import React, { useState, useEffect } from 'react';
import {
  Card,
  CardContent,
  Button,
  TextField,
  LinearProgress,
  Alert,
  Box,
  Typography,
  Chip,
  Paper,
} from '@mui/material';
import {
  CheckCircle as CheckCircleIcon,
  Cancel as CancelIcon,
  ArrowForward as ArrowForwardIcon,
} from '@mui/icons-material';

interface VocabularyItem {
  id: string;
  word: string;
  meaning: string;
  example?: string;
  translation?: string;
  round?: number;
  isKnown?: boolean;
}

interface EvaluationResult {
  wordId: string;
  isCorrect: boolean;
  userAnswer: string;
}

interface VocabularyEvaluationProps {
  vocabularyData: VocabularyItem[];
  onComplete: (results: EvaluationResult[], attemptNumber: number) => void;
  attemptNumber: number;
  studentId: string;
}

const VocabularyEvaluation: React.FC<VocabularyEvaluationProps> = ({
  vocabularyData,
  onComplete,
  attemptNumber,
  studentId
}) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [userInput, setUserInput] = useState('');
  const [results, setResults] = useState<EvaluationResult[]>([]);
  const [evaluationWords, setEvaluationWords] = useState<VocabularyItem[]>([]);
  const [showAutoComplete, setShowAutoComplete] = useState(false);

  // 1차 평가 대상 단어 필터링 (1라운드에서 "알겠어요"를 선택한 단어 제외)
  useEffect(() => {
    console.log('VocabularyEvaluation: vocabularyData received:', vocabularyData);
    
    const wordsToEvaluate = vocabularyData.filter(
      item => !(item.round === 1 && item.isKnown === true)
    );
    
    console.log('VocabularyEvaluation: wordsToEvaluate:', wordsToEvaluate);
    setEvaluationWords(wordsToEvaluate);
  }, [vocabularyData]);

  const currentWord = evaluationWords[currentIndex];
  const progress = evaluationWords.length > 0 
    ? ((currentIndex + 1) / evaluationWords.length) * 100 
    : 0;

  // 첫 글자 입력 시 자동완성 처리
  const handleInputChange = (value: string) => {
    console.log('handleInputChange called with value:', value);
    setUserInput(value);
    
    if (!currentWord?.meaning) return;
    
    // 첫 글자 입력 시 자동완성
    if (value.length === 1) {
      const firstLetter = currentWord.meaning.charAt(0);
      console.log('Checking first letter:', value, '===', firstLetter);
      
      if (value === firstLetter) {
        console.log('Match! Showing auto-complete for:', currentWord.meaning);
        setShowAutoComplete(true);
      } else {
        setShowAutoComplete(false);
      }
    } else {
      setShowAutoComplete(false);
    }
  };

  // 자동완성 클릭 처리
  const handleAutoCompleteClick = () => {
    console.log('Auto-complete clicked:', currentWord.meaning);
    setUserInput(currentWord.meaning);
    setShowAutoComplete(false);
    handleAnswer(true, currentWord.meaning);
  };

  // 답변 처리
  const handleAnswer = (correct: boolean, answer: string) => {
    console.log('handleAnswer called:', { correct, answer });
    
    const result: EvaluationResult = {
      wordId: currentWord.id,
      isCorrect: correct,
      userAnswer: answer
    };
    
    const newResults = [...results, result];
    setResults(newResults);
    
    // 다음 문제로 이동
    if (currentIndex < evaluationWords.length - 1) {
      setCurrentIndex(currentIndex + 1);
      setUserInput('');
      setShowAutoComplete(false);
    } else {
      // 평가 완료
      onComplete(newResults, attemptNumber);
    }
  };

  // 입력 버튼 처리
  const handleSubmit = () => {
    console.log('handleSubmit called with userInput:', userInput);
    
    if (!currentWord) {
      console.error('No current word');
      return;
    }
    
    // 빈칸인 경우 오답 처리
    if (userInput.trim() === '') {
      console.log('Empty input, marking as incorrect');
      handleAnswer(false, userInput);
      return;
    }
    
    // 입력값이 정답과 정확히 일치하는지 확인
    const isCorrect = userInput.trim() === currentWord.meaning;
    
    console.log('Answer check:');
    console.log('  Expected:', currentWord.meaning);
    console.log('  User input:', userInput.trim());
    console.log('  Is correct:', isCorrect);
    
    handleAnswer(isCorrect, userInput);
  };

  const handleKeyPress = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      if (showAutoComplete) {
        handleAutoCompleteClick();
      } else {
        handleSubmit();
      }
    }
  };

  if (evaluationWords.length === 0) {
    return (
      <Alert severity="info" sx={{ maxWidth: 600, mx: 'auto', mt: 3 }}>
        평가할 단어가 없습니다. 먼저 학습을 완료해주세요.
      </Alert>
    );
  }

  if (!currentWord) {
    return null;
  }

  // currentWord.meaning이 없는 경우 처리
  if (!currentWord.meaning) {
    console.error('Current word has no meaning:', currentWord);
    return (
      <Alert severity="error" sx={{ maxWidth: 600, mx: 'auto', mt: 3 }}>
        단어 데이터에 문제가 있습니다. 다시 시도해주세요.
      </Alert>
    );
  }

  return (
    <Card sx={{ maxWidth: 800, mx: 'auto', mt: 3 }}>
      <CardContent>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Typography variant="h5">{attemptNumber}차 평가</Typography>
          <Typography variant="body2" color="text.secondary">
            {currentIndex + 1} / {evaluationWords.length}
          </Typography>
        </Box>
        <LinearProgress variant="determinate" value={progress} sx={{ mb: 3 }} />

        {/* 플래시 카드 스타일 */}
        <Paper 
          elevation={3} 
          sx={{ 
            p: 4, 
            mb: 3, 
            textAlign: 'center',
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            color: 'white'
          }}
        >
          <Typography variant="h2" sx={{ fontWeight: 'bold', mb: 2 }}>
            {currentWord.word}
          </Typography>
          {currentWord.example && (
            <Typography variant="body1" sx={{ fontStyle: 'italic', opacity: 0.9, mb: 2 }}>
              "{currentWord.example}"
            </Typography>
          )}
        </Paper>

        {/* 입력 필드 */}
        <Box sx={{ mb: 3 }}>
          <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
            뜻의 첫 글자를 입력하세요
          </Typography>
          <TextField
            fullWidth
            value={userInput}
            onChange={(e) => handleInputChange(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="첫 글자 입력..."
            autoFocus
            variant="outlined"
            sx={{ mb: 1 }}
          />
          
          {/* 자동완성 표시 */}
          {showAutoComplete && (
            <Paper 
              elevation={2} 
              sx={{ 
                p: 2, 
                mt: 1, 
                cursor: 'pointer',
                '&:hover': { bgcolor: 'action.hover' }
              }}
              onClick={handleAutoCompleteClick}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                <Typography variant="body1" sx={{ fontWeight: 'bold' }}>
                  {currentWord.meaning}
                </Typography>
                <ArrowForwardIcon />
              </Box>
              <Typography variant="caption" color="text.secondary">
                클릭하거나 엔터를 눌러 정답 처리
              </Typography>
            </Paper>
          )}
        </Box>

        {/* 입력 버튼 */}
        <Button
          variant="contained"
          onClick={handleSubmit}
          sx={{ mt: 2 }}
          fullWidth
          disabled={showAutoComplete}
        >
          {showAutoComplete ? '자동완성 사용' : '입력'}
        </Button>

        {/* 진행 상태 */}
        <Box sx={{ display: 'flex', justifyContent: 'center', gap: 0.5, mt: 3 }}>
          {evaluationWords.map((_, index) => (
            <Box
              key={index}
              sx={{
                width: 8,
                height: 8,
                borderRadius: '50%',
                bgcolor: index < currentIndex
                  ? results[index]?.isCorrect
                    ? 'success.main'
                    : 'error.main'
                  : index === currentIndex
                  ? 'primary.main'
                  : 'grey.300'
              }}
            />
          ))}
        </Box>

        {/* 디버깅 정보 */}
        <Box sx={{ mt: 2, p: 2, bgcolor: 'grey.100', borderRadius: 1 }}>
          <Typography variant="caption" color="text.secondary">
            디버깅: 정답 = {currentWord.meaning} | 첫 글자 = {currentWord.meaning.charAt(0)}
          </Typography>
        </Box>
      </CardContent>
    </Card>
  );
};

export default VocabularyEvaluation;
