import React, { useState } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Button,
  TextField,
  LinearProgress,
  IconButton,
  useTheme,
  useMediaQuery,
  Container,
  Alert,
  Chip,
} from '@mui/material';
import {
  CheckCircle as CheckCircleIcon,
  Close as CloseIcon,
  PlayArrow as PlayArrowIcon,
  Send as SendIcon,
} from '@mui/icons-material';

interface EvaluationWord {
  id: string;
  word: string;
  meaning: string;
  example?: string;
}

interface EvaluationResult {
  wordId: string;
  word: string;
  userAnswer: string;
  correctAnswer: string;
  score: number;
  isCorrect: boolean;
}

interface VocabularyEvaluationProps {
  vocabularyData: EvaluationWord[];
  onComplete: (results: EvaluationResult[]) => void;
  onClose: () => void;
}

const VocabularyEvaluation: React.FC<VocabularyEvaluationProps> = ({
  vocabularyData,
  onComplete,
  onClose,
}) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [userAnswer, setUserAnswer] = useState('');
  const [isEvaluating, setIsEvaluating] = useState(false);
  const [evaluationResults, setEvaluationResults] = useState<EvaluationResult[]>([]);
  const [showResult, setShowResult] = useState(false);
  const [currentResult, setCurrentResult] = useState<EvaluationResult | null>(null);

  // 데이터 유효성 검사
  if (!vocabularyData || vocabularyData.length === 0) {
    return (
      <Container maxWidth="md" sx={{ py: 2 }}>
        <Card sx={{ borderRadius: 2 }}>
          <CardContent sx={{ p: 3, textAlign: 'center' }}>
            <Typography variant="h6" color="error">
              평가할 단어가 없습니다.
            </Typography>
            <Button onClick={onClose} sx={{ mt: 2 }}>
              닫기
            </Button>
          </CardContent>
        </Card>
      </Container>
    );
  }

  const currentWord = vocabularyData[currentWordIndex];
  const progress = ((currentWordIndex + 1) / vocabularyData.length) * 100;

  // 현재 단어가 유효한지 확인
  if (!currentWord) {
    return (
      <Container maxWidth="md" sx={{ py: 2 }}>
        <Card sx={{ borderRadius: 2 }}>
          <CardContent sx={{ p: 3, textAlign: 'center' }}>
            <Typography variant="h6" color="error">
              단어 정보를 불러올 수 없습니다.
            </Typography>
            <Button onClick={onClose} sx={{ mt: 2 }}>
              닫기
            </Button>
          </CardContent>
        </Card>
      </Container>
    );
  }

  const evaluateWithGemini = async (userAnswer: string, correctAnswer: string, word: string) => {
    try {
      const GEMINI_API_KEY = process.env.REACT_APP_GEMINI_API_KEY;
      
      if (!GEMINI_API_KEY) {
        console.log('Gemini API 키가 없어 로컬 채점을 사용합니다.');
        return evaluateLocally(userAnswer, correctAnswer);
      }

      const prompt = `다음 영어 단어의 뜻을 채점해주세요:
영어 단어: ${word}
정답: ${correctAnswer}
사용자 답변: ${userAnswer}
점수만 숫자로 답변해주세요. (100: 정답, 50-90: 부분정답, 0: 오답)`;

      const response = await fetch(`https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${GEMINI_API_KEY}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          contents: [{ parts: [{ text: prompt }] }]
        })
      });

      if (!response.ok) {
        throw new Error(`API 응답 오류: ${response.status}`);
      }

      const data = await response.json();
      
      if (!data.candidates || !data.candidates[0] || !data.candidates[0].content) {
        throw new Error('API 응답 형식 오류');
      }

      const scoreText = data.candidates[0].content.parts[0].text.trim();
      const score = parseInt(scoreText);
      
      if (isNaN(score)) {
        throw new Error('점수 파싱 오류');
      }
      
      return Math.max(0, Math.min(100, score));
    } catch (error) {
      console.error('Gemini API 호출 실패:', error);
      console.log('로컬 채점으로 대체합니다.');
      return evaluateLocally(userAnswer, correctAnswer);
    }
  };

  const evaluateLocally = (userAnswer: string, correctAnswer: string) => {
    // undefined나 null 체크
    if (!userAnswer || !correctAnswer) {
      console.error('Invalid input for evaluation:', { userAnswer, correctAnswer });
      return 0;
    }
    
    const cleanUser = userAnswer.toString().trim().toLowerCase();
    const cleanCorrect = correctAnswer.toString().trim().toLowerCase();
    
    // 정확한 매칭
    if (cleanUser === cleanCorrect) return 100;
    
    // 부분 매칭 (키워드 포함)
    const userWords = cleanUser.split(/[\s,]+/).filter(word => word.length > 0);
    const correctWords = cleanCorrect.split(/[\s,]+/).filter(word => word.length > 0);
    
    if (userWords.length === 0 || correctWords.length === 0) {
      return 0;
    }
    
    let matchCount = 0;
    userWords.forEach(userWord => {
      correctWords.forEach(correctWord => {
        // 완전 일치
        if (userWord === correctWord) {
          matchCount += 2;
        }
        // 부분 포함
        else if (userWord.includes(correctWord) || correctWord.includes(userWord)) {
          matchCount += 1;
        }
      });
    });
    
    if (matchCount > 0) {
      const maxPossibleMatches = Math.max(userWords.length, correctWords.length) * 2;
      const score = Math.round((matchCount / maxPossibleMatches) * 100);
      return Math.min(90, score); // 최대 90점으로 제한
    }
    
    return 0;
  };

  const handleSubmitAnswer = async () => {
    if (!userAnswer || !userAnswer.trim()) {
      alert('답변을 입력해주세요.');
      return;
    }

    if (!currentWord || !currentWord.meaning) {
      alert('단어 정보가 올바르지 않습니다.');
      return;
    }

    setIsEvaluating(true);

    try {
      const score = await evaluateWithGemini(userAnswer, currentWord.meaning, currentWord.word);
      const isCorrect = score >= 80;

      const result: EvaluationResult = {
        wordId: currentWord.id || 'unknown',
        word: currentWord.word || 'unknown',
        userAnswer: userAnswer.trim(),
        correctAnswer: currentWord.meaning || 'unknown',
        score: score,
        isCorrect: isCorrect,
      };

      setCurrentResult(result);
      setShowResult(true);
    } catch (error) {
      console.error('평가 중 오류 발생:', error);
      alert('평가 중 오류가 발생했습니다.');
    } finally {
      setIsEvaluating(false);
    }
  };

  const handleNextWord = () => {
    if (currentResult) {
      setEvaluationResults([...evaluationResults, currentResult]);
    }

    if (currentWordIndex + 1 < vocabularyData.length) {
      setCurrentWordIndex(currentWordIndex + 1);
      setUserAnswer('');
      setShowResult(false);
      setCurrentResult(null);
    } else {
      const finalResults = currentResult ? [...evaluationResults, currentResult] : evaluationResults;
      onComplete(finalResults);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 2 }}>
      <Card sx={{ borderRadius: isMobile ? 2 : 1 }}>
        <CardContent sx={{ p: isMobile ? 3 : 2 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
            <Typography variant="h5" sx={{ fontWeight: 600 }}>
              단어 평가
            </Typography>
            <IconButton onClick={onClose}>
              <CloseIcon />
            </IconButton>
          </Box>

          <Box sx={{ mb: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
              <Typography variant="body2">
                {currentWordIndex + 1} / {vocabularyData.length}
              </Typography>
              <Typography variant="body2" color="primary">
                {Math.round(progress)}%
              </Typography>
            </Box>
            <LinearProgress variant="determinate" value={progress} sx={{ height: 8, borderRadius: 4 }} />
          </Box>

          {!showResult ? (
            <Box>
              <Box sx={{ textAlign: 'center', mb: 4 }}>
                <Typography variant="h3" sx={{ fontWeight: 700, mb: 2 }}>
                  {currentWord.word}
                </Typography>
                {currentWord.example && (
                  <Typography variant="body1" color="text.secondary" sx={{ mb: 2 }}>
                    예문: {currentWord.example}
                  </Typography>
                )}
              </Box>

              <Box sx={{ mb: 3 }}>
                <Typography variant="h6" sx={{ mb: 2 }}>
                  이 단어의 뜻을 입력하세요:
                </Typography>
                <TextField
                  fullWidth
                  multiline
                  rows={3}
                  variant="outlined"
                  placeholder="뜻을 입력하세요..."
                  value={userAnswer}
                  onChange={(e) => setUserAnswer(e.target.value)}
                  disabled={isEvaluating}
                  sx={{ mb: 2 }}
                />
                <Button
                  fullWidth
                  variant="contained"
                  size="large"
                  onClick={handleSubmitAnswer}
                  disabled={!userAnswer.trim() || isEvaluating}
                  startIcon={isEvaluating ? <CheckCircleIcon /> : <SendIcon />}
                >
                  {isEvaluating ? '평가 중...' : '평가하기'}
                </Button>
              </Box>
            </Box>
          ) : (
            <Box>
              <Box sx={{ textAlign: 'center', mb: 3 }}>
                <Typography variant="h4" sx={{ mb: 2 }}>
                  {currentWord.word}
                </Typography>
                
                <Alert severity={currentResult?.isCorrect ? 'success' : 'error'} sx={{ mb: 2 }}>
                  <Typography variant="h6">
                    {currentResult?.isCorrect ? '정답입니다!' : '틀렸습니다.'}
                  </Typography>
                </Alert>

                <Box sx={{ mb: 2 }}>
                  <Typography variant="body1" color="text.secondary">
                    당신의 답변: {currentResult?.userAnswer}
                  </Typography>
                  <Typography variant="body1" color="text.secondary">
                    정답: {currentResult?.correctAnswer}
                  </Typography>
                </Box>

                <Chip
                  label={`점수: ${currentResult?.score || 0}점`}
                  color={(currentResult?.score || 0) >= 80 ? 'success' : 'error'}
                  sx={{ fontSize: '1.1rem', fontWeight: 600 }}
                />
              </Box>

              <Button
                fullWidth
                variant="contained"
                size="large"
                onClick={handleNextWord}
                startIcon={<PlayArrowIcon />}
              >
                {currentWordIndex + 1 < vocabularyData.length ? '다음 단어' : '평가 완료'}
              </Button>
            </Box>
          )}
        </CardContent>
      </Card>
    </Container>
  );
};

export default VocabularyEvaluation;
