import React, { useState } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Button,
  TextField,
  LinearProgress,
  IconButton,
  useTheme,
  useMediaQuery,
  Container,
  Alert,
  Chip,
} from '@mui/material';
import {
  CheckCircle as CheckCircleIcon,
  Close as CloseIcon,
  PlayArrow as PlayArrowIcon,
  Send as SendIcon,
} from '@mui/icons-material';

interface EvaluationWord {
  id: string;
  word: string;
  meaning: string;
  example?: string;
}

interface EvaluationResult {
  wordId: string;
  word: string;
  userAnswer: string;
  correctAnswer: string;
  score: number;
  isCorrect: boolean;
}

interface VocabularyEvaluationProps {
  vocabularyData: EvaluationWord[];
  onComplete: (results: EvaluationResult[]) => void;
  onClose: () => void;
}

const VocabularyEvaluation: React.FC<VocabularyEvaluationProps> = ({
  vocabularyData,
  onComplete,
  onClose,
}) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [userAnswer, setUserAnswer] = useState('');
  const [isEvaluating, setIsEvaluating] = useState(false);
  const [evaluationResults, setEvaluationResults] = useState<EvaluationResult[]>([]);
  const [showResult, setShowResult] = useState(false);
  const [currentResult, setCurrentResult] = useState<EvaluationResult | null>(null);

  // 데이터 유효성 검사
    if (!vocabularyData || vocabularyData.length === 0) {
    return (
      <Container maxWidth="md" sx={{ py: 2 }}>
        <Card sx={{ borderRadius: 2 }}>
          <CardContent sx={{ p: 3, textAlign: 'center' }}>
            <Typography variant="h6" color="error">
              평가할 단어가 없습니다.
            </Typography>
            <Button onClick={onClose} sx={{ mt: 2 }}>
              닫기
            </Button>
          </CardContent>
        </Card>
      </Container>
    );
  }

  const currentWord = vocabularyData[currentWordIndex];
  const progress = ((currentWordIndex + 1) / vocabularyData.length) * 100;

  // 디버깅 로그 추가
  console.log('=== VocabularyEvaluation 디버깅 ===');
  console.log('vocabularyData:', vocabularyData);
  console.log('currentWordIndex:', currentWordIndex);
  console.log('currentWord:', currentWord);
  console.log('currentWord.word:', currentWord?.word);
  console.log('currentWord.meaning:', currentWord?.meaning);

  // 현재 단어가 유효한지 확인
  if (!currentWord) {
    console.error('currentWord가 undefined입니다');
    return (
      <Container maxWidth="md" sx={{ py: 2 }}>
        <Card sx={{ borderRadius: 2 }}>
          <CardContent sx={{ p: 3, textAlign: 'center' }}>
            <Typography variant="h6" color="error">
              단어 정보를 불러올 수 없습니다.
            </Typography>
            <Button onClick={onClose} sx={{ mt: 2 }}>
              닫기
            </Button>
          </CardContent>
        </Card>
      </Container>
    );
  }

  const evaluateWithGemini = async (userAnswer: string, correctAnswer: string, word: string) => {
    try {
      // API 키를 직접 설정 (환경변수 문제 해결)
      const GEMINI_API_KEY = 'AIzaSyDQJ6SXBexKbCGeU-DXWCVmtfCJ56gAATs';
      
      console.log('=== Gemini API 디버깅 ===');
      console.log('Gemini API 키 설정됨:', !!GEMINI_API_KEY);
      console.log('API 키 앞 10자리:', GEMINI_API_KEY.substring(0, 10) + '...');

      const prompt = `다음 영어 단어의 뜻을 채점해주세요:
영어 단어: ${word}
정답: ${correctAnswer}
사용자 답변: ${userAnswer}
점수만 숫자로 답변해주세요. (100: 정답, 50-90: 부분정답, 0: 오답)`;

      console.log('Gemini API 호출 시작...');
      console.log('프롬프트:', prompt);

      const response = await fetch(`https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash:generateContent?key=${GEMINI_API_KEY}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          contents: [{ parts: [{ text: prompt }] }]
        })
      });

      console.log('API 응답 상태:', response.status);
      console.log('API 응답 OK:', response.ok);

      if (!response.ok) {
        const errorText = await response.text();
        console.error('API 응답 오류 내용:', errorText);
        throw new Error(`API 응답 오류: ${response.status} - ${errorText}`);
      }

      const data = await response.json();
      console.log('API 응답 데이터:', data);
      
      if (!data.candidates || !data.candidates[0] || !data.candidates[0].content) {
        console.error('API 응답 형식 오류:', data);
        throw new Error('API 응답 형식 오류');
      }

      const scoreText = data.candidates[0].content.parts[0].text.trim();
      console.log('추출된 점수 텍스트:', scoreText);
      
      const score = parseInt(scoreText);
      console.log('파싱된 점수:', score);
      
      if (isNaN(score)) {
        throw new Error(`점수 파싱 오류: "${scoreText}"`);
      }
      
      const finalScore = Math.max(0, Math.min(100, score));
      console.log('최종 점수:', finalScore);
      
      return finalScore;
    } catch (error) {
      console.error('Gemini API 호출 실패:', error);
      
      // API 실패 시 기본 채점 로직 (간단한 문자열 비교)
      const cleanUser = userAnswer.toLowerCase().trim();
      const cleanCorrect = correctAnswer.toLowerCase().trim();
      
      if (cleanUser === cleanCorrect) {
        console.log('API 실패 시 기본 채점: 완전 일치 - 100점');
        return 100;
      } else if (cleanUser.includes(cleanCorrect) || cleanCorrect.includes(cleanUser)) {
        console.log('API 실패 시 기본 채점: 부분 일치 - 70점');
        return 70;
      } else {
        console.log('API 실패 시 기본 채점: 불일치 - 30점 (API 실패로 인한 기본 점수)');
        return 30; // API 실패 시 최소 점수 보장
      }
    }
  };

  const handleSubmitAnswer = async () => {
    console.log('=== handleSubmitAnswer 디버깅 ===');
    console.log('userAnswer:', userAnswer);
    console.log('userAnswer.trim():', userAnswer?.trim());
    console.log('currentWord:', currentWord);
    console.log('currentWord.meaning:', currentWord?.meaning);
    console.log('currentWord.word:', currentWord?.word);
    console.log('currentWord.id:', currentWord?.id);

    if (!userAnswer || !userAnswer.trim()) {
      console.error('사용자 답변이 비어있음');
      alert('답변을 입력해주세요.');
      return;
    }
    
    if (!currentWord) {
      console.error('currentWord가 undefined');
      alert('단어 정보가 올바르지 않습니다. (currentWord 없음)');
      return;
    }
    
    if (!currentWord.meaning) {
      console.error('currentWord.meaning이 undefined:', currentWord);
      alert('단어 정보가 올바르지 않습니다. (meaning 없음)');
      return;
    }
    
    setIsEvaluating(true);

    try {
      const score = await evaluateWithGemini(userAnswer, currentWord.meaning, currentWord.word);
      const isCorrect = score >= 80;

      const result: EvaluationResult = {
        wordId: currentWord.id || 'unknown',
        word: currentWord.word || 'unknown',
        userAnswer: userAnswer.trim(),
        correctAnswer: currentWord.meaning || 'unknown',
        score: score,
        isCorrect: isCorrect,
      };

      setCurrentResult(result);
      setShowResult(true);
    } catch (error) {
      console.error('평가 중 오류 발생:', error);
      alert('평가 중 오류가 발생했습니다.');
    } finally {
      setIsEvaluating(false);
    }
  };

  const handleNextWord = () => {
    if (currentResult) {
      setEvaluationResults([...evaluationResults, currentResult]);
    }

    if (currentWordIndex + 1 < vocabularyData.length) {
      setCurrentWordIndex(currentWordIndex + 1);
      setUserAnswer('');
      setShowResult(false);
      setCurrentResult(null);
    } else {
      const finalResults = currentResult ? [...evaluationResults, currentResult] : evaluationResults;
      onComplete(finalResults);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 2 }}>
      <Card sx={{ borderRadius: isMobile ? 2 : 1 }}>
        <CardContent sx={{ p: isMobile ? 3 : 2 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
            <Typography variant="h5" sx={{ fontWeight: 600 }}>
              단어 평가
            </Typography>
            <IconButton onClick={onClose}>
              <CloseIcon />
            </IconButton>
          </Box>

          <Box sx={{ mb: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
              <Typography variant="body2">
                {currentWordIndex + 1} / {vocabularyData.length}
              </Typography>
              <Typography variant="body2" color="primary">
                {Math.round(progress)}%
          </Typography>
        </Box>
            <LinearProgress variant="determinate" value={progress} sx={{ height: 8, borderRadius: 4 }} />
          </Box>

          {!showResult ? (
            <Box>
              <Box sx={{ textAlign: 'center', mb: 4 }}>
                <Typography variant="h3" sx={{ fontWeight: 700, mb: 2 }}>
            {currentWord.word}
          </Typography>
          {currentWord.example && (
                  <Typography variant="body1" color="text.secondary" sx={{ mb: 2 }}>
                    예문: {currentWord.example}
            </Typography>
          )}
              </Box>

        <Box sx={{ mb: 3 }}>
                <Typography variant="h6" sx={{ mb: 2 }}>
                  이 단어의 뜻을 입력하세요:
          </Typography>
          <TextField
            fullWidth
                  multiline
                  rows={3}
            variant="outlined"
                  placeholder="뜻을 입력하세요..."
                  value={userAnswer}
                  onChange={(e) => setUserAnswer(e.target.value)}
                  onKeyPress={(e) => {
                    if (e.key === 'Enter' && !e.shiftKey && !isEvaluating && userAnswer.trim()) {
                      e.preventDefault();
                      handleSubmitAnswer();
                    }
                  }}
                  disabled={isEvaluating}
                  sx={{ mb: 2 }}
                />
                <Button
                  fullWidth
                  variant="contained"
                  size="large"
                  onClick={handleSubmitAnswer}
                  disabled={!userAnswer.trim() || isEvaluating}
                  startIcon={isEvaluating ? <CheckCircleIcon /> : <SendIcon />}
                >
                  {isEvaluating ? '평가 중...' : '평가하기'}
                </Button>
              </Box>
            </Box>
          ) : (
            <Box>
              <Box sx={{ textAlign: 'center', mb: 3 }}>
                <Typography variant="h4" sx={{ mb: 2 }}>
                  {currentWord.word}
                </Typography>
                
                <Alert severity={currentResult?.isCorrect ? 'success' : 'error'} sx={{ mb: 2 }}>
                  <Typography variant="h6">
                    {currentResult?.isCorrect ? '정답입니다!' : '틀렸습니다.'}
                  </Typography>
                </Alert>

                <Box sx={{ mb: 2 }}>
                  <Typography variant="body1" color="text.secondary">
                    당신의 답변: {currentResult?.userAnswer}
                  </Typography>
                  <Typography variant="body1" color="text.secondary">
                    정답: {currentResult?.correctAnswer}
                  </Typography>
                </Box>

                <Chip
                  label={`점수: ${currentResult?.score || 0}점`}
                  color={(currentResult?.score || 0) >= 80 ? 'success' : 'error'}
                  sx={{ fontSize: '1.1rem', fontWeight: 600 }}
                />
        </Box>

        <Button
                fullWidth
          variant="contained"
                size="large"
                onClick={handleNextWord}
                startIcon={<PlayArrowIcon />}
              >
                {currentWordIndex + 1 < vocabularyData.length ? '다음 단어' : '평가 완료'}
        </Button>
        </Box>
          )}
      </CardContent>
    </Card>
    </Container>
  );
};

export default VocabularyEvaluation;
