export interface VocabularyWord {
  id: string;
  english: string;
  korean: string;
  unit: string;
  difficulty?: 'easy' | 'medium' | 'hard';
  category?: string;
}

export interface VocabularyUnit {
  id: string;
  name: string;
  words: VocabularyWord[];
  totalWords: number;
}

export interface VocabularyBook {
  id: string;
  name: string;
  units: VocabularyUnit[];
  totalUnits: number;
}

// 더미 단어장 데이터 (실제로는 API에서 가져올 예정)
