];

// 단어장 ID로 단어장 가져오기
export const getVocabularyBook = (bookId: string): VocabularyBook | null => {
  return vocabularyBooks.find(book => book.id === bookId) || null;
};

// 단원 ID로 단원 가져오기
export const getVocabularyUnit = (unitId: string): VocabularyUnit | null => {
  for (const book of vocabularyBooks) {
    const unit = book.units.find(u => u.id === unitId);
    if (unit) return unit;
  }
  return null;
};

// 단어 ID로 단어 가져오기
export const getVocabularyWord = (wordId: string): VocabularyWord | null => {
  for (const book of vocabularyBooks) {
    for (const unit of book.units) {
      const word = unit.words.find(w => w.id === wordId);
      if (word) return word;
    }
  }
  return null;
};

// 배정된 과제의 단어 목록 가져오기
export const getAssignedWords = (assignment: any): VocabularyWord[] => {
  console.log('getAssignedWords called with assignment:', assignment);
  console.log('assignment type:', typeof assignment);
  console.log('assignment keys:', Object.keys(assignment || {}));
  
  // 오늘이 몇 번째 학습일인지 계산
  const calculateCurrentUnit = (assignment: any): string | null => {
    if (!assignment.weeklySchedule || !assignment.startDate || !assignment.startUnit) {
      return null;
    }
    
    const startDate = new Date(assignment.startDate);
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    startDate.setHours(0, 0, 0, 0);
    
    const dayNames = ['일', '월', '화', '수', '목', '금', '토'];
    let studyDayCount = 0;
    
    // startDate부터 오늘까지 학습일 수 계산
    const currentDate = new Date(startDate);
    while (currentDate <= today) {
      const dayName = dayNames[currentDate.getDay()];
      if (assignment.weeklySchedule[dayName]?.isActive) {
        studyDayCount++;
      }
      currentDate.setDate(currentDate.getDate() + 1);
    }
    
    console.log('Study day count from start:', studyDayCount);
    
    // 오늘이 학습일이 아니면 null 반환
    const todayDayName = dayNames[today.getDay()];
    if (!assignment.weeklySchedule[todayDayName]?.isActive) {
      console.log('Today is not a study day');
      return null;
    }
    
    return studyDayCount > 0 ? studyDayCount.toString() : null;
  };
  
  const currentStudyDay = calculateCurrentUnit(assignment);
  console.log('Current study day:', currentStudyDay);
  
  // 과제에 vocabularyData가 있는지 확인
  if (assignment && assignment.vocabularyData && Array.isArray(assignment.vocabularyData)) {
    console.log('Using assignment vocabularyData:', assignment.vocabularyData);
    console.log('vocabularyData length:', assignment.vocabularyData.length);
    // 과제의 vocabularyData를 VocabularyWord 형식으로 변환
    return assignment.vocabularyData.map((word: any, index: number) => ({
      id: `word_${index}`,
      english: word.english,
      korean: word.meaning,
      unit: word.unit,
    }));
  }

  // TaskManagement에서 저장된 과제 데이터를 가져와서 매칭
  try {
    const { loadTasks } = require('./storage');
    const tasks = loadTasks();
    console.log('Looking for task with ID:', assignment.taskId, 'or title:', assignment.taskTitle);
    console.log('Available tasks:', tasks.map((t: any) => ({ id: t.id, title: t.title })));
    
    const matchingTask = tasks.find((task: any) => 
      task.id === assignment.taskId || task.title === assignment.taskTitle
    );
    
    if (matchingTask && matchingTask.vocabularyData) {
      console.log('Found matching task:', matchingTask.title);
      console.log('Task vocabularyData:', matchingTask.vocabularyData);
      console.log('Assignment startUnit:', assignment.startUnit);
      
      let wordsToReturn = matchingTask.vocabularyData;
      
      // startUnit이 있으면 해당 단원부터 시작
      if (assignment.startUnit) {
        // 모든 단원을 순서대로 정렬
        const unitSet = new Set<string>();
        matchingTask.vocabularyData.forEach((w: any) => unitSet.add(w.unit));
        const allUnits = Array.from(unitSet);
        const startUnitIndex = allUnits.indexOf(assignment.startUnit);
        
        if (startUnitIndex !== -1) {
          // startUnit부터의 단원들만 포함
          const unitsToInclude = allUnits.slice(startUnitIndex);
          wordsToReturn = matchingTask.vocabularyData.filter((word: any) => 
            unitsToInclude.includes(word.unit)
          );
          console.log(`Filtering from unit ${assignment.startUnit}, including units:`, unitsToInclude);
          console.log(`Filtered ${wordsToReturn.length} words from total ${matchingTask.vocabularyData.length} words`);
        }
      }
      
      return wordsToReturn.map((word: any, index: number) => ({
        id: `word_${index}`,
        english: word.english,
        korean: word.meaning,
        unit: word.unit,
      }));
    }
  } catch (error) {
    console.log('Error loading tasks:', error);
  }

  // assignment에서 startUnit 정보를 가져와서 해당 단원의 단어들을 반환
  if (assignment && assignment.startUnit) {
    const unit = getVocabularyUnit(assignment.startUnit);
    if (unit) {
      return unit.words;
    }
  }

  console.log('Falling back to default vocabulary');
  // 기본 단어장의 첫 번째 단원 사용
  return vocabularyBooks[0].units[0].words;
};

// 단어장 목록 가져오기
export const getAllVocabularyBooks = (): VocabularyBook[] => {
  return vocabularyBooks;
};

// 단어장의 모든 단어 가져오기
export const getAllWordsFromBook = (bookId: string): VocabularyWord[] => {
  const book = getVocabularyBook(bookId);
  if (!book) return [];

  const allWords: VocabularyWord[] = [];
  book.units.forEach(unit => {
    allWords.push(...unit.words);
  });
  
  return allWords;
};
